import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { Users, Mail, Shield, MoreVertical, RefreshCw } from "lucide-react";
import { userService, User } from "../../services/userService";

const TeamPage: React.FC = () => {
    const [members, setMembers] = useState<User[]>([]);
    const [loading, setLoading] = useState(true);

    const fetchTeam = async () => {
        try {
            setLoading(true);
            const data = await userService.getAllUsers();
            setMembers(data);
        } catch (error) {
            console.error("Failed to fetch team", error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchTeam();
        // Refresh every minute to update statuses
        const interval = setInterval(fetchTeam, 60000);
        return () => clearInterval(interval);
    }, []);

    const onlineCount = members.filter(m => m.status === 'Online').length;
    const offlineCount = members.filter(m => m.status === 'Offline').length;

    const getInitials = (name: string) => {
        return name?.split(" ").map(n => n[0]).join("").toUpperCase().substring(0, 2) || "U";
    };

    return (
        <div className="p-6 max-w-7xl mx-auto space-y-8">
            <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="flex flex-col md:flex-row md:items-center justify-between gap-4"
            >
                <div>
                    <h1 className="text-3xl font-bold text-gray-900 tracking-tight">Team Members</h1>
                    <p className="text-gray-500 mt-1">Manage your team and their access levels.</p>
                </div>
                <div className="flex items-center gap-3">
                    <button
                        onClick={fetchTeam}
                        className="p-2 text-gray-500 hover:bg-gray-100 rounded-lg transition-colors"
                        title="Refresh"
                    >
                        <RefreshCw size={20} className={loading ? "animate-spin" : ""} />
                    </button>
                    <button className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg font-medium transition-colors flex items-center gap-2 w-fit shadow-sm">
                        <Users size={18} />
                        Invite Member
                    </button>
                </div>
            </motion.div>

            {/* Status Summary */}
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-6">
                <div className="bg-white p-6 rounded-xl border border-gray-100 shadow-sm">
                    <p className="text-sm font-medium text-gray-500 mb-1">Total Members</p>
                    <p className="text-3xl font-bold text-gray-900">{members.length}</p>
                </div>
                <div className="bg-white p-6 rounded-xl border border-gray-100 shadow-sm">
                    <p className="text-sm font-medium text-gray-500 mb-1">Online Now</p>
                    <div className="flex items-center gap-2">
                        <span className="w-2.5 h-2.5 rounded-full bg-green-500 animate-pulse" />
                        <p className="text-3xl font-bold text-gray-900">{onlineCount}</p>
                    </div>
                </div>
                <div className="bg-white p-6 rounded-xl border border-gray-100 shadow-sm">
                    <p className="text-sm font-medium text-gray-500 mb-1">Currently Offline</p>
                    <p className="text-3xl font-bold text-gray-900">{offlineCount}</p>
                </div>
            </div>

            <div className="bg-white rounded-xl shadow-sm border border-gray-100 overflow-hidden">
                <div className="overflow-x-auto">
                    <table className="w-full text-left">
                        <thead className="bg-gray-50 border-b border-gray-100 text-xs font-semibold text-gray-500 uppercase tracking-wider">
                            <tr>
                                <th className="px-6 py-4">Member</th>
                                <th className="px-6 py-4">Role</th>
                                <th className="px-6 py-4">Status</th>
                                <th className="px-6 py-4 text-right">Actions</th>
                            </tr>
                        </thead>
                        <tbody className="divide-y divide-gray-50">
                            {loading && members.length === 0 ? (
                                <tr>
                                    <td colSpan={4} className="px-6 py-12 text-center text-gray-500">
                                        Loading team members...
                                    </td>
                                </tr>
                            ) : members.map((member) => (
                                <tr key={member.id} className="hover:bg-gray-50/50 transition-colors">
                                    <td className="px-6 py-4">
                                        <div className="flex items-center gap-3">
                                            <div className="w-10 h-10 rounded-full bg-blue-100 flex items-center justify-center text-blue-700 font-bold overflow-hidden">
                                                {member.avatar ? (
                                                    <img src={member.avatar} alt={member.name} className="w-full h-full object-cover" />
                                                ) : getInitials(member.name)}
                                            </div>
                                            <div>
                                                <div className="text-sm font-semibold text-gray-900">{member.name || 'Anonymous User'}</div>
                                                <div className="text-xs text-gray-500 flex items-center gap-1">
                                                    <Mail size={12} />
                                                    {member.email}
                                                </div>
                                            </div>
                                        </div>
                                    </td>
                                    <td className="px-6 py-4">
                                        <div className="flex items-center gap-1.5 text-sm text-gray-600 font-medium">
                                            <Shield size={14} className="text-blue-500" />
                                            {member.role || 'Member'}
                                        </div>
                                    </td>
                                    <td className="px-6 py-4">
                                        <span className={`inline-flex items-center gap-1.5 px-2.5 py-1 rounded-full text-xs font-semibold ${member.status === 'Online' ? 'bg-green-100 text-green-700' :
                                                member.status === 'Away' ? 'bg-yellow-100 text-yellow-700' :
                                                    'bg-gray-100 text-gray-700'
                                            }`}>
                                            <span className={`w-1.5 h-1.5 rounded-full ${member.status === 'Online' ? 'bg-green-500 pulse-green' :
                                                    member.status === 'Away' ? 'bg-yellow-500' :
                                                        'bg-gray-400'
                                                }`} />
                                            {member.status || 'Offline'}
                                        </span>
                                        {member.last_seen && member.status !== 'Online' && (
                                            <p className="text-[10px] text-gray-400 mt-1">
                                                Seen {new Date(member.last_seen).toLocaleDateString()}
                                            </p>
                                        )}
                                    </td>
                                    <td className="px-6 py-4 text-right">
                                        <button className="text-gray-400 hover:text-gray-600 p-1 hover:bg-gray-100 rounded">
                                            <MoreVertical size={18} />
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default TeamPage;
