import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
    ChevronLeft,
    Save,
    Loader2,
    Calendar,
    User,
    AlignLeft,
    Type,
    Sparkles,
    ShieldCheck,
    Clock,
    Target
} from "lucide-react";
import { taskService } from "../../services/taskService";
import { userService } from "../../services/userService";
import { motion } from "framer-motion";

const AddTaskPage: React.FC = () => {
    const navigate = useNavigate();
    const { id } = useParams<{ id: string }>();
    const isEditing = Boolean(id);

    const [users, setUsers] = useState<any[]>([]);
    const [submitting, setSubmitting] = useState(false);
    const [loading, setLoading] = useState(isEditing);

    const [formData, setFormData] = useState({
        title: "",
        description: "",
        status: "Upcoming",
        priority: "Normal",
        assigned_to: "",
        due_date: new Date().toISOString().split('T')[0]
    });

    useEffect(() => {
        const loadData = async () => {
            try {
                const [usersData, taskData] = await Promise.all([
                    userService.getAllUsers(),
                    isEditing && id ? taskService.getTaskById(Number(id)) : Promise.resolve(null)
                ]);
                setUsers(usersData);
                if (taskData) {
                    setFormData({
                        title: taskData.title,
                        description: taskData.description || "",
                        status: taskData.status === "todo" ? "Upcoming" : taskData.status === "in_progress" ? "In Progress" : taskData.status === "done" ? "Completed" : taskData.status,
                        priority: taskData.priority,
                        assigned_to: taskData.assignee_id ? String(taskData.assignee_id) : "",
                        due_date: taskData.due_date ? taskData.due_date.split("T")[0] : new Date().toISOString().split('T')[0]
                    });
                }
            } catch (e) {
                console.error("Failed to load data", e);
            } finally {
                setLoading(false);
            }
        };
        loadData();
    }, [id, isEditing]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setSubmitting(true);
        try {
            const payload = {
                ...formData,
                status: (formData.status === "Upcoming" ? "todo" : formData.status === "In Progress" ? "in_progress" : "done") as any,
                priority: formData.priority as any,
                assigned_to: formData.assigned_to ? Number(formData.assigned_to) : undefined
            };

            if (isEditing && id) {
                await taskService.updateTask(Number(id), payload);
            } else {
                await taskService.createTask(payload);
            }
            navigate("/tasks");
        } catch (error) {
            console.error(error);
            setSubmitting(false);
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-[#F8FAFC] flex items-center justify-center">
                <Loader2 className="w-10 h-10 text-indigo-600 animate-spin" />
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-[#F8FAFC] py-12 px-4 sm:px-6 lg:px-8 font-sans transition-all duration-500">
            <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="max-w-5xl mx-auto"
            >
                {/* ðŸš€ Premium Header */}
                <div className="flex flex-col md:flex-row md:items-center justify-between gap-6 mb-12">
                    <div className="flex items-center gap-6">
                        <button
                            onClick={() => navigate(-1)}
                            className="w-12 h-12 bg-white border border-slate-200 rounded-2xl flex items-center justify-center text-slate-400 hover:text-indigo-600 hover:border-indigo-100 hover:shadow-xl hover:shadow-indigo-50 transition-all"
                        >
                            <ChevronLeft size={24} />
                        </button>
                        <div>
                            <h1 className="text-4xl font-black text-slate-900 tracking-tighter">
                                {isEditing ? 'UPDATE NODE' : 'DEPLOY TASK'}
                            </h1>
                            <p className="text-slate-500 font-medium">
                                {isEditing ? 'Modify the technical specifications of this project node.' : 'Initialize a new project milestone in the system.'}
                            </p>
                        </div>
                    </div>
                </div>

                <form onSubmit={handleSubmit} className="grid grid-cols-1 lg:grid-cols-12 gap-8">

                    {/* Left Column: Main Editor */}
                    <div className="lg:col-span-8 space-y-6">
                        <div className="bg-white rounded-[32px] p-8 border border-slate-100 shadow-sm space-y-8">

                            <div className="space-y-2">
                                <label className="flex items-center gap-2 text-[10px] font-black text-slate-400 uppercase tracking-widest pl-1">
                                    <Type size={12} /> Task Identification
                                </label>
                                <input
                                    type="text"
                                    value={formData.title}
                                    onChange={e => setFormData({ ...formData, title: e.target.value })}
                                    placeholder="Enter task title..."
                                    required
                                    className="w-full px-6 py-5 bg-slate-50 border-none rounded-2xl text-xl font-black text-slate-900 placeholder:text-slate-300 focus:ring-2 focus:ring-indigo-100 transition-all outline-none"
                                />
                            </div>

                            <div className="space-y-2">
                                <label className="flex items-center gap-2 text-[10px] font-black text-slate-400 uppercase tracking-widest pl-1">
                                    <AlignLeft size={12} /> Documentation
                                </label>
                                <textarea
                                    value={formData.description}
                                    onChange={e => setFormData({ ...formData, description: e.target.value })}
                                    placeholder="Describe the task requirements & technical details..."
                                    rows={10}
                                    className="w-full px-6 py-5 bg-slate-50 border-none rounded-2xl text-slate-700 font-medium placeholder:text-slate-300 focus:ring-2 focus:ring-indigo-100 transition-all outline-none resize-none leading-relaxed"
                                />
                            </div>
                        </div>

                        {/* Integration Panel */}
                        <div className="bg-indigo-600 rounded-[32px] p-8 text-white relative overflow-hidden shadow-2xl shadow-indigo-200">
                            <div className="relative z-10 flex items-center justify-between">
                                <div className="space-y-1">
                                    <h3 className="text-xl font-black flex items-center gap-2">
                                        <Sparkles size={20} /> AI Status Monitoring
                                    </h3>
                                    <p className="text-indigo-100/80 text-sm font-medium">Automatic time tracking and status updates enabled.</p>
                                </div>
                                <div className="bg-white/10 p-3 rounded-2xl backdrop-blur-md">
                                    <ShieldCheck size={32} />
                                </div>
                            </div>
                            <div className="absolute -right-10 -bottom-10 w-40 h-40 bg-white/5 rounded-full blur-3xl"></div>
                        </div>
                    </div>

                    {/* Right Column: Logistics */}
                    <div className="lg:col-span-4 space-y-6">

                        <div className="bg-white rounded-[32px] p-8 border border-slate-100 shadow-sm space-y-8">
                            <h2 className="text-[10px] font-black text-slate-400 uppercase tracking-widest border-b border-slate-50 pb-4">
                                Resource Allocation
                            </h2>

                            <div className="space-y-6">
                                {/* Assignee */}
                                <div className="space-y-2">
                                    <label className="text-[10px] font-black text-slate-500 uppercase tracking-tighter flex items-center gap-2">
                                        <User size={12} /> Assignee
                                    </label>
                                    <select
                                        value={formData.assigned_to}
                                        onChange={e => setFormData({ ...formData, assigned_to: e.target.value })}
                                        className="w-full px-5 py-4 bg-slate-50 border-none rounded-2xl text-slate-800 font-bold focus:ring-2 focus:ring-indigo-100 outline-none appearance-none cursor-pointer"
                                    >
                                        <option value="">Select Member</option>
                                        {users.map(u => (
                                            <option key={u.id} value={u.id}>{u.name || u.email}</option>
                                        ))}
                                    </select>
                                </div>

                                {/* Timeline */}
                                <div className="space-y-2">
                                    <label className="text-[10px] font-black text-slate-500 uppercase tracking-tighter flex items-center gap-2">
                                        <Clock size={12} /> DeadLine
                                    </label>
                                    <input
                                        type="date"
                                        value={formData.due_date}
                                        onChange={e => setFormData({ ...formData, due_date: e.target.value })}
                                        className="w-full px-5 py-4 bg-slate-50 border-none rounded-2xl text-slate-800 font-bold focus:ring-2 focus:ring-indigo-100 outline-none cursor-pointer"
                                    />
                                </div>

                                {/* Goal/Status */}
                                <div className="space-y-2">
                                    <label className="text-[10px] font-black text-slate-500 uppercase tracking-tighter flex items-center gap-2">
                                        <Target size={12} /> Status Mapping
                                    </label>
                                    <div className="grid grid-cols-1 gap-2">
                                        {["Upcoming", "In Progress", "Completed"].map(s => (
                                            <button
                                                key={s}
                                                type="button"
                                                onClick={() => setFormData({ ...formData, status: s })}
                                                className={`px-4 py-3 rounded-2xl text-[10px] font-black uppercase tracking-widest transition-all border-2 ${formData.status === s ? 'bg-indigo-600 border-indigo-600 text-white shadow-lg shadow-indigo-100' : 'bg-white border-slate-100 text-slate-400 hover:border-indigo-100'}`}
                                            >
                                                {s}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                            </div>
                        </div>

                        {/* Submit Actions */}
                        <div className="space-y-4">
                            <button
                                type="submit"
                                disabled={submitting}
                                className="w-full bg-slate-900 hover:bg-black text-white py-6 rounded-[32px] font-black uppercase tracking-widest text-sm flex items-center justify-center gap-3 shadow-2xl shadow-slate-200 transition-all active:scale-95 disabled:opacity-50 group px-2"
                            >
                                {submitting ? <Loader2 size={24} className="animate-spin" /> : <Save size={24} />}
                                {isEditing ? 'COMMIT CHANGES' : 'DEPLOY NODE'}
                            </button>
                            <button
                                type="button"
                                onClick={() => navigate(-1)}
                                className="w-full bg-white border border-slate-100 text-slate-400 py-4 rounded-[32px] font-bold text-xs hover:text-rose-500 hover:border-rose-100 transition-all"
                            >
                                ABORT MISSION
                            </button>
                        </div>
                    </div>
                </form>
            </motion.div>
        </div>
    );
};

export default AddTaskPage;
