import React, { useState } from "react";
import { Eye, EyeOff, Shield, Zap, Activity, ChevronRight, Mail, Lock, User, Sparkles, Loader2 } from "lucide-react";
import { Link, useNavigate } from "react-router-dom";
import { authService } from "../../services/authService";
import { motion, AnimatePresence } from "framer-motion";

const RegisterPage: React.FC = () => {
    const [showPassword, setShowPassword] = useState(false);
    const [showConfirmPassword, setShowConfirmPassword] = useState(false);
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        password: "",
        confirmPassword: "",
    });
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (formData.password !== formData.confirmPassword) {
            alert("Passwords do not match!");
            return;
        }
        setIsLoading(true);
        try {
            await authService.register({
                email: formData.email,
                name: formData.name,
                password: formData.password
            });
            navigate("/login");
        } catch (error: any) {
            console.error("Registration failed:", error);
            alert(error.response?.data?.detail || "Registration failed. Please try again.");
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-[#fcfdfe] flex items-center justify-center p-6 font-sans overflow-hidden relative">
            <div className="w-full max-w-[1100px] h-[750px] bg-white rounded-[40px] shadow-2xl flex relative overflow-hidden border border-slate-100/50">

                {/* Left: Branding & Visuals */}
                <div className="hidden lg:flex lg:w-1/2 bg-slate-900 p-16 flex-col justify-between relative overflow-hidden">
                    {/* Simple accent patterns */}
                    <div className="absolute top-0 left-0 w-full h-full opacity-5 pointer-events-none">
                        <div className="absolute -top-20 -left-20 w-80 h-80 border border-white rounded-full" />
                        <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-[500px] h-[500px] border border-white rounded-[100px] rotate-45" />
                    </div>

                    <div className="relative z-10">
                        <div className="w-12 h-12 bg-indigo-600 rounded-2xl flex items-center justify-center shadow-lg shadow-indigo-500/20 mb-10">
                            <Sparkles size={24} className="text-white fill-white" />
                        </div>
                        <h1 className="text-5xl font-bold text-white tracking-tight leading-tight mb-6">
                            Join the Elite <br />
                            <span className="text-indigo-400">Team Matrix.</span>
                        </h1>
                        <p className="text-slate-400 text-lg font-medium leading-relaxed max-w-[340px]">
                            Create your workspace identity and start orchestrating mission-critical operations today.
                        </p>
                    </div>

                    <div className="relative z-10 flex items-center gap-3 text-slate-500">
                        <Shield size={16} />
                        <span className="text-[10px] font-bold uppercase tracking-[0.2em]">Secure Induction Protocol Active</span>
                    </div>
                </div>

                {/* Right: Registration Form */}
                <div className="w-full lg:w-1/2 p-10 md:p-16 flex flex-col justify-center overflow-y-auto">
                    <motion.div
                        initial={{ opacity: 0, y: 10 }}
                        animate={{ opacity: 1, y: 0 }}
                        className="max-w-md w-full mx-auto"
                    >
                        <div className="mb-10">
                            <h2 className="text-3xl font-bold text-slate-900 tracking-tight mb-2">Request Access</h2>
                            <p className="text-sm font-semibold text-slate-400">Establish your operative credential sequence.</p>
                        </div>

                        <form onSubmit={handleSubmit} className="space-y-4">
                            <div className="space-y-1.5">
                                <label className="text-[10px] font-bold text-slate-400 uppercase tracking-wider ml-1">Operative Name</label>
                                <div className="relative group">
                                    <User className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-300 group-focus-within:text-indigo-600 transition-colors" size={18} />
                                    <input
                                        name="name"
                                        type="text"
                                        value={formData.name}
                                        onChange={handleChange}
                                        className="w-full bg-slate-50 border border-slate-100 rounded-2xl py-3 pl-11 pr-4 text-sm font-semibold text-slate-900 focus:bg-white focus:border-indigo-500 outline-none transition-all placeholder:text-slate-300"
                                        placeholder="Full Name"
                                        required
                                    />
                                </div>
                            </div>

                            <div className="space-y-1.5">
                                <label className="text-[10px] font-bold text-slate-400 uppercase tracking-wider ml-1">Digital Coordinate</label>
                                <div className="relative group">
                                    <Mail className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-300 group-focus-within:text-indigo-600 transition-colors" size={18} />
                                    <input
                                        name="email"
                                        type="email"
                                        value={formData.email}
                                        onChange={handleChange}
                                        className="w-full bg-slate-50 border border-slate-100 rounded-2xl py-3 pl-11 pr-4 text-sm font-semibold text-slate-900 focus:bg-white focus:border-indigo-500 outline-none transition-all placeholder:text-slate-300"
                                        placeholder="name@company.com"
                                        required
                                    />
                                </div>
                            </div>

                            <div className="grid grid-cols-2 gap-4">
                                <div className="space-y-1.5">
                                    <label className="text-[10px] font-bold text-slate-400 uppercase tracking-wider ml-1">Access Key</label>
                                    <div className="relative group">
                                        <Lock className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-300 group-focus-within:text-indigo-600 transition-colors" size={16} />
                                        <input
                                            name="password"
                                            type={showPassword ? "text" : "password"}
                                            value={formData.password}
                                            onChange={handleChange}
                                            className="w-full bg-slate-50 border border-slate-100 rounded-2xl py-3 pl-10 pr-10 text-xs font-semibold text-slate-900 focus:bg-white focus:border-indigo-500 outline-none transition-all placeholder:text-slate-300"
                                            placeholder="••••••••"
                                            required
                                        />
                                        <button
                                            type="button"
                                            onClick={() => setShowPassword(!showPassword)}
                                            className="absolute right-3 top-1/2 -translate-y-1/2 text-slate-300 hover:text-indigo-600"
                                        >
                                            {showPassword ? <EyeOff size={16} /> : <Eye size={16} />}
                                        </button>
                                    </div>
                                </div>
                                <div className="space-y-1.5">
                                    <label className="text-[10px] font-bold text-slate-400 uppercase tracking-wider ml-1">Confirm Key</label>
                                    <div className="relative group">
                                        <Lock className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-300 group-focus-within:text-indigo-600 transition-colors" size={16} />
                                        <input
                                            name="confirmPassword"
                                            type={showConfirmPassword ? "text" : "password"}
                                            value={formData.confirmPassword}
                                            onChange={handleChange}
                                            className="w-full bg-slate-50 border border-slate-100 rounded-2xl py-3 pl-10 pr-10 text-xs font-semibold text-slate-900 focus:bg-white focus:border-indigo-500 outline-none transition-all placeholder:text-slate-300"
                                            placeholder="••••••••"
                                            required
                                        />
                                        <button
                                            type="button"
                                            onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                                            className="absolute right-3 top-1/2 -translate-y-1/2 text-slate-300 hover:text-indigo-600"
                                        >
                                            {showConfirmPassword ? <EyeOff size={16} /> : <Eye size={16} />}
                                        </button>
                                    </div>
                                </div>
                            </div>

                            <div className="pt-6">
                                <button
                                    disabled={isLoading}
                                    type="submit"
                                    className="w-full bg-indigo-600 hover:bg-indigo-700 text-white rounded-2xl py-4 font-bold text-sm shadow-xl shadow-indigo-100 transition-all flex items-center justify-center gap-3 active:scale-[0.98]"
                                >
                                    {isLoading ? <Loader2 className="animate-spin" size={20} /> : (
                                        <>
                                            Establish Identity
                                            <ChevronRight size={18} />
                                        </>
                                    )}
                                </button>
                            </div>
                        </form>

                        <div className="mt-8 pt-6 border-t border-slate-50 text-center">
                            <p className="text-xs font-bold text-slate-400">
                                Already Authorized?{" "}
                                <Link to="/login" className="text-indigo-600 hover:underline font-bold ml-1">
                                    Access Portal
                                </Link>
                            </p>
                        </div>
                    </motion.div>
                </div>
            </div>
        </div>
    );
};

export default RegisterPage;
