import React, { useState } from "react";
import { Calendar, Clock, FileText, ArrowLeft, CheckCircle2, Link as LinkIcon, Sparkles, Activity, Shield, Zap, Globe, Briefcase } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { meetingService } from "../../services/meetingService";

const ScheduleMeetingPage: React.FC = () => {
  const navigate = useNavigate();
  const [meeting, setMeeting] = useState({
    title: "",
    date: "",
    time: "",
    end_time: "",
    notes: "",
    location: "",
    meeting_link: "",
    is_recurring_mon_fri: false
  });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!meeting.title.trim() || !meeting.date || !meeting.time || !meeting.end_time) {
      return;
    }

    setLoading(true);
    try {
      const createMeetingData = (dateStr: string) => ({
        title: meeting.title,
        description: meeting.notes,
        start_time: new Date(`${dateStr}T${meeting.time}:00`).toISOString(),
        end_time: new Date(`${dateStr}T${meeting.end_time}:00`).toISOString(),
        meeting_link: meeting.meeting_link,
        location: meeting.location
      });

      if (meeting.is_recurring_mon_fri) {
        const baseDate = new Date(meeting.date);
        const datesToCreate: string[] = [];
        const day = baseDate.getDay();
        const diff = baseDate.getDate() - day + (day === 0 ? -6 : 1);
        const monday = new Date(baseDate.setDate(diff));

        for (let i = 0; i < 5; i++) {
          const d = new Date(monday);
          d.setDate(monday.getDate() + i);
          datesToCreate.push(d.toISOString().split('T')[0]);
        }
        await Promise.all(datesToCreate.map(d => meetingService.createMeeting(createMeetingData(d))));
      } else {
        await meetingService.createMeeting(createMeetingData(meeting.date));
      }
      navigate("/meetings");
    } catch (error) {
      console.error("Failed to schedule meeting", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-[#f8fafc] text-slate-900 p-8 lg:p-12 overflow-hidden relative font-sans">
      {/* Background Orbs */}
      <div className="fixed top-0 right-0 w-[700px] h-[700px] bg-indigo-50/50 rounded-full blur-[150px] -mr-64 -mt-64 animate-pulse" />
      <div className="fixed bottom-0 left-0 w-[500px] h-[500px] bg-sky-50/50 rounded-full blur-[120px] -ml-48 -mb-48" />

      <div className="max-w-4xl mx-auto relative z-10">
        {/* Nav Header */}
        <div className="flex items-center justify-between mb-16">
          <button
            onClick={() => navigate("/meetings")}
            className="group flex items-center gap-4 px-6 py-3 bg-white rounded-2xl hover:bg-slate-900 hover:text-white transition-all shadow-sm border border-slate-50"
          >
            <ArrowLeft size={18} className="group-hover:-translate-x-1 transition-transform" />
            <span className="text-[10px] font-black uppercase tracking-widest">Abort & Exit</span>
          </button>
          <div className="flex items-center gap-3 px-5 py-2.5 bg-indigo-50 text-indigo-700 rounded-full text-[10px] font-black uppercase tracking-[0.3em] border border-indigo-100 shadow-sm">
            <Sparkles size={14} className="fill-indigo-600" /> Synchronization Lab
          </div>
        </div>

        <div className="flex flex-col lg:flex-row gap-16">
          {/* Left - Hero Copy */}
          <div className="lg:w-1/3 space-y-8">
            <h1 className="text-6xl font-[1000] tracking-tighter leading-[0.95] text-slate-900">
              Design <span className="gradient-text">Sync.</span>
            </h1>
            <p className="text-slate-500 font-medium text-xl leading-relaxed">
              Calibrate the temporal coordinates for your next high-impact squad alignment.
            </p>
            <div className="pt-8 space-y-6">
              <div className="flex items-start gap-4 p-5 bg-white rounded-[32px] border border-slate-50 shadow-sm">
                <div className="w-10 h-10 bg-indigo-600 rounded-2xl flex items-center justify-center text-white shrink-0"><Shield size={20} /></div>
                <div>
                  <p className="text-[10px] font-black uppercase tracking-widest text-slate-400">Security</p>
                  <p className="text-sm font-bold text-slate-700">Encrypted transmission protocols active.</p>
                </div>
              </div>
              <div className="flex items-start gap-4 p-5 bg-white rounded-[32px] border border-slate-50 shadow-sm">
                <div className="w-10 h-10 bg-emerald-500 rounded-2xl flex items-center justify-center text-white shrink-0"><Zap size={20} /></div>
                <div>
                  <p className="text-[10px] font-black uppercase tracking-widest text-slate-400">Velocity</p>
                  <p className="text-sm font-bold text-slate-700">Real-time sync across the matrix.</p>
                </div>
              </div>
            </div>
          </div>

          {/* Right - Form Module */}
          <div className="lg:w-2/3">
            <motion.div
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              className="bg-white rounded-[56px] border-2 border-slate-50 p-12 shadow-2xl shadow-indigo-500/5"
            >
              <form onSubmit={handleSubmit} className="space-y-10">
                <div className="space-y-3">
                  <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Manifest Title (Required)</label>
                  <input
                    type="text"
                    value={meeting.title}
                    onChange={(e) => setMeeting({ ...meeting, title: e.target.value })}
                    placeholder="e.g. Critical Strategic Convergence"
                    className="w-full px-8 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[28px] text-slate-900 font-bold text-sm focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all placeholder:text-slate-300"
                    required
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                  <div className="space-y-3">
                    <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Temporal Date</label>
                    <input
                      type="date"
                      value={meeting.date}
                      onChange={(e) => setMeeting({ ...meeting, date: e.target.value })}
                      className="w-full px-8 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[28px] text-slate-900 font-bold text-sm focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all appearance-none"
                      required
                    />
                  </div>
                  <div className="grid grid-cols-2 gap-4">
                    <div className="space-y-3">
                      <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Commence</label>
                      <input
                        type="time"
                        value={meeting.time}
                        onChange={(e) => setMeeting({ ...meeting, time: e.target.value })}
                        className="w-full px-6 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[24px] text-slate-900 font-bold text-sm focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all"
                        required
                      />
                    </div>
                    <div className="space-y-3">
                      <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Terminate</label>
                      <input
                        type="time"
                        value={meeting.end_time}
                        onChange={(e) => setMeeting({ ...meeting, end_time: e.target.value })}
                        className="w-full px-6 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[24px] text-slate-900 font-bold text-sm focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all"
                        required
                      />
                    </div>
                  </div>
                </div>

                <motion.div
                  whileHover={{ scale: 1.02 }}
                  className="flex items-center gap-5 p-8 bg-indigo-50/30 rounded-[32px] border-2 border-indigo-100 shadow-xl shadow-indigo-100/10 cursor-pointer"
                  onClick={() => setMeeting({ ...meeting, is_recurring_mon_fri: !meeting.is_recurring_mon_fri })}
                >
                  <div className={`w-10 h-10 rounded-2xl flex items-center justify-center transition-all ${meeting.is_recurring_mon_fri ? 'bg-indigo-600 text-white shadow-lg' : 'bg-white text-indigo-300'}`}>
                    <Activity size={20} className={meeting.is_recurring_mon_fri ? 'animate-pulse' : ''} />
                  </div>
                  <div className="flex-1">
                    <p className="text-[10px] font-black uppercase tracking-widest text-indigo-900/60 leading-none mb-1">Weekly Pulse Loop</p>
                    <p className="text-sm font-black text-indigo-900 uppercase tracking-tight">Synchronize Monday through Friday</p>
                  </div>
                  <div className={`h-6 w-11 rounded-full relative transition-all ${meeting.is_recurring_mon_fri ? 'bg-indigo-600' : 'bg-slate-200'}`}>
                    <div className={`absolute top-1 w-4 h-4 bg-white rounded-full transition-all ${meeting.is_recurring_mon_fri ? 'left-6' : 'left-1'}`} />
                  </div>
                </motion.div>

                <div className="space-y-3">
                  <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Neural Entry Link</label>
                  <div className="relative">
                    <input
                      type="url"
                      value={meeting.meeting_link}
                      onChange={(e) => setMeeting({ ...meeting, meeting_link: e.target.value })}
                      placeholder="https://zoom.us/matrix/..."
                      className="w-full px-16 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[28px] text-slate-900 font-bold text-sm focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all placeholder:text-slate-300"
                    />
                    <LinkIcon size={18} className="absolute left-7 top-1/2 -translate-y-1/2 text-slate-300" />
                  </div>
                </div>

                <div className="space-y-3">
                  <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Mission Brief (Notes)</label>
                  <textarea
                    value={meeting.notes}
                    onChange={(e) => setMeeting({ ...meeting, notes: e.target.value })}
                    rows={4}
                    placeholder="Outline the operational objectives..."
                    className="w-full px-8 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[32px] text-slate-900 font-bold text-sm focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all placeholder:text-slate-300 resize-none"
                  />
                </div>

                <div className="pt-8 flex items-center justify-end gap-10">
                  <button
                    type="button"
                    onClick={() => navigate("/meetings")}
                    className="text-[10px] font-black uppercase tracking-widest text-slate-400 hover:text-slate-900 transition-colors"
                  >
                    Cancel Sync
                  </button>
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    type="submit"
                    disabled={loading}
                    className="h-16 px-12 bg-slate-900 text-white rounded-[28px] font-black uppercase tracking-widest text-[10px] shadow-2xl shadow-indigo-500/10 hover:bg-indigo-600 transition-all flex items-center gap-4"
                  >
                    {loading ? <Activity size={20} className="animate-spin" /> : <CheckCircle2 size={20} />}
                    Commit Schedule
                  </motion.button>
                </div>
              </form>
            </motion.div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ScheduleMeetingPage;
