import React from 'react';
import { motion } from 'framer-motion';

const ApiStatusPage = () => {
    return (
        <div className="p-6 max-w-3xl mx-auto space-y-8">
            <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="space-y-4"
            >
                <h1 className="text-3xl font-bold text-slate-800">System Status</h1>
                <div className="bg-green-50 border border-green-200 rounded-lg p-4 flex items-center space-x-3">
                    <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
                    <span className="text-green-800 font-medium">All systems operational</span>
                </div>
            </motion.div>

            <div className="space-y-4">
                {['API', 'Dashboard', 'Database', 'Storage', 'Notifications'].map((service) => (
                    <div key={service} className="flex items-center justify-between p-4 bg-white rounded-lg border border-gray-100 shadow-sm">
                        <span className="font-medium text-gray-700">{service}</span>
                        <span className="text-sm text-green-600 font-medium">Operational</span>
                    </div>
                ))}
            </div>

            <div className="pt-8 border-t border-gray-100">
                <h3 className="text-lg font-semibold mb-4">Past Incidents</h3>
                <p className="text-gray-500 text-sm">No incidents reported in the last 30 days.</p>
            </div>
        </div>
    );
};

export default ApiStatusPage;
