import React, { useState, useEffect } from "react";
import { Outlet, useLocation } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import Topbar from "../components/Topbar";
import Footer from "../components/Footer";

const MainLayout: React.FC = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const location = useLocation();

  // Close sidebar on route change (for mobile)
  useEffect(() => {
    setIsSidebarOpen(false);
  }, [location]);

  // Toggle sidebar for mobile
  const toggleSidebar = () => {
    setIsSidebarOpen((prev) => !prev);
  };

  return (
    <div className="flex min-h-screen bg-slate-50/50 text-slate-900 font-sans selection:bg-blue-100 selection:text-blue-700">
      {/* üåë Mobile Overlay */}
      {isSidebarOpen && (
        <div
          className="fixed inset-0 bg-slate-900/40 backdrop-blur-sm z-40 lg:hidden transition-all duration-300"
          onClick={() => setIsSidebarOpen(false)}
        />
      )}

      {/* üß≠ Sidebar - Fixed on desktop, slide-in on mobile */}
      <aside
        className={`fixed top-0 left-0 h-full w-72 bg-white border-r border-slate-200 shadow-2xl lg:shadow-none z-50 transition-transform duration-300 ease-in-out lg:translate-x-0 ${isSidebarOpen ? "translate-x-0" : "-translate-x-full"
          }`}
      >
        <Sidebar />
      </aside>

      {/* üìÑ Main Content Wrapper */}
      <div className="flex flex-col flex-1 min-h-screen lg:ml-72 transition-all duration-300">
        {/* üîù Sticky Topbar with Glassmorphism */}
        <header className="sticky top-0 z-30 bg-white/70 backdrop-blur-xl border-b border-slate-200/60 shadow-sm transition-shadow">
          <Topbar onMenuClick={toggleSidebar} isSidebarOpen={isSidebarOpen} />
        </header>

        {/* üßæ Page Content with Smooth Transition */}
        <main className="flex-1 overflow-x-hidden">
          <div className="p-4 sm:p-6 lg:p-8 animate-in fade-in slide-in-from-bottom-2 duration-500">
            <div className="max-w-7xl mx-auto">
              <Outlet />
            </div>
          </div>
        </main>

        {/* ü¶∂ Footer */}
        <footer className="mt-auto bg-white border-t border-slate-200">
          <Footer />
        </footer>
      </div>
    </div>
  );
};

export default MainLayout;
