import React from "react";
import {
  LayoutDashboard,
  MessageSquare,
  CalendarDays,
  FileUp,
  Workflow,
  ClipboardList,
  LogOut,
  UsersRound,
  X,
} from "lucide-react";
import { useNavigate, useLocation } from "react-router-dom";

interface SidebarProps {
  isOpen: boolean;
  onClose: () => void;
}

const Sidebar: React.FC<SidebarProps> = ({ isOpen, onClose }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const navItems = [
    { label: "Dashboard", icon: LayoutDashboard, path: "/dashboard" },
    { label: "Chat", icon: MessageSquare, path: "/chat" },
    { label: "Tasks", icon: ClipboardList, path: "/tasks" },
    { label: "Meetings", icon: UsersRound, path: "/meetings" },
    { label: "Calendar", icon: CalendarDays, path: "/calendar" },
    { label: "Files", icon: FileUp, path: "/files" },
    { label: "Workflows", icon: Workflow, path: "/workflows" },
  ];

  return (
    <>
      {/* Overlay (mobile) */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black/40 z-30 lg:hidden"
          onClick={onClose}
        />
      )}

      <aside
        className={`
          fixed top-0 left-0 z-40 h-screen w-64 bg-white border-r
          transform transition-transform duration-300
          ${isOpen ? "translate-x-0" : "-translate-x-full"}
          lg:translate-x-0
        `}
      >
        {/* Mobile Close */}
        <div className="lg:hidden flex justify-end p-4">
          <button onClick={onClose}>
            <X />
          </button>
        </div>

        {/* Logo */}
        <div
          className="flex flex-col items-center py-6 cursor-pointer"
          onClick={() => {
            navigate("/dashboard");
            onClose();
          }}
        >
          <img src="/logo1.png" className="w-24" />
          <h1 className="mt-2 font-semibold">Collabryta</h1>
        </div>

        {/* Nav */}
        <nav className="px-4 space-y-1">
          {navItems.map(({ label, icon: Icon, path }) => {
            const active = location.pathname === path;
            return (
              <button
                key={path}
                onClick={() => {
                  navigate(path);
                  onClose();
                }}
                className={`w-full flex items-center gap-3 px-3 py-2 rounded-lg text-sm
                  ${active
                    ? "bg-blue-100 text-blue-700"
                    : "hover:bg-gray-100"
                  }`}
              >
                <Icon size={18} />
                {label}
              </button>
            );
          })}
        </nav>

        {/* Logout */}
        <div className="absolute bottom-6 w-full px-4">
          <button
            onClick={() => navigate("/login")}
            className="w-full flex items-center justify-center gap-2 py-2 bg-gray-100 text-red-600 rounded-lg"
          >
            <LogOut size={18} />
            Logout
          </button>
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
