// Mock data for now as backend doesn't have messages yet
export interface ConversationUser {
    id: number;
    name: string;
    avatar?: string;
    unread_count?: number;
    status?: 'online' | 'offline' | 'busy';
}

export interface Message {
    id: number;
    sender_id: number | string;
    content: string;
    timestamp: string;
}

export const messageService = {
    getConversations: async (): Promise<ConversationUser[]> => {
        // Mock response
        return [
            { id: 1, name: "Alice Smith", unread_count: 2, status: 'online' },
            { id: 2, name: "Bob Jones", unread_count: 0, status: 'offline' },
            { id: 3, name: "Charlie Day", unread_count: 5, status: 'busy' }
        ];
    },

    getMessages: async (userId: number): Promise<Message[]> => {
        // Mock response
        return [
            { id: 1, sender_id: userId, content: "Hey, how are you?", timestamp: new Date(Date.now() - 100000).toISOString() },
            { id: 2, sender_id: "me", content: "I'm good, thanks! Working on the frontend.", timestamp: new Date(Date.now() - 50000).toISOString() },
            { id: 3, sender_id: userId, content: "Great progress!", timestamp: new Date().toISOString() }
        ];
    },

    sendMessage: async (userId: number, content: string): Promise<Message> => {
        return {
            id: Date.now(),
            sender_id: "me",
            content: content,
            timestamp: new Date().toISOString()
        };
    }
};
