import React, { createContext, useContext, useEffect, useState, useCallback, useRef } from 'react';
import { notificationService, Notification } from '../services/notificationService';

interface NotificationContextType {
    notifications: Notification[];
    unreadCount: number;
    addNotification: (notification: Notification) => void;
    markAsRead: (id: number) => Promise<void>;
    markAllAsRead: () => Promise<void>;
    subscribe: (type: string, callback: (data: any) => void) => () => void;
}

const NotificationContext = createContext<NotificationContextType | undefined>(undefined);

export const NotificationProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    const [notifications, setNotifications] = useState<Notification[]>([]);
    const [unreadCount, setUnreadCount] = useState(0);
    const listeners = useRef<{ [type: string]: ((data: any) => void)[] }>({});
    const wsRef = useRef<WebSocket | null>(null);

    const subscribe = useCallback((type: string, callback: (data: any) => void) => {
        if (!listeners.current[type]) {
            listeners.current[type] = [];
        }
        listeners.current[type].push(callback);
        return () => {
            listeners.current[type] = listeners.current[type].filter(cb => cb !== callback);
        };
    }, []);

    const fetchNotifications = useCallback(async () => {
        const token = localStorage.getItem('token');
        if (!token) return;
        try {
            console.log('DEBUG: Fetching initial notifications...');
            const data = await notificationService.getNotifications();
            setNotifications(data);
            setUnreadCount(data.filter(n => !n.is_read).length);
        } catch (error) {
            console.error('Failed to fetch notifications:', error);
        }
    }, []);

    useEffect(() => {
        fetchNotifications();
    }, [fetchNotifications]);

    const addNotification = useCallback((notification: Notification) => {
        console.log('DEBUG: Adding new notification to list:', notification);
        setNotifications(prev => {
            // Avoid duplicates
            if (prev.find(n => n.id === notification.id)) return prev;
            return [notification, ...prev];
        });
        if (!notification.is_read) {
            setUnreadCount(prev => prev + 1);
        }
    }, []);

    const markAsRead = async (id: number) => {
        try {
            await notificationService.markAsRead(id);
            setNotifications(prev => prev.map(n => n.id === id ? { ...n, is_read: true } : n));
            setUnreadCount(prev => Math.max(0, prev - 1));
        } catch (error) {
            console.error('Failed to mark notification as read:', error);
        }
    };

    const markAllAsRead = async () => {
        try {
            await notificationService.markAllAsRead();
            setNotifications(prev => prev.map(n => ({ ...n, is_read: true })));
            setUnreadCount(0);
        } catch (error) {
            console.error('Failed to mark all as read:', error);
        }
    }

    useEffect(() => {
        let timer: any;
        const connect = () => {
            const token = localStorage.getItem('token');
            if (!token) {
                // If no token, wait 3s and check again
                timer = setTimeout(connect, 3000);
                return;
            }

            if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
                return;
            }

            console.log('DEBUG: Connecting to WebSocket...');
            const wsUrl = `ws://localhost:8000/api/v1/ws?token=${token}`;
            const ws = new WebSocket(wsUrl);
            wsRef.current = ws;

            ws.onopen = () => {
                console.log('DEBUG: WebSocket Connected âœ…');
                // Refresh list on connect to ensure we have latest
                fetchNotifications();
            };

            ws.onmessage = (event) => {
                try {
                    const message = JSON.parse(event.data);
                    console.log('DEBUG: WS Message received:', message);

                    if (message.type && listeners.current[message.type]) {
                        listeners.current[message.type].forEach(cb => cb(message.data));
                    }

                    if (message.type === 'notification') {
                        addNotification(message.data);
                    }
                } catch (err) {
                    console.error('Error parsing WS message:', err);
                }
            };

            ws.onclose = (event) => {
                console.log(`DEBUG: WebSocket Disconnected (Code: ${event.code}), retrying in 5s... ðŸ”„`);
                wsRef.current = null;
                timer = setTimeout(connect, 5000);
            };

            ws.onerror = (err) => {
                console.error('DEBUG: WebSocket Error:', err);
            };
        };

        connect();

        return () => {
            clearTimeout(timer);
            if (wsRef.current) {
                wsRef.current.close();
                wsRef.current = null;
            }
        };
    }, [addNotification, fetchNotifications]);

    return (
        <NotificationContext.Provider value={{ notifications, unreadCount, addNotification, markAsRead, markAllAsRead, subscribe }}>
            {children}
        </NotificationContext.Provider>
    );
};

export const useNotifications = () => {
    const context = useContext(NotificationContext);
    if (!context) throw new Error('useNotifications must be used within a NotificationProvider');
    return context;
};
