import React, { useState, useEffect } from "react";
import {
  Plus,
  Calendar,
  User as UserIcon,
  LayoutGrid,
  List as ListIcon,
  Search,
  Loader2,
  Trash2,
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { taskService, Task } from "../../services/taskService";
import { motion } from "framer-motion";

const TasksPage: React.FC = () => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [viewMode, setViewMode] = useState<"board" | "list">("board");

  // Load Tasks
  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    } finally {
      setLoading(false);
    }
  };

  // Helper to normalize status for display
  const normalizeStatus = (status: string) => {
    const s = status ? status.toLowerCase() : "";
    if (s === "todo" || s === "upcoming") return "Upcoming";
    if (s === "in progress" || s === "inprogress") return "In Progress";
    if (s === "completed" || s === "done") return "Completed";
    return "Upcoming";
  };

  const getFilteredTasks = () => {
    return tasks.filter(t =>
      t.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (t.description && t.description.toLowerCase().includes(searchQuery.toLowerCase()))
    );
  };

  const tasksByStatus = {
    "Upcoming": getFilteredTasks().filter(t => normalizeStatus(t.status) === "Upcoming"),
    "In Progress": getFilteredTasks().filter(t => normalizeStatus(t.status) === "In Progress"),
    "Completed": getFilteredTasks().filter(t => normalizeStatus(t.status) === "Completed"),
  };

  const handleStatusUpdate = async (task: Task, newStatus: string) => {
    // Optimistic Update
    setTasks(prev => prev.map(t => t.id === task.id ? { ...t, status: newStatus } : t));
    try {
      await taskService.updateTask(task.id, { status: newStatus });
    } catch (error) {
      console.error("Failed to update status");
      fetchTasks(); // Revert on error
    }
  };

  const handleDelete = async (e: React.MouseEvent, id: number) => {
    e.stopPropagation();
    if (!window.confirm("Delete this task?")) return;
    setTasks(prev => prev.filter(t => t.id !== id));
    try {
      await taskService.deleteTask(id);
    } catch (error) {
      console.error("Failed to delete task");
    }
  };

  const getPriorityColor = (p: string) => {
    const priority = p ? p.toLowerCase() : "normal";
    if (priority === "urgent" || priority === "high") return "text-red-700 bg-red-50 border-red-200";
    if (priority === "medium" || priority === "normal") return "text-orange-700 bg-orange-50 border-orange-200";
    return "text-green-700 bg-green-50 border-green-200"; // Low
  };

  return (
    <div className="min-h-screen p-6 md:p-8 space-y-8 animate-in fade-in duration-500">

      {/* üü¢ Header Section */}
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-6">
        <div>
          <h1 className="text-3xl font-bold text-slate-900 tracking-tight">Task Board</h1>
          <div className="flex items-center gap-2 mt-2 text-slate-500 text-sm">
            <span>Manage your project workflow step-by-step.</span>
            <span className="w-1 h-1 rounded-full bg-slate-300"></span>
            <span className="font-medium text-slate-700">{tasks.length} Tasks Total</span>
          </div>
        </div>

        <div className="flex items-center gap-3">
          <div className="relative group">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-indigo-500 transition-colors" size={18} />
            <input
              type="text"
              placeholder="Search..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 pr-4 py-2.5 bg-white border border-slate-200 rounded-xl text-sm focus:outline-none focus:ring-2 focus:ring-indigo-500/50 shadow-sm w-48 md:w-64 transition-all"
            />
          </div>

          <div className="flex bg-white p-1 rounded-xl border border-slate-200 shadow-sm">
            <button
              onClick={() => setViewMode("board")}
              className={`p-2 rounded-lg transition-all ${viewMode === 'board' ? 'bg-indigo-50 text-indigo-600' : 'text-slate-400 hover:text-slate-600'}`}
            >
              <LayoutGrid size={18} />
            </button>
            <button
              onClick={() => setViewMode("list")}
              className={`p-2 rounded-lg transition-all ${viewMode === 'list' ? 'bg-indigo-50 text-indigo-600' : 'text-slate-400 hover:text-slate-600'}`}
            >
              <ListIcon size={18} />
            </button>
          </div>

          <button
            onClick={() => navigate("/tasks/new")}
            className="flex items-center gap-2 bg-slate-900 hover:bg-slate-800 text-white px-5 py-2.5 rounded-xl font-semibold shadow-lg shadow-slate-200 hover:shadow-xl hover:-translate-y-0.5 transition-all active:scale-95"
          >
            <Plus size={18} /> <span className="hidden md:inline">New Task</span>
          </button>
        </div>
      </div>

      {loading ? (
        <div className="h-96 flex items-center justify-center">
          <Loader2 className="w-10 h-10 text-indigo-600 animate-spin" />
        </div>
      ) : (
        <>
          {viewMode === 'board' ? (
            /* üìã Kanban Board Layout */
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 overflow-x-auto pb-4">

              <KanbanColumn
                title="Upcoming"
                color="bg-indigo-500"
                count={tasksByStatus["Upcoming"].length}
                tasks={tasksByStatus["Upcoming"]}
                onUpdateStatus={handleStatusUpdate}
                onDelete={handleDelete}
                getPriorityColor={getPriorityColor}
                navigate={navigate}
              />

              <KanbanColumn
                title="In Progress"
                color="bg-orange-500"
                count={tasksByStatus["In Progress"].length}
                tasks={tasksByStatus["In Progress"]}
                onUpdateStatus={handleStatusUpdate}
                onDelete={handleDelete}
                getPriorityColor={getPriorityColor}
                navigate={navigate}
              />

              <KanbanColumn
                title="Completed"
                color="bg-green-500"
                count={tasksByStatus["Completed"].length}
                tasks={tasksByStatus["Completed"]}
                onUpdateStatus={handleStatusUpdate}
                onDelete={handleDelete}
                getPriorityColor={getPriorityColor}
                navigate={navigate}
              />

            </div>
          ) : (
            /* üìù List Layout */
            <div className="space-y-3 max-w-5xl mx-auto">
              {["Upcoming", "In Progress", "Completed"].map((status) => (
                <div key={status} className="mb-8">
                  <h3 className="text-sm font-bold text-slate-500 uppercase tracking-wider mb-4 border-b border-slate-100 pb-2">{status}</h3>
                  <div className="space-y-3">
                    {tasksByStatus[status as keyof typeof tasksByStatus].map(task => (
                      <TaskCardList
                        key={task.id}
                        task={task}
                        onDelete={handleDelete}
                        onUpdateStatus={handleStatusUpdate}
                        navigate={navigate}
                      />
                    ))}
                    {tasksByStatus[status as keyof typeof tasksByStatus].length === 0 && (
                      <div className="p-4 text-center text-slate-400 text-sm border border-dashed border-slate-200 rounded-lg">No tasks</div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </>
      )}

    </div>
  );
};

/* üß© Sub-Components */

const KanbanColumn = ({ title, color, count, tasks, onUpdateStatus, onDelete, getPriorityColor, navigate }: any) => (
  <div className="flex flex-col h-full min-h-[500px]">
    <div className="flex items-center justify-between mb-4 bg-white p-3 rounded-xl border border-slate-100 shadow-sm border-t-4" style={{ borderColor: title === 'Upcoming' ? '#6366f1' : title === 'In Progress' ? '#f97316' : '#22c55e' }}>
      <div className="flex items-center gap-3">
        <h3 className="font-bold text-slate-800">{title}</h3>
      </div>
      <span className="bg-slate-100 text-slate-600 px-2.5 py-0.5 rounded-full text-xs font-bold">{count}</span>
    </div>

    <div className="flex-1 space-y-4 bg-slate-100/50 p-2 rounded-xl border border-slate-200/50">
      {tasks.length === 0 ? (
        <div className="h-32 flex items-center justify-center text-slate-400 text-sm italic">
          No tasks
        </div>
      ) : (
        tasks.map((task: Task) => (
          <TaskCard
            key={task.id}
            task={task}
            onDelete={onDelete}
            onUpdateStatus={onUpdateStatus}
            getPriorityColor={getPriorityColor}
            navigate={navigate}
          />
        ))
      )}
    </div>
  </div>
);

const TaskCard = ({ task, onDelete, onUpdateStatus, getPriorityColor, navigate }: any) => (
  <motion.div
    layoutId={String(task.id)}
    initial={{ opacity: 0, y: 10 }}
    animate={{ opacity: 1, y: 0 }}
    whileHover={{ y: -4, transition: { duration: 0.2 } }}
    className="bg-white p-4 rounded-xl border border-slate-200 shadow-sm hover:shadow-md transition-all cursor-pointer group"
  >
    <div className="flex justify-between items-start mb-2">
      <span className={`px-2 py-0.5 text-[10px] font-bold uppercase tracking-wide rounded-full border ${getPriorityColor(task.priority)}`}>
        {task.priority || "Normal"}
      </span>
      <div className="opacity-0 group-hover:opacity-100 transition-opacity">
        <button
          onClick={(e) => onDelete(e, task.id)}
          className="text-slate-300 hover:text-red-500 transition-colors p-1"
        >
          <Trash2 size={14} />
        </button>
      </div>
    </div>

    <h4 onClick={() => navigate(`/tasks/edit/${task.id}`)} className="font-bold text-slate-800 mb-1 hover:text-indigo-600 transition-colors">{task.title}</h4>
    <p className="text-slate-500 text-xs line-clamp-2 mb-4">{task.description || "No description provided."}</p>

    <div className="flex items-center justify-between border-t border-slate-50 pt-3">
      <div className="flex items-center gap-2">
        {task.assigned_to_user ? (
          <div className="w-6 h-6 rounded-full bg-indigo-100 flex items-center justify-center text-[10px] font-bold text-indigo-700">
            {task.assigned_to_user.name.charAt(0)}
          </div>
        ) : (
          <div className="w-6 h-6 rounded-full bg-slate-100 flex items-center justify-center text-slate-400">
            <UserIcon size={12} />
          </div>
        )}
        {task.due_date && (
          <span className="flex items-center gap-1 text-[10px] font-medium text-slate-400">
            <Calendar size={10} />
            {new Date(task.due_date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' })}
          </span>
        )}
      </div>

      {/* Move Actions */}
      <div className="flex gap-1">
        {normalizeStatus(task.status) !== "Upcoming" && (
          <button
            onClick={(e) => { e.stopPropagation(); onUpdateStatus(task, "Upcoming"); }}
            className="w-5 h-5 flex items-center justify-center rounded-full hover:bg-slate-100 group/btn" title="Move to Upcoming"
          >
            <div className="w-2 h-2 rounded-full bg-indigo-500 group-hover/btn:scale-125 transition-transform"></div>
          </button>
        )}
        {normalizeStatus(task.status) !== "In Progress" && (
          <button
            onClick={(e) => { e.stopPropagation(); onUpdateStatus(task, "In Progress"); }}
            className="w-5 h-5 flex items-center justify-center rounded-full hover:bg-slate-100 group/btn" title="Move to In Progress"
          >
            <div className="w-2 h-2 rounded-full bg-orange-500 group-hover/btn:scale-125 transition-transform"></div>
          </button>
        )}
        {normalizeStatus(task.status) !== "Completed" && (
          <button
            onClick={(e) => { e.stopPropagation(); onUpdateStatus(task, "Completed"); }}
            className="w-5 h-5 flex items-center justify-center rounded-full hover:bg-slate-100 group/btn" title="Move to Completed"
          >
            <div className="w-2 h-2 rounded-full bg-green-500 group-hover/btn:scale-125 transition-transform"></div>
          </button>
        )}
      </div>
    </div>
  </motion.div>
);

const normalizeStatus = (status: string) => {
  const s = status ? status.toLowerCase() : "";
  if (s === "todo" || s === "upcoming") return "Upcoming";
  if (s === "in progress" || s === "inprogress") return "In Progress";
  if (s === "completed" || s === "done") return "Completed";
  return "Upcoming";
};

const TaskCardList = ({ task, onDelete, onUpdateStatus, navigate }: any) => (
  <div className="flex items-center justify-between bg-white p-4 rounded-xl border border-slate-200 shadow-sm hover:shadow-md transition-all group">
    <div className="flex items-center gap-4 cursor-pointer" onClick={() => navigate(`/tasks/edit/${task.id}`)}>
      <div className={`w-2.5 h-2.5 rounded-full ${normalizeStatus(task.status) === 'Completed' ? 'bg-green-500' : normalizeStatus(task.status) === 'In Progress' ? 'bg-orange-500' : 'bg-indigo-500'}`}></div>
      <div>
        <h4 className="font-bold text-slate-800 text-sm hover:text-indigo-600">{task.title}</h4>
        <p className="text-slate-500 text-xs hidden sm:block">{task.description || "No description"}</p>
      </div>
    </div>
    <div className="flex items-center gap-6">
      {task.due_date && (
        <span className="text-xs text-slate-500 bg-slate-50 px-2.5 py-1 rounded-md border border-slate-100 font-medium">
          {new Date(task.due_date).toLocaleDateString()}
        </span>
      )}
      <button onClick={(e) => onDelete(e, task.id)} className="text-slate-300 hover:text-red-500 transition-colors">
        <Trash2 size={16} />
      </button>
    </div>
  </div>
);

export default TasksPage;
