import React from 'react';
import { motion } from 'framer-motion';

const BlogPage = () => {
    return (
        <div className="p-6 max-w-5xl mx-auto space-y-8">
            <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="space-y-4"
            >
                <h1 className="text-4xl font-bold text-slate-800">The Collabryta Blog</h1>
                <p className="text-slate-600 text-lg">
                    Stories, updates, and tips for better teamwork.
                </p>
            </motion.div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                {[1, 2, 3, 4].map((i) => (
                    <div key={i} className="bg-white rounded-xl overflow-hidden shadow-sm border border-gray-100 hover:shadow-md transition-shadow">
                        <div className="h-48 bg-gray-200 w-full animate-pulse"></div>
                        <div className="p-6">
                            <span className="text-xs font-semibold text-indigo-600 uppercase tracking-wider">Productivity</span>
                            <h3 className="text-xl font-bold mt-2 mb-3">Maximizing Efficiency with Workflows</h3>
                            <p className="text-gray-500 text-sm mb-4">
                                Learn how to automate your daily tasks and focus on what matters most.
                            </p>
                            <a href="#!" className="text-indigo-600 font-medium hover:underline">Read more â†’</a>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default BlogPage;
