import React, { useState, useEffect } from "react";
import {
    User,
    Bell,
    ShieldCheck,
    Save,
    Loader2,
    CheckCircle2,
    Eye,
    Smartphone,
    Globe,
    Lock,
    Activity,
    Shield,
    Zap,
    Cpu,
    Star
} from "lucide-react";
import { authService } from "../../services/authService";
import { motion, AnimatePresence } from "framer-motion";

const SettingsPage: React.FC = () => {
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [success, setSuccess] = useState(false);
    const [settings, setSettings] = useState<any>(null);
    const [show2FAModal, setShow2FAModal] = useState(false);
    const [otpCode, setOtpCode] = useState("");
    const [is2FALoading, setIs2FALoading] = useState(false);
    const [testNotifStatus, setTestNotifStatus] = useState<string | null>(null);

    const translations: any = {
        "English (US)": {
            title: "Matrix configuration",
            subtitle: "Calibrate your user interface and operational preferences.",
            save: "Save Protocol",
            saving: "Executing...",
            saved: "Protocol Synchronized"
        }
    };

    const t = translations["English (US)"];

    useEffect(() => {
        const fetchSettings = async () => {
            try {
                const data = await authService.getCurrentUser();
                setSettings(data);
            } catch (error) {
                console.error("Failed to load settings", error);
            } finally {
                setLoading(false);
            }
        };
        fetchSettings();
    }, []);

    const handleUpdate = (field: string, value: any) => {
        setSettings((prev: any) => ({ ...prev, [field]: value }));
    };

    const handleSave = async () => {
        try {
            setSaving(true);
            setSuccess(false);
            const { id, email, hashed_password, last_seen, status, ...updateData } = settings;
            await authService.updateProfile(updateData);
            setSuccess(true);
            setTimeout(() => setSuccess(false), 3000);
        } catch (error) {
            console.error("Failed to save settings", error);
        } finally {
            setSaving(false);
        }
    };

    const handleSendTestNotif = () => {
        setTestNotifStatus("Transmitting...");
        setTimeout(() => {
            setTestNotifStatus("Delivered!");
            setTimeout(() => setTestNotifStatus(null), 3000);
        }, 1500);
    };

    const handleEnable2FA = () => {
        if (settings.two_factor_enabled) {
            handleUpdate('two_factor_enabled', false);
        } else {
            setShow2FAModal(true);
        }
    };

    const handleVerifyOTP = () => {
        setIs2FALoading(true);
        // Simulate OTP verification
        setTimeout(() => {
            if (otpCode === "123456") {
                handleUpdate('two_factor_enabled', true);
                setShow2FAModal(false);
                setOtpCode("");
            } else {
                alert("Invalid OTP! Try 123456");
            }
            setIs2FALoading(false);
        }, 1000);
    };

    if (loading || !settings) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-[#f8fafc]">
                <div className="w-16 h-16 bg-indigo-600 rounded-3xl flex items-center justify-center animate-bounce shadow-2xl">
                    <Activity size={32} className="text-white" />
                </div>
            </div>
        );
    }

    const Section = ({ icon: Icon, title, desc, children }: any) => (
        <section className="bg-white border-2 border-slate-50 rounded-[48px] overflow-hidden shadow-xl shadow-slate-200/50">
            <div className="px-10 py-8 bg-slate-50/50 border-b border-slate-50 flex items-center gap-6">
                <div className="p-4 bg-white rounded-2xl shadow-sm text-indigo-600">
                    <Icon size={24} />
                </div>
                <div>
                    <h2 className="text-xl font-[1000] text-slate-900 tracking-tight uppercase leading-none">{title}</h2>
                    <p className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mt-2">{desc}</p>
                </div>
            </div>
            <div className="p-10">{children}</div>
        </section>);

    const Toggle = ({ active, label, subtitle, field, disabled }: any) => (
        <div className={`flex items-center justify-between py-6 first:pt-0 border-b border-slate-50 last:border-0 last:pb-0 ${disabled ? 'opacity-50' : ''}`}>
            <div>
                <p className="text-sm font-black text-slate-800 uppercase tracking-tight">{label}</p>
                <p className="text-xs font-bold text-slate-400 mt-1">{subtitle}</p>
            </div>
            <button
                onClick={() => !disabled && handleUpdate(field, !active)}
                disabled={disabled}
                className={`relative inline-flex h-7 w-14 items-center rounded-full transition-all duration-500 focus:outline-none ${active ? 'bg-indigo-600 shadow-lg shadow-indigo-200' : 'bg-slate-200'} ${disabled ? 'cursor-not-allowed' : ''}`}
            >
                <span className={`inline-block h-5 w-5 transform rounded-full bg-white shadow-sm transition-transform duration-500 ${active ? 'translate-x-8' : 'translate-x-1'}`} />
            </button>
        </div>
    );

    return (
        <div className="min-h-screen bg-[#f8fafc] text-slate-900 p-8 lg:p-12 overflow-hidden relative font-sans">
            {/* Background Orbs */}
            <div className="fixed top-0 right-0 w-[700px] h-[700px] bg-indigo-50/50 rounded-full blur-[150px] -mr-64 -mt-64 animate-pulse" />
            <div className="fixed bottom-0 left-0 w-[500px] h-[500px] bg-sky-50/50 rounded-full blur-[120px] -ml-48 -mb-48" />

            <div className="max-w-[1000px] mx-auto relative z-10 space-y-12">

                {/* Elite Header */}
                <div className="flex flex-col lg:flex-row lg:items-end justify-between gap-12 mb-20">
                    <div className="space-y-6">
                        <motion.div
                            initial={{ opacity: 0, x: -20 }}
                            animate={{ opacity: 1, x: 0 }}
                            className="inline-flex items-center gap-2 px-4 py-2 bg-indigo-50 text-indigo-700 rounded-full text-[10px] font-black uppercase tracking-[0.3em] border border-indigo-100 shadow-sm"
                        >
                            <Cpu size={14} className="fill-indigo-600" /> System Core
                        </motion.div>
                        <h1 className="text-7xl font-[1000] tracking-tight leading-[0.95] text-slate-900">
                            Central <span className="gradient-text">Matrix.</span>
                        </h1>
                        <p className="text-slate-500 font-medium text-xl max-w-xl leading-relaxed">
                            Fine-tune the synchronization protocols and interface dynamics of your identity.
                        </p>
                    </div>

                    <motion.button
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                        onClick={handleSave}
                        disabled={saving}
                        className={`h-16 px-12 rounded-[28px] font-black uppercase tracking-widest text-[10px] shadow-2xl transition-all flex items-center gap-4 ${success ? 'bg-emerald-500 text-white shadow-emerald-200' : 'bg-slate-900 text-white shadow-slate-200 hover:bg-indigo-600'
                            }`}
                    >
                        {saving ? <Loader2 size={20} className="animate-spin" /> : success ? <CheckCircle2 size={20} /> : <Save size={20} />}
                        {saving ? t.saving : success ? t.saved : t.save}
                    </motion.button>
                </div>

                {/* Profile Section */}
                <Section icon={User} title="Identity Records" desc="Operative profile and matrix identification">
                    <div className="space-y-12">
                        <div className="flex items-center gap-8">
                            <div className="relative">
                                <div className="w-28 h-28 rounded-[40px] bg-white border-2 border-slate-50 shadow-2xl overflow-hidden flex items-center justify-center transition-transform hover:rotate-6">
                                    {settings.avatar ? (
                                        <img src={settings.avatar} alt="Avatar" className="w-full h-full object-cover" />
                                    ) : (
                                        <span className="text-3xl font-[1000] text-indigo-600">{settings.name?.charAt(0)}</span>
                                    )}
                                </div>
                                <div className="absolute -bottom-2 -right-2 w-10 h-10 bg-indigo-600 text-white rounded-2xl flex items-center justify-center shadow-lg border-4 border-white">
                                    <Star size={16} className="fill-white" />
                                </div>
                            </div>
                            <div>
                                <h3 className="text-xl font-[1000] text-slate-900 tracking-tight leading-none uppercase">Avatar Protocol</h3>
                                <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest mt-3">Immutable identification data.</p>
                            </div>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                            <div className="space-y-3">
                                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Full Identity</label>
                                <div className="relative group">
                                    <input
                                        type="text"
                                        value={settings.name || ''}
                                        disabled
                                        className="w-full px-8 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[24px] text-slate-500 font-bold text-sm cursor-not-allowed"
                                    />
                                    <Lock size={16} className="absolute right-6 top-1/2 -translate-y-1/2 text-slate-200" />
                                </div>
                            </div>
                            <div className="space-y-3">
                                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Deployment Role</label>
                                <div className="relative">
                                    <input
                                        type="text"
                                        value={settings.job_title || ''}
                                        disabled
                                        className="w-full px-8 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[24px] text-slate-500 font-bold text-sm cursor-not-allowed"
                                    />
                                    <Shield size={16} className="absolute right-6 top-1/2 -translate-y-1/2 text-slate-200" />
                                </div>
                            </div>
                            <div className="md:col-span-2 space-y-3">
                                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Digital Coordinate (Email)</label>
                                <div className="relative">
                                    <input
                                        type="text"
                                        value={settings.email || ''}
                                        disabled
                                        className="w-full px-8 py-5 bg-slate-50/50 border-2 border-slate-50 rounded-[24px] text-slate-500 font-bold text-sm cursor-not-allowed"
                                    />
                                    <Globe size={16} className="absolute right-6 top-1/2 -translate-y-1/2 text-slate-200" />
                                </div>
                            </div>
                        </div>
                    </div>
                </Section>

                {/* Regional Section */}
                <Section icon={Globe} title="Regional Synchronization" desc="Localization and temporal shift">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                        <div className="space-y-3">
                            <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Linguistics</label>
                            <select
                                value={settings.language}
                                onChange={(e) => handleUpdate('language', e.target.value)}
                                className="w-full px-8 py-5 bg-white border-2 border-slate-50 rounded-[24px] focus:border-indigo-600/20 focus:outline-none text-slate-900 font-bold text-sm shadow-sm transition-all"
                            >
                                <option>English (US)</option>
                                <option>Hindi (HI)</option>
                                <option>Spanish (ES)</option>
                            </select>
                        </div>
                        <div className="space-y-3">
                            <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Temporal Anchor</label>
                            <select
                                value={settings.timezone}
                                onChange={(e) => handleUpdate('timezone', e.target.value)}
                                className="w-full px-8 py-5 bg-white border-2 border-slate-50 rounded-[24px] focus:border-indigo-600/20 focus:outline-none text-slate-900 font-bold text-sm shadow-sm transition-all"
                            >
                                <option value="(GMT+05:30) Mumbai, New Delhi">India (GMT+5:30)</option>
                                <option value="(GMT-08:00) Pacific Time">Pacific Time (GMT-8:00)</option>
                                <option value="(GMT+00:00) London, UTC">London (GMT+0:00)</option>
                            </select>
                        </div>
                    </div>
                </Section>

                {/* Privacy Section */}
                <Section icon={Eye} title="Visibility Protocols" desc="Manage existence in the network">
                    <div className="space-y-2">
                        <Toggle active={settings.show_online_status} label="Active Status Pulse" subtitle="Reveal your presence to the squad matrix" field="show_online_status" />
                        <Toggle active={settings.share_local_time} label="Chronological Shift" subtitle="Synchronize your local time with team members" field="share_local_time" />
                        <Toggle active={true} label="Analytics Telemetry" subtitle="Help improve core mechanics through usage data" field="anonymous_reporting" disabled={true} />
                    </div>
                </Section>

                {/* Notifications Section */}
                <Section icon={Bell} title="Transmission Logic" desc="Coordinate incoming data streams">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                        <div className="space-y-2">
                            <h4 className="text-[10px] font-black text-slate-300 uppercase tracking-[0.4em] mb-6">Email Uplink</h4>
                            <Toggle active={settings.email_tasks} label="Operation Shifts" subtitle="Daily tactical updates" field="email_tasks" />
                            <Toggle active={settings.email_summary} label="Matrix Digest" subtitle="Weekly performance summary" field="email_summary" />
                        </div>
                        <div className="space-y-2">
                            <h4 className="text-[10px] font-black text-slate-300 uppercase tracking-[0.4em] mb-6">Nexus HUD</h4>
                            <Toggle active={settings.push_meetings} label="Sync Alerts" subtitle="Real-time meeting invitations" field="push_meetings" />
                            <Toggle active={settings.push_chat} label="Neural Comms" subtitle="Encrypted push notifications" field="push_chat" />
                        </div>
                    </div>
                    <div className="mt-12 pt-10 border-t border-slate-50">
                        <motion.button
                            whileHover={{ scale: 1.02 }}
                            whileTap={{ scale: 0.98 }}
                            onClick={handleSendTestNotif}
                            disabled={!!testNotifStatus}
                            className="flex items-center gap-4 px-8 py-4 bg-indigo-50 text-indigo-700 rounded-[20px] text-[10px] font-black uppercase tracking-widest hover:bg-indigo-100 transition-all border border-indigo-100"
                        >
                            {testNotifStatus === "Delivered!" ? <CheckCircle2 size={16} /> : <Zap size={16} className="fill-indigo-600" />}
                            {testNotifStatus || "Send Connectivity Test"}
                        </motion.button>
                    </div>
                </Section>

                {/* Security Section */}
                <Section icon={ShieldCheck} title="Neural Guard" desc="Secure your identity via 2FA protocols">
                    <div className="space-y-8">
                        <div className={`p-8 rounded-[40px] border-2 transition-all flex flex-col md:flex-row items-center justify-between gap-8 ${settings.two_factor_enabled ? 'bg-emerald-50 border-emerald-100 text-emerald-900 shadow-xl shadow-emerald-100' : 'bg-slate-900 text-white shadow-2xl'
                            }`}>
                            <div className="flex items-center gap-6">
                                <div className={`w-16 h-16 rounded-2xl flex items-center justify-center shadow-lg ${settings.two_factor_enabled ? 'bg-white text-emerald-600' : 'bg-white/10 text-white'
                                    }`}>
                                    <ShieldCheck size={28} />
                                </div>
                                <div>
                                    <p className="text-xl font-[1000] tracking-tight uppercase leading-none">Double Layer Guard</p>
                                    <p className={`text-[10px] font-black uppercase tracking-widest mt-2 ${settings.two_factor_enabled ? 'text-emerald-700/60' : 'text-white/40'}`}>
                                        {settings.two_factor_enabled ? 'ACTIVE: IDENTITY SHIELDED' : 'Protocol: Offline'}
                                    </p>
                                </div>
                            </div>
                            <button
                                onClick={handleEnable2FA}
                                className={`px-10 py-4 rounded-2xl text-[10px] font-black uppercase tracking-widest transition-all ${settings.two_factor_enabled
                                    ? 'bg-white text-slate-900 shadow-md'
                                    : 'bg-white text-slate-900 hover:bg-emerald-500 hover:text-white hover:shadow-2xl'
                                    }`}
                            >
                                {settings.two_factor_enabled ? 'Terminate 2FA' : 'Initialize 2FA'}
                            </button>
                        </div>

                        <AnimatePresence>
                            {show2FAModal && (
                                <motion.div
                                    initial={{ opacity: 0, height: 0 }}
                                    animate={{ opacity: 1, height: 'auto' }}
                                    exit={{ opacity: 0, height: 0 }}
                                    className="overflow-hidden"
                                >
                                    <div className="p-10 rounded-[40px] border-2 border-indigo-100 bg-indigo-50/30 space-y-8 shadow-inner">
                                        <div className="flex items-center gap-4 text-indigo-700">
                                            <Smartphone size={24} />
                                            <h4 className="text-lg font-[1000] uppercase tracking-tight">Identity Verification</h4>
                                        </div>
                                        <p className="text-[10px] font-black text-indigo-600/60 uppercase tracking-widest leading-relaxed">
                                            Enter the 6-digit pulse code sent to your linked device. <br />(Demo Bypass: <span className="text-indigo-600 font-bold">123456</span>)
                                        </p>
                                        <div className="flex flex-wrap items-center gap-6">
                                            <input
                                                type="text"
                                                maxLength={6}
                                                placeholder="000000"
                                                value={otpCode}
                                                onChange={(e) => setOtpCode(e.target.value)}
                                                className="w-48 px-8 py-5 bg-white border-2 border-indigo-100 rounded-[24px] text-center text-xl font-black tracking-[0.4em] text-indigo-900 focus:border-indigo-600/20 focus:outline-none shadow-sm"
                                            />
                                            <button
                                                onClick={handleVerifyOTP}
                                                disabled={otpCode.length !== 6 || is2FALoading}
                                                className="h-16 px-10 bg-indigo-600 text-white rounded-[24px] text-[10px] font-black uppercase tracking-widest hover:bg-indigo-700 transition-all shadow-xl disabled:bg-indigo-200"
                                            >
                                                {is2FALoading ? <Loader2 size={20} className="animate-spin" /> : "Verify Identity"}
                                            </button>
                                            <button
                                                onClick={() => setShow2FAModal(false)}
                                                className="text-[10px] font-black text-slate-400 uppercase tracking-widest hover:text-slate-900 transition-colors"
                                            >
                                                Cancel
                                            </button>
                                        </div>
                                    </div>
                                </motion.div>
                            )}
                        </AnimatePresence>
                    </div>
                </Section>

            </div>
        </div>
    );
};

export default SettingsPage;
