export type WebSocketMessageType = 'notification' | 'message' | 'task' | 'meeting' | 'file';

export interface WebSocketMessage {
    type: WebSocketMessageType;
    action?: string;
    data: any;
}

type MessageHandler = (message: WebSocketMessage) => void;

class WebSocketService {
    private socket: WebSocket | null = null;
    private handlers: Set<MessageHandler> = new Set();
    private reconnectAttempts = 0;
    private maxReconnectAttempts = 5;
    private reconnectInterval = 3000;
    private token: string | null = null;

    connect(token: string) {
        if (this.socket && this.socket.readyState === WebSocket.OPEN) return;

        this.token = token;
        const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
        // Use the current hostname but port 8000 for the backend api
        const host = `${window.location.hostname}:8000`;
        const url = `${protocol}//${host}/api/v1/ws?token=${token}`;

        this.socket = new WebSocket(url);

        this.socket.onopen = () => {
            console.log('WebSocket Connected');
            this.reconnectAttempts = 0;
        };

        this.socket.onmessage = (event) => {
            try {
                const message: WebSocketMessage = JSON.parse(event.data);
                this.handlers.forEach(handler => handler(message));
            } catch (error) {
                console.error('WebSocket message error:', error);
            }
        };

        this.socket.onclose = () => {
            console.log('WebSocket Disconnected');
            this.attemptReconnect();
        };

        this.socket.onerror = (error) => {
            console.error('WebSocket Error:', error);
        };
    }

    private attemptReconnect() {
        if (this.reconnectAttempts < this.maxReconnectAttempts && this.token) {
            this.reconnectAttempts++;
            console.log(`Attempting reconnect ${this.reconnectAttempts}...`);
            setTimeout(() => this.connect(this.token!), this.reconnectInterval);
        }
    }

    subscribe(handler: MessageHandler) {
        this.handlers.add(handler);
        return () => this.handlers.delete(handler);
    }

    disconnect() {
        this.token = null;
        if (this.socket) {
            this.socket.close();
            this.socket = null;
        }
    }
}

export const webSocketService = new WebSocketService();
