import React, { useState, useEffect } from "react";
import { taskService } from "../../services/taskService";
import { meetingService } from "../../services/meetingService";
import {
  Calendar as CalendarIcon, CheckCircle2, Clock, MapPin,
  Video, AlertCircle, ChevronLeft, ChevronRight, Zap,
  Layers, Activity, Shield, Sparkles
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

interface CalendarEvent {
  id: string | number;
  title: string;
  time?: string;
  date: string;
  type: "meeting" | "task";
  status?: string;
  details?: string;
  isOverdue?: boolean;
}

const CalendarPage: React.FC = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [events, setEvents] = useState<CalendarEvent[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [hoveredDate, setHoveredDate] = useState<string | null>(null);
  const [tooltipPos, setTooltipPos] = useState({ x: 0, y: 0 });

  const monthNames = [
    "January", "February", "March", "April",
    "May", "June", "July", "August",
    "September", "October", "November", "December"
  ];

  const daysOfWeek = ["SUN", "MON", "TUE", "WED", "THU", "FRI", "SAT"];

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();

  const firstDay = new Date(year, month, 1);
  const lastDay = new Date(year, month + 1, 0);
  const startingDay = firstDay.getDay();
  const totalDays = lastDay.getDate();

  const daysArray: number[] = [];
  for (let i = 1; i <= totalDays; i++) daysArray.push(i);

  const today = new Date();
  const todayStr = today.toLocaleDateString("en-CA");

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [tasks, meetings] = await Promise.all([
          taskService.getTasks(),
          meetingService.getAllMeetings()
        ]);

        const taskEvents: CalendarEvent[] = tasks
          .filter(t => t.due_date)
          .map(t => ({
            id: `task-${t.id}`,
            title: t.title,
            date: t.due_date ? new Date(t.due_date).toLocaleDateString("en-CA") : "",
            type: "task",
            status: t.status,
            details: t.priority,
            isOverdue: t.is_overdue
          }));

        const meetingEvents: CalendarEvent[] = meetings.map(m => {
          const start = new Date(m.start_time);
          const end = new Date(m.end_time);
          const timeStr = `${start.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })} – ${end.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}`;

          return {
            id: `meeting-${m.id}`,
            title: m.title,
            time: timeStr,
            date: start.toLocaleDateString("en-CA"),
            type: "meeting",
            details: m.location || "Secure Portal"
          };
        });

        setEvents([...taskEvents, ...meetingEvents]);
      } catch (error) {
        console.error("Failed to fetch calendar data", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const selectedDateStr = selectedDate.toLocaleDateString("en-CA");
  const dayEvents = events.filter((e) => e.date === selectedDateStr);

  const containerVariants = {
    hidden: { opacity: 0 },
    show: {
      opacity: 1,
      transition: { staggerChildren: 0.1 }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    show: { opacity: 1, y: 0 }
  };

  return (
    <div className="min-h-screen bg-[#f8fafc] text-slate-900 p-8 lg:p-12 overflow-hidden relative font-sans">
      {/* Dynamic Background Elements */}
      <div className="fixed top-0 right-0 w-[800px] h-[800px] bg-indigo-50/50 rounded-full blur-[150px] -mr-64 -mt-64 animate-pulse" />
      <div className="fixed bottom-0 left-0 w-[600px] h-[600px] bg-sky-50/50 rounded-full blur-[120px] -ml-48 -mb-48" />

      <div className="max-w-7xl mx-auto relative z-10">

        {/* Elite Header */}
        <div className="flex flex-col lg:flex-row lg:items-end justify-between gap-12 mb-20">
          <div className="space-y-6">
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              className="inline-flex items-center gap-2 px-4 py-2 bg-indigo-50 text-indigo-700 rounded-full text-[10px] font-black uppercase tracking-[0.3em] border border-indigo-100 shadow-sm"
            >
              <Activity size={14} className="fill-indigo-600" /> Temporal Intelligence
            </motion.div>
            <h1 className="text-7xl font-[1000] tracking-tight leading-[0.95] text-slate-900">
              Calendar <span className="gradient-text">Proximity.</span>
            </h1>
            <p className="text-slate-500 font-medium text-xl max-w-xl leading-relaxed">
              Analyze and orchestrate your time-sensitive operations across the monthly matrix.
            </p>
          </div>

          <div className="flex items-center gap-6">
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => {
                setCurrentDate(new Date());
                setSelectedDate(new Date());
              }}
              className="h-16 px-10 rounded-[28px] bg-white border-2 border-slate-100 font-black uppercase tracking-widest text-[10px] text-slate-500 hover:border-indigo-600 hover:text-indigo-600 hover:shadow-xl transition-all shadow-sm"
            >
              Align Today
            </motion.button>
            <div className="flex items-center bg-slate-900 p-2 rounded-[28px] shadow-2xl">
              <button
                onClick={() => setCurrentDate(new Date(year, month - 1, 1))}
                className="p-4 text-white hover:bg-white/10 rounded-full transition-all"
              >
                <ChevronLeft size={24} />
              </button>
              <div className="px-6 min-w-[200px] text-center">
                <h3 className="text-sm font-black text-white uppercase tracking-[0.2em]">{monthNames[month]} {year}</h3>
              </div>
              <button
                onClick={() => setCurrentDate(new Date(year, month + 1, 1))}
                className="p-4 text-white hover:bg-white/10 rounded-full transition-all"
              >
                <ChevronRight size={24} />
              </button>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-12 gap-16 items-start">

          {/* Calendar Grid Container */}
          <motion.div
            initial={{ opacity: 0, scale: 0.98 }}
            animate={{ opacity: 1, scale: 1 }}
            className="lg:col-span-8 bg-white/70 backdrop-blur-xl rounded-[48px] border-2 border-slate-50 p-12 shadow-2xl shadow-indigo-500/5"
          >
            <div className="grid grid-cols-7 text-center font-black text-slate-300 text-[10px] tracking-[0.4em] mb-10 pb-6 border-b border-slate-50">
              {daysOfWeek.map((d, i) => (
                <div key={i}>{d}</div>
              ))}
            </div>

            <div className="grid grid-cols-7 gap-4">
              {Array(startingDay).fill(null).map((_, i) => (
                <div key={`empty-${i}`} />
              ))}
              {daysArray.map((day) => {
                const d = new Date(year, month, day);
                const dateStr = d.toLocaleDateString("en-CA");
                const isToday = dateStr === todayStr;
                const hasEvent = events.some((e) => e.date === dateStr);
                const isSelected = dateStr === selectedDateStr;

                return (
                  <motion.div
                    key={day}
                    whileHover={{ scale: 1.05, y: -4 }}
                    onClick={() => setSelectedDate(new Date(year, month, day))}
                    onMouseEnter={(e) => {
                      setHoveredDate(dateStr);
                      const rect = e.currentTarget.getBoundingClientRect();
                      setTooltipPos({ x: rect.left + rect.width / 2, y: rect.top });
                    }}
                    onMouseLeave={() => setHoveredDate(null)}
                    className={`relative aspect-square rounded-[24px] cursor-pointer flex flex-col items-center justify-center transition-all duration-300 group ${isToday
                        ? "bg-slate-900 text-white shadow-2xl shadow-slate-400 z-10"
                        : isSelected
                          ? "bg-indigo-600 text-white shadow-xl shadow-indigo-200"
                          : "bg-slate-50/50 hover:bg-white border-2 border-transparent hover:border-indigo-100 hover:shadow-xl"
                      }`}
                  >
                    <span className={`text-lg font-black tracking-tighter ${!isToday && !isSelected && 'text-slate-600 group-hover:text-indigo-600'}`}>{day}</span>
                    {hasEvent && (
                      <div className={`absolute bottom-3 w-1.5 h-1.5 rounded-full ${isToday || isSelected ? 'bg-white' : 'bg-indigo-500 group-hover:bg-indigo-600'} shadow-sm`} />
                    )}
                  </motion.div>
                );
              })}
            </div>

            {/* Premium Tooltip */}
            <AnimatePresence>
              {hoveredDate && (
                <motion.div
                  initial={{ opacity: 0, y: 10, scale: 0.9 }}
                  animate={{ opacity: 1, y: 0, scale: 1 }}
                  exit={{ opacity: 0, y: 10, scale: 0.9 }}
                  style={{
                    position: 'fixed',
                    left: tooltipPos.x,
                    top: tooltipPos.y,
                    transform: 'translate(-50%, -115%)',
                    zIndex: 100,
                    pointerEvents: 'none'
                  }}
                  className="w-72 bg-slate-900 text-white rounded-[32px] shadow-[0_20px_50px_rgba(0,0,0,0.3)] p-8 border border-white/10"
                >
                  <div className="text-[10px] font-black text-white/40 uppercase tracking-[0.3em] mb-4 border-b border-white/10 pb-4">
                    {new Date(hoveredDate).toLocaleDateString('en-US', { month: 'long', day: 'numeric', year: 'numeric' })}
                  </div>

                  {events.filter(e => e.date === hoveredDate).length > 0 ? (
                    <div className="space-y-6">
                      {/* Meetings */}
                      {events.filter(e => e.date === hoveredDate && e.type === 'meeting').length > 0 && (
                        <div className="space-y-3">
                          <h4 className="flex items-center text-[10px] font-black text-indigo-400 uppercase tracking-widest">
                            <Video size={14} className="mr-2" /> Logistics
                          </h4>
                          {events.filter(e => e.date === hoveredDate && e.type === 'meeting').map(m => (
                            <div key={m.id} className="text-xs">
                              <div className="font-bold text-white mb-1 line-clamp-1">{m.title}</div>
                              <div className="text-white/40 font-bold uppercase tracking-tighter">{m.time}</div>
                            </div>
                          ))}
                        </div>
                      )}

                      {/* Tasks */}
                      {events.filter(e => e.date === hoveredDate && e.type === 'task').length > 0 && (
                        <div className="space-y-3">
                          <h4 className="flex items-center text-[10px] font-black text-emerald-400 uppercase tracking-widest">
                            <Zap size={14} className="mr-2" /> Operations
                          </h4>
                          {events.filter(e => e.date === hoveredDate && e.type === 'task' && e.status !== 'Completed').map(t => (
                            <div key={t.id} className="text-xs">
                              <div className="font-bold text-white mb-1 line-clamp-1">{t.title}</div>
                              <div className="flex items-center text-[10px] text-white/40 uppercase font-black">
                                <span className={`w-1.5 h-1.5 rounded-full mr-2 ${t.details === 'high' ? 'bg-rose-500' : 'bg-amber-500'}`} />
                                {t.details} Priority
                              </div>
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  ) : (
                    <div className="text-[10px] text-white/30 uppercase font-black tracking-widest py-2">Zero Activity Detected</div>
                  )}

                  <div className="absolute left-1/2 -bottom-2 -translate-x-1/2 w-4 h-4 bg-slate-900 border-r border-b border-white/10 rotate-45" />
                </motion.div>
              )}
            </AnimatePresence>
          </motion.div>

          {/* Schedule Sidebar */}
          <div className="lg:col-span-4 space-y-12">
            <motion.div
              variants={containerVariants}
              initial="hidden"
              animate="show"
              className="space-y-8"
            >
              <div className="flex items-center justify-between border-b border-slate-100 pb-8">
                <div>
                  <h2 className="text-3xl font-[1000] text-slate-900 tracking-tight leading-none uppercase">Sequence</h2>
                  <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest mt-2">Selected Interface</p>
                </div>
                <div className="text-right">
                  <p className="text-[10px] font-black text-indigo-600 uppercase tracking-widest">{selectedDate.toLocaleDateString('en-US', { weekday: 'long' })}</p>
                  <p className="text-xl font-black text-slate-900 tracking-tighter mt-1">{selectedDate.toLocaleDateString('en-US', { day: 'numeric', month: 'short' })}</p>
                </div>
              </div>

              {loading ? (
                <div className="py-20 flex flex-col items-center">
                  <div className="w-12 h-12 bg-indigo-600 rounded-2xl flex items-center justify-center animate-bounce shadow-xl">
                    <Activity size={24} className="text-white" />
                  </div>
                  <p className="text-[10px] font-black uppercase tracking-widest text-slate-400 mt-6">Parsing Data...</p>
                </div>
              ) : dayEvents.length > 0 ? (
                <div className="space-y-4">
                  {dayEvents.map((event, i) => (
                    <motion.div
                      key={event.id}
                      variants={itemVariants}
                      whileHover={{ scale: 1.02, x: 8 }}
                      className={`p-6 rounded-[32px] border-2 transition-all flex items-center justify-between group ${event.type === 'meeting'
                          ? 'bg-slate-900 text-white border-slate-900 shadow-xl'
                          : 'bg-white border-slate-50 hover:border-indigo-100 shadow-sm'
                        }`}
                    >
                      <div className="flex items-center gap-5">
                        <div className={`w-14 h-14 rounded-2xl flex items-center justify-center shadow-lg transition-transform group-hover:rotate-6 ${event.type === 'meeting' ? 'bg-white/10 text-white' : 'bg-slate-50 text-indigo-600'
                          }`}>
                          {event.type === 'meeting' ? <Video size={20} /> : <Zap size={20} />}
                        </div>
                        <div>
                          <p className={`text-base font-black tracking-tight ${event.type === 'meeting' ? 'text-white' : 'text-slate-800'}`}>{event.title}</p>
                          <div className="flex items-center gap-3 mt-1">
                            <span className={`text-[10px] font-black uppercase tracking-widest ${event.type === 'meeting' ? 'text-indigo-400' : 'text-slate-400'}`}>
                              {event.time || `${event.details} Priority`}
                            </span>
                          </div>
                        </div>
                      </div>

                      {event.isOverdue && (
                        <div className="px-3 py-1 bg-rose-500 text-white rounded-full text-[8px] font-black uppercase tracking-widest">Late</div>
                      )}
                    </motion.div>
                  ))}
                </div>
              ) : (
                <motion.div
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  className="p-16 border-2 border-dashed border-slate-100 rounded-[40px] text-center"
                >
                  <Layers size={40} className="text-slate-200 mx-auto mb-4" />
                  <p className="text-xs font-black text-slate-400 uppercase tracking-widest leading-relaxed">
                    Zero temporal events found for this coordinate.
                  </p>
                </motion.div>
              )}
            </motion.div>

            {/* Matrix Status Card */}
            <div className="bg-gradient-to-br from-indigo-600 to-violet-700 rounded-[40px] p-10 text-white shadow-2xl relative overflow-hidden group">
              <div className="absolute top-0 right-0 p-8 opacity-10 group-hover:scale-110 transition-transform">
                <Sparkles size={100} />
              </div>
              <div className="relative z-10 space-y-6">
                <h4 className="text-[10px] font-black uppercase tracking-[0.4em] text-white/40">Continuum Status</h4>
                <p className="text-2xl font-[1000] leading-tight">Your workflow <br /><span className="underline decoration-white/20 underline-offset-8 decoration-4">Performance Stable.</span></p>
                <div className="pt-4 flex items-center gap-4">
                  <div className="w-12 h-12 rounded-2xl bg-white/10 flex items-center justify-center"><Shield size={20} /> </div>
                  <p className="text-[10px] font-black uppercase tracking-widest text-white/60 leading-relaxed">Identity protected by end-to-end matrix encryption.</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CalendarPage;
