import React, { useState, useEffect } from "react";
import {
    User,
    Bell,
    ShieldCheck,
    Save,
    Loader2,
    CheckCircle2,
    Eye,
    Smartphone,
    Globe,
    Lock,
    Activity,
    Shield,
    Zap,
    Cpu,
    Star,
    ArrowUpRight,
    ChevronRight,
    Search,
    Languages,
    Clock
} from "lucide-react";
import { authService } from "../../services/authService";
import { motion, AnimatePresence } from "framer-motion";

const SettingsPage: React.FC = () => {
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [success, setSuccess] = useState(false);
    const [settings, setSettings] = useState<any>(null);
    const [show2FAModal, setShow2FAModal] = useState(false);
    const [otpCode, setOtpCode] = useState("");
    const [is2FALoading, setIs2FALoading] = useState(false);
    const [testNotifStatus, setTestNotifStatus] = useState<string | null>(null);

    useEffect(() => {
        const fetchSettings = async () => {
            try {
                const data = await authService.getCurrentUser();
                setSettings(data);
            } catch (error) {
                console.error("Failed to load settings", error);
            } finally {
                setLoading(false);
            }
        };
        fetchSettings();
    }, []);

    const handleUpdate = (field: string, value: any) => {
        setSettings((prev: any) => ({ ...prev, [field]: value }));
    };

    const handleSave = async () => {
        try {
            setSaving(true);
            setSuccess(false);
            const { id, email, hashed_password, last_seen, status, ...updateData } = settings;
            await authService.updateProfile(updateData);
            setSuccess(true);
            setTimeout(() => setSuccess(false), 3000);
        } catch (error) {
            console.error("Failed to save settings", error);
        } finally {
            setSaving(false);
        }
    };

    const handleSendTestNotif = () => {
        setTestNotifStatus("Transmitting...");
        setTimeout(() => {
            setTestNotifStatus("Delivered");
            setTimeout(() => setTestNotifStatus(null), 3000);
        }, 1500);
    };

    const handleEnable2FA = () => {
        if (settings.two_factor_enabled) {
            handleUpdate('two_factor_enabled', false);
        } else {
            setShow2FAModal(true);
        }
    };

    const handleVerifyOTP = () => {
        setIs2FALoading(true);
        setTimeout(() => {
            if (otpCode === "123456") {
                handleUpdate('two_factor_enabled', true);
                setShow2FAModal(false);
                setOtpCode("");
            } else {
                alert("Invalid OTP code. Please try 123456 for testing.");
            }
            setIs2FALoading(false);
        }, 1000);
    };

    if (loading || !settings) {
        return (
            <div className="min-h-screen flex flex-col items-center justify-center bg-[#fcfdfe]">
                <div className="w-12 h-12 border-4 border-slate-100 border-t-indigo-600 rounded-full animate-spin mb-6" />
                <p className="text-sm font-semibold text-slate-400">Accessing preferences...</p>
            </div>
        );
    }

    const Section = ({ icon: Icon, title, desc, children }: any) => (
        <section className="bg-white rounded-[32px] border border-slate-100 shadow-sm overflow-hidden mb-8 animate-fade-in transition-all">
            <div className="px-8 py-6 border-b border-slate-50 flex items-center justify-between">
                <div className="flex items-center gap-4">
                    <div className="p-3 bg-indigo-50 text-indigo-600 rounded-2xl">
                        <Icon size={20} />
                    </div>
                    <div>
                        <h2 className="text-lg font-bold text-slate-900 tracking-tight leading-none mb-1.5">{title}</h2>
                        <p className="text-xs font-semibold text-slate-400 uppercase tracking-widest">{desc}</p>
                    </div>
                </div>
            </div>
            <div className="p-8">{children}</div>
        </section>
    );

    const Toggle = ({ active, label, subtitle, field, disabled }: any) => (
        <div className={`flex items-center justify-between py-6 first:pt-0 border-b border-slate-50 last:border-0 last:pb-0 ${disabled ? 'opacity-40' : ''}`}>
            <div>
                <p className="text-sm font-bold text-slate-900 leading-none mb-1.5">{label}</p>
                <p className="text-xs font-medium text-slate-400">{subtitle}</p>
            </div>
            <button
                onClick={() => !disabled && handleUpdate(field, !active)}
                disabled={disabled}
                className={`relative inline-flex h-6 w-12 items-center rounded-full transition-all duration-300 shadow-inner ${active ? 'bg-indigo-600' : 'bg-slate-200'}`}
            >
                <span className={`inline-block h-4 w-4 transform rounded-full bg-white shadow-sm transition-transform duration-300 ${active ? 'translate-x-7' : 'translate-x-1'}`} />
            </button>
        </div>
    );

    return (
        <div className="min-h-screen bg-[#fcfdfe] text-slate-900 px-8 py-10 lg:px-12 animate-fade-in relative">
            <div className="max-w-4xl mx-auto">

                {/* Header Area */}
                <div className="flex flex-col md:flex-row md:items-center justify-between gap-6 mb-12">
                    <div>
                        <h1 className="text-4xl font-bold tracking-tight text-slate-900">Settings</h1>
                        <p className="text-slate-500 font-medium mt-1">Manage your account preferences and security options.</p>
                    </div>

                    <button
                        onClick={handleSave}
                        disabled={saving}
                        className={`btn-primary shadow-lg shadow-indigo-100 ${success ? 'bg-emerald-600 hover:bg-emerald-700' : ''}`}
                    >
                        {saving ? (
                            <Loader2 size={18} className="animate-spin" />
                        ) : success ? (
                            <CheckCircle2 size={18} />
                        ) : (
                            <Save size={18} />
                        )}
                        {saving ? "Saving..." : success ? "Settings Saved" : "Save Changes"}
                    </button>
                </div>

                {/* Profile Section (NON-EDITABLE) */}
                <Section icon={User} title="Profile Information" desc="Identity details provided by organization">
                    <div className="space-y-8">
                        <div className="flex items-center gap-6 pb-8 border-b border-slate-50">
                            <div className="w-20 h-20 rounded-[24px] bg-slate-50 border border-slate-100 flex items-center justify-center relative overflow-hidden group shadow-sm">
                                {settings.avatar ? (
                                    <img src={settings.avatar} alt="Avatar" className="w-full h-full object-cover" />
                                ) : (
                                    <span className="text-3xl font-bold text-slate-300">{settings.name?.charAt(0)}</span>
                                )}
                                <div className="absolute inset-0 bg-slate-900/40 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity">
                                    <Lock size={20} className="text-white" />
                                </div>
                            </div>
                            <div className="flex-1">
                                <h3 className="text-sm font-bold text-slate-900 mb-1.5 uppercase tracking-wider">Account Data Policy</h3>
                                <p className="text-xs font-medium text-slate-400 leading-relaxed max-w-sm">Profile information is managed by your organization's directory and cannot be edited locally.</p>
                            </div>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                            <div className="space-y-2">
                                <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Full Name</label>
                                <div className="relative group">
                                    <input
                                        type="text"
                                        value={settings.name || ''}
                                        disabled
                                        className="w-full px-5 py-3 bg-slate-50/50 border border-slate-100 rounded-2xl text-slate-400 font-bold text-sm cursor-not-allowed shadow-inner"
                                    />
                                    <Lock size={14} className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-200" />
                                </div>
                            </div>
                            <div className="space-y-2">
                                <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Job Title</label>
                                <div className="relative group">
                                    <input
                                        type="text"
                                        value={settings.job_title || ''}
                                        disabled
                                        className="w-full px-5 py-3 bg-slate-50/50 border border-slate-100 rounded-2xl text-slate-400 font-bold text-sm cursor-not-allowed shadow-inner"
                                    />
                                    <Shield size={14} className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-200" />
                                </div>
                            </div>
                            <div className="md:col-span-2 space-y-2">
                                <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Email Address</label>
                                <div className="relative group">
                                    <input
                                        type="text"
                                        value={settings.email || ''}
                                        disabled
                                        className="w-full px-5 py-3 bg-slate-50/50 border border-slate-100 rounded-2xl text-slate-400 font-bold text-sm cursor-not-allowed shadow-inner"
                                    />
                                    <Lock size={14} className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-200" />
                                </div>
                            </div>
                        </div>
                    </div>
                </Section>

                {/* Region & Language */}
                <Section icon={Globe} title="Region & Language" desc="Local settings and time coordination">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div className="space-y-2">
                            <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Language</label>
                            <div className="relative">
                                <Languages className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-300" size={16} />
                                <select
                                    value={settings.language}
                                    onChange={(e) => handleUpdate('language', e.target.value)}
                                    className="w-full pl-12 pr-10 py-3 bg-white border border-slate-100 rounded-2xl focus:border-indigo-500 focus:ring-4 focus:ring-indigo-500/5 outline-none text-sm font-bold text-slate-700 transition-all shadow-sm appearance-none cursor-pointer"
                                >
                                    <option>English (US)</option>
                                    <option>Hindi (HI)</option>
                                    <option>Spanish (ES)</option>
                                </select>
                                <ChevronRight className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-300 rotate-90" size={16} />
                            </div>
                        </div>
                        <div className="space-y-2">
                            <label className="text-xs font-bold text-slate-400 uppercase tracking-widest px-1">Time Zone</label>
                            <div className="relative">
                                <Clock className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-300" size={16} />
                                <select
                                    value={settings.timezone}
                                    onChange={(e) => handleUpdate('timezone', e.target.value)}
                                    className="w-full pl-12 pr-10 py-3 bg-white border border-slate-100 rounded-2xl focus:border-indigo-500 focus:ring-4 focus:ring-indigo-500/5 outline-none text-sm font-bold text-slate-700 transition-all shadow-sm appearance-none cursor-pointer"
                                >
                                    <option value="(GMT+05:30) Mumbai, New Delhi">India (GMT+5:30)</option>
                                    <option value="(GMT-08:00) Pacific Time">Pacific Time (GMT-8:00)</option>
                                    <option value="(GMT+00:00) London, UTC">London (GMT+0:00)</option>
                                </select>
                                <ChevronRight className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-300 rotate-90" size={16} />
                            </div>
                        </div>
                    </div>
                </Section>

                {/* Privacy */}
                <Section icon={Eye} title="Privacy" desc="Manage what information others can see">
                    <div className="space-y-2">
                        <Toggle active={settings.show_online_status} label="Show Online Status" subtitle="Allow team members to see when you are active" field="show_online_status" />
                        <Toggle active={settings.share_local_time} label="Share Local Time" subtitle="Help team members know your current working hours" field="share_local_time" />
                    </div>
                </Section>

                {/* Notifications */}
                <Section icon={Bell} title="Notifications" desc="Choose how you want to be notified">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                        <div className="space-y-6">
                            <h4 className="text-xs font-bold text-indigo-600 uppercase tracking-widest mb-4 flex items-center gap-2">
                                <div className="w-1.5 h-1.5 bg-indigo-600 rounded-full" /> Email Notifications
                            </h4>
                            <div className="space-y-2">
                                <Toggle active={settings.email_tasks} label="Task Updates" subtitle="Notifications for task assignments" field="email_tasks" />
                                <Toggle active={settings.email_summary} label="Weekly Report" subtitle="Progress summary of the week" field="email_summary" />
                            </div>
                        </div>
                        <div className="space-y-6">
                            <h4 className="text-xs font-bold text-slate-900 uppercase tracking-widest mb-4 flex items-center gap-2">
                                <div className="w-1.5 h-1.5 bg-slate-900 rounded-full" /> Push Notifications
                            </h4>
                            <div className="space-y-2">
                                <Toggle active={settings.push_meetings} label="Meeting Alerts" subtitle="Reminders before sessions start" field="push_meetings" />
                                <Toggle active={settings.push_chat} label="Direct Messages" subtitle="Alerts for new chat messages" field="push_chat" />
                            </div>
                        </div>
                    </div>
                    <div className="mt-10 pt-8 border-t border-slate-50 flex justify-end">
                        <button
                            onClick={handleSendTestNotif}
                            disabled={!!testNotifStatus}
                            className="flex items-center gap-3 px-6 py-3 bg-slate-50 text-slate-900 text-xs font-bold border border-slate-100 rounded-2xl hover:bg-slate-100 transition-all shadow-sm"
                        >
                            {testNotifStatus === "Delivered" ? <CheckCircle2 size={16} className="text-emerald-500" /> : <Zap size={16} className="text-amber-500" />}
                            {testNotifStatus || "Send Test Notification"}
                        </button>
                    </div>
                </Section>

                {/* Security */}
                <Section icon={ShieldCheck} title="Security & Access" desc="Reinforce your account stabilization">
                    <div className="space-y-6">
                        <div className={`p-8 rounded-[32px] border transition-all flex flex-col md:flex-row md:items-center justify-between gap-6 ${settings.two_factor_enabled
                                ? 'bg-indigo-600 border-indigo-600 text-white shadow-lg shadow-indigo-100'
                                : 'bg-slate-50 border-slate-100'
                            }`}>
                            <div className="flex items-center gap-5">
                                <div className={`w-14 h-14 rounded-2xl flex items-center justify-center shadow-sm ${settings.two_factor_enabled ? 'bg-white/20 text-white' : 'bg-white text-indigo-600 border border-slate-100'
                                    }`}>
                                    <ShieldCheck size={28} />
                                </div>
                                <div>
                                    <p className="text-lg font-bold tracking-tight mb-0.5">Two-Factor Authentication</p>
                                    <p className={`text-xs font-semibold ${settings.two_factor_enabled ? 'text-indigo-100' : 'text-slate-400'}`}>
                                        {settings.two_factor_enabled ? 'Your account is fortified.' : 'Add an extra layer of protection.'}
                                    </p>
                                </div>
                            </div>
                            <button
                                onClick={handleEnable2FA}
                                className={`px-8 py-3.5 text-xs font-bold rounded-2xl transition-all shadow-md ${settings.two_factor_enabled
                                        ? 'bg-white text-indigo-600 hover:bg-slate-50'
                                        : 'bg-indigo-600 text-white hover:bg-indigo-700'
                                    }`}
                            >
                                {settings.two_factor_enabled ? 'Disable 2FA' : 'Enable Security'}
                            </button>
                        </div>

                        <AnimatePresence>
                            {show2FAModal && (
                                <motion.div
                                    initial={{ opacity: 0, y: -10 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    exit={{ opacity: 0, y: -10 }}
                                    className="p-8 bg-slate-50 rounded-[32px] border border-slate-100 space-y-8 shadow-inner"
                                >
                                    <div className="flex items-center gap-3">
                                        <div className="w-8 h-8 rounded-lg bg-indigo-100 text-indigo-600 flex items-center justify-center">
                                            <Smartphone size={16} />
                                        </div>
                                        <h4 className="text-sm font-bold text-slate-900">MOBI Card Validation</h4>
                                    </div>
                                    <p className="text-xs font-semibold text-slate-400 max-w-sm">
                                        Enter the 6-digit verification code from your device. For testing, use code: <span className="text-indigo-600 font-bold">123456</span>
                                    </p>
                                    <div className="flex flex-wrap items-center gap-6">
                                        <input
                                            type="text"
                                            maxLength={6}
                                            placeholder="000000"
                                            value={otpCode}
                                            onChange={(e) => setOtpCode(e.target.value)}
                                            className="w-40 bg-white border border-slate-100 px-5 py-4 text-2xl font-bold tracking-[0.2em] rounded-2xl text-slate-900 focus:border-indigo-500 focus:ring-4 focus:ring-indigo-500/5 outline-none shadow-sm placeholder:text-slate-100"
                                        />
                                        <div className="flex items-center gap-3">
                                            <button
                                                onClick={handleVerifyOTP}
                                                disabled={otpCode.length !== 6 || is2FALoading}
                                                className="px-8 py-4 bg-indigo-600 text-white text-xs font-bold rounded-2xl hover:bg-indigo-700 transition-all disabled:opacity-50 flex items-center gap-3 shadow-lg shadow-indigo-100"
                                            >
                                                {is2FALoading ? <Loader2 size={16} className="animate-spin" /> : <ShieldCheck size={16} />}
                                                Verify Code
                                            </button>
                                            <button
                                                onClick={() => setShow2FAModal(false)}
                                                className="px-6 py-4 text-xs font-bold text-slate-400 hover:text-slate-900 transition-all"
                                            >
                                                Cancel
                                            </button>
                                        </div>
                                    </div>
                                </motion.div>
                            )}
                        </AnimatePresence>
                    </div>
                </Section>

            </div>
        </div>
    );
};

export default SettingsPage;
