import React from "react";
import {
  LayoutDashboard,
  MessageSquare,
  CalendarDays,
  FileUp,
  Workflow,
  ClipboardList,
  LogOut,
  UsersRound,
  X,
  ChevronRight,
  Sparkles,
} from "lucide-react";
import { useNavigate, useLocation } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";

interface SidebarProps {
  isOpen: boolean;
  onClose: () => void;
}

const Sidebar: React.FC<SidebarProps> = ({ isOpen, onClose }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const navItems = [
    { label: "Dashboard", icon: LayoutDashboard, path: "/dashboard" },
    { label: "Chat", icon: MessageSquare, path: "/chat" },
    { label: "Tasks", icon: ClipboardList, path: "/tasks" },
    { label: "Meetings", icon: UsersRound, path: "/meetings" },
    { label: "Calendar", icon: CalendarDays, path: "/calendar" },
    { label: "Files", icon: FileUp, path: "/files" },
    { label: "Workflows", icon: Workflow, path: "/workflows" },
  ];

  return (
    <>
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-white/20 backdrop-blur-md z-40 lg:hidden"
            onClick={onClose}
          />
        )}
      </AnimatePresence>

      <aside
        className={`
          fixed top-0 left-0 z-50 h-screen w-64 
          bg-white/40 backdrop-blur-2xl border-r border-white/60
          transform transition-transform duration-300 ease-in-out
          ${isOpen ? "translate-x-0" : "-translate-x-full"}
          lg:translate-x-0 flex flex-col shadow-2xl shadow-slate-200/50
        `}
      >
        {/* Glass Glow Reflection */}
        <div className="absolute top-0 left-0 w-full h-[300px] bg-gradient-to-b from-white/30 to-transparent pointer-events-none" />

        {/* Brand Section */}
        <div
          className="p-8 pb-10 flex flex-col items-center gap-4 cursor-pointer group relative z-10"
          onClick={() => {
            navigate("/dashboard");
            onClose();
          }}
        >
          <motion.div
            whileHover={{ scale: 1.1, rotate: 5 }}
            className="w-14 h-14 bg-gradient-to-br from-blue-600 to-indigo-600 rounded-2xl flex items-center justify-center shadow-2xl shadow-blue-300 transition-transform border border-white/40"
          >
            <img src="/logo1.png" alt="Logo" className="w-8 h-8 invert brightness-0" />
          </motion.div>
          <div className="text-center">
            <h1 className="text-2xl font-black tracking-tighter text-slate-900 flex items-center gap-2 justify-center">
              Collabryta
              <Sparkles size={16} className="text-blue-500" />
            </h1>
            <span className="text-[10px] font-black uppercase tracking-[0.3em] text-blue-600/60 mt-1 block">Futuristic Hub</span>
          </div>
        </div>

        {/* Navigation */}
        <nav className="flex-1 px-4 space-y-2 overflow-y-auto custom-scrollbar relative z-10">
          {navItems.map(({ label, icon: Icon, path }) => {
            const active = location.pathname === path;
            return (
              <motion.button
                key={path}
                whileHover={{ x: 5 }}
                whileTap={{ scale: 0.98 }}
                onClick={() => {
                  navigate(path);
                  onClose();
                }}
                className={`w-full flex items-center justify-between px-5 py-4 rounded-2xl text-sm font-bold transition-all group relative overflow-hidden
                  ${active
                    ? "text-blue-700 shadow-xl shadow-blue-500/10 border border-white/60"
                    : "text-slate-500 hover:text-slate-900"
                  }`}
              >
                {active && (
                  <motion.div
                    layoutId="glassNav"
                    className="absolute inset-0 bg-white/60 backdrop-blur-md -z-10"
                  />
                )}

                <div className="flex items-center gap-4 relative z-10">
                  <Icon size={18} className={`${active ? "text-blue-600" : "text-slate-400 group-hover:text-blue-500"} transition-colors`} />
                  {label}
                </div>

                <ChevronRight size={14} className={`transition-all ${active ? "text-blue-400" : "opacity-0 group-hover:opacity-40 group-hover:translate-x-1"}`} />
              </motion.button>
            );
          })}
        </nav>

        {/* Footer Action */}
        <div className="p-6 relative z-10">
          <motion.button
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={() => navigate("/login")}
            className="w-full flex items-center justify-center gap-3 py-4 bg-white/60 backdrop-blur-lg text-slate-700 rounded-2xl text-sm font-black transition-all border border-white/80 shadow-lg shadow-slate-200 group"
          >
            <div className="p-2 bg-rose-500/10 rounded-xl group-hover:bg-rose-500/20 transition-colors">
              <LogOut size={18} className="text-rose-600" />
            </div>
            Sign Out
          </motion.button>
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
