import React from "react";
import { Outlet } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import Topbar from "../components/Topbar";
import Footer from "../components/Footer";
import ScrollToTop from "../components/ScrollToTop";

const MainLayout: React.FC = () => {
  return (
    <div className="flex min-h-screen bg-gray-100 text-gray-900">
      {/* üß≠ Sidebar */}
      <aside className="fixed top-0 left-0 w-64 h-full border-r border-gray-200 bg-white shadow-md z-40">
        <Sidebar />
      </aside>

      {/* üìÑ Main Section */}
      <div className="flex flex-col flex-1 ml-64 min-h-screen">
        {/* üîù Topbar */}
        <header className="sticky top-0 z-30 bg-white/80 backdrop-blur-md shadow-sm">
          <Topbar />
        </header>

        {/* üßæ Page Content */}
        <main className="flex-1 overflow-y-auto">
          <div className="p-6">
            <Outlet />
          </div>
        </main>

        {/* ü¶∂ Footer */}
        <footer className="bg-white border-t border-gray-200">
          <Footer />
        </footer>
      </div>
    </div>
  );
};

export default MainLayout;
