import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Calendar as CalendarIcon,
  MessageSquare,
  CheckCircle,
  Clock,
  ArrowRight,
  Activity,
  Zap,
  Star,
  Shield,
  Layers,
  ChevronRight,
  Plus,
  ArrowUpRight
} from "lucide-react";
import { motion } from "framer-motion";
import { taskService, Task } from "../../services/taskService";
import { meetingService } from "../../services/meetingService";
import { messageService, Chat } from "../../services/messageService";
import { userService } from "../../services/userService";
import { authService } from "../../services/authService";


interface DashboardEvent {
  id: string;
  title: string;
  time: string;
  type: "meeting" | "task";
  status?: string;
  details?: string;
  timestamp: Date;
}

const DashboardPage: React.FC = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState<any>(null);
  const [stats, setStats] = useState({
    tasksPending: 0,
    meetingsToday: 0,
    unreadMessages: 0,
    teamMembers: 0,
    tasksCompleted: 0
  });
  const [todaysEvents, setTodaysEvents] = useState<DashboardEvent[]>([]);
  const [recentTasks, setRecentTasks] = useState<Task[]>([]);

  const fetchData = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const [user, tasks, meetings, chats, users] = await Promise.all([
        authService.getCurrentUser(),
        taskService.getTasks(),
        meetingService.getAllMeetings(),
        messageService.getChats(),
        userService.getAllUsers()
      ]);

      setCurrentUser(user);

      const pendingTasks = tasks.filter(t => t.status !== "Completed");
      const completedTasks = tasks.filter(t => t.status === "Completed");
      const unreadCount = chats.reduce((acc: number, curr: Chat) => acc + (curr.unread_count || 0), 0);
      const todayStr = new Date().toLocaleDateString("en-CA");
      const todayMeetings = meetings.filter(m => new Date(m.start_time).toLocaleDateString("en-CA") === todayStr);

      setStats({
        tasksPending: pendingTasks.length,
        meetingsToday: todayMeetings.length,
        unreadMessages: unreadCount,
        teamMembers: users.length,
        tasksCompleted: completedTasks.length
      });

      const taskEvents: DashboardEvent[] = tasks
        .filter(t => t.due_date && new Date(t.due_date).toLocaleDateString("en-CA") === todayStr && t.status !== "Completed")
        .map(t => ({
          id: `task-${t.id}`,
          title: t.title,
          time: "Due Today",
          type: "task",
          status: t.status,
          details: t.priority,
          timestamp: new Date(t.created_at || new Date())
        }));

      const meetingEvents: DashboardEvent[] = todayMeetings.map(m => {
        const startDate = new Date(m.start_time);
        return {
          id: `meeting-${m.id}`,
          title: m.title,
          time: startDate.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', hour12: true }),
          type: "meeting",
          details: m.location || "Virtual HQ",
          timestamp: startDate
        };
      });

      const allEvents = [...taskEvents, ...meetingEvents].sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());
      setTodaysEvents(allEvents);
      setRecentTasks(tasks.slice(0, 5));

    } catch (error) {
      console.error("Failed to load dashboard data", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    // Poll for updates every 30 seconds as a fallback for real-time
    const pollInterval = setInterval(() => {
      fetchData(false);
    }, 30000);

    return () => {
      clearInterval(pollInterval);
    };
  }, []);

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return "MORNING";
    if (hour < 18) return "AFTERNOON";
    return "EVENING";
  };

  if (loading) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center bg-white">
        <div className="w-10 h-10 border border-zinc-200 border-t-black rounded-full animate-spin mb-4" />
        <p className="text-[10px] font-bold tracking-[0.2em] text-zinc-400 uppercase">Synchronizing Systems</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white text-black p-10 lg:p-16 animate-fade-in">
      {/* Header Area */}
      <div className="flex flex-col md:flex-row md:items-end justify-between gap-10 mb-20 border-b border-zinc-100 pb-12">
        <div>
          <p className="text-[10px] font-black tracking-[0.3em] text-zinc-400 mb-2 uppercase">OPERATIONAL OVERVIEW / {getGreeting()}</p>
          <h1 className="text-5xl font-bold tracking-tighter leading-none uppercase">
            {currentUser?.name || 'USER'}
          </h1>
        </div>

        <div className="flex items-center gap-6">
          <button
            onClick={() => navigate('/tasks/add')}
            className="group flex items-center gap-4 px-10 py-4 bg-black text-white hover:bg-zinc-800 transition-all active:scale-95"
          >
            <span className="text-[11px] font-bold tracking-widest uppercase">CREATE TASK</span>
            <Plus size={16} />
          </button>
        </div>
      </div>

      {/* Highlights Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-px bg-zinc-100 border border-zinc-100 mb-20">
        <StatCard
          label="PENDING OBJECTIVES"
          value={stats.tasksPending}
          delta="ACTIVE PROTOCOLS"
          onClick={() => navigate('/tasks')}
        />
        <StatCard
          label="SYNC SESSIONS"
          value={stats.meetingsToday}
          delta="TODAY'S TIMELINE"
          onClick={() => navigate('/meetings')}
        />
        <StatCard
          label="UNREAD INTEL"
          value={stats.unreadMessages}
          delta="INBOUND COMMS"
          onClick={() => navigate('/messages')}
        />
        <StatCard
          label="COMPLETED UNITS"
          value={stats.tasksCompleted}
          delta="TOTAL VELOCITY"
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-16">
        {/* Schedule Section */}
        <div className="lg:col-span-2 space-y-12">
          <div className="flex items-end justify-between">
            <div>
              <h3 className="text-2xl font-bold tracking-tighter uppercase leading-none">TEMPORAL RADIUS</h3>
              <p className="text-[10px] font-bold tracking-widest text-zinc-400 uppercase mt-2">Active Timeline</p>
            </div>
            <button onClick={() => navigate('/calendar')} className="text-[10px] font-black tracking-widest text-zinc-400 hover:text-black uppercase underline underline-offset-8">VIEW FULL ARCHIVE</button>
          </div>

          <div className="border border-zinc-100 divide-y divide-zinc-100">
            {todaysEvents.length > 0 ? (
              todaysEvents.map((event, idx) => (
                <div key={idx} className="p-10 flex items-center gap-10 hover:bg-zinc-50 transition-all group cursor-pointer" onClick={() => navigate(event.type === 'meeting' ? '/meetings' : '/tasks')}>
                  <div className="w-24 shrink-0">
                    <p className="text-[10px] font-black text-zinc-400 tracking-widest uppercase mb-1">{event.type}</p>
                    <p className="text-[11px] font-bold text-black uppercase tracking-widest">{event.time}</p>
                  </div>
                  <div className="flex-1">
                    <h4 className="text-lg font-bold tracking-tight text-black mb-1 uppercase">{event.title}</h4>
                    <p className="text-[10px] font-medium text-zinc-400 tracking-wider uppercase">{event.details}</p>
                  </div>
                  <div className="opacity-0 group-hover:opacity-100 transition-all">
                    <ArrowUpRight size={20} className="text-black" />
                  </div>
                </div>
              ))
            ) : (
              <div className="p-20 text-center">
                <p className="text-[11px] font-black text-zinc-300 tracking-[0.3em] uppercase">TIMELINE CLEAR / NO ACTIVE PROTOCOLS</p>
              </div>
            )}
          </div>
        </div>

        {/* Recent Activity/Tasks */}
        <div className="space-y-12">
          <div className="flex items-end justify-between">
            <div>
              <h3 className="text-2xl font-bold tracking-tighter uppercase leading-none">OBJECTIVE LOG</h3>
              <p className="text-[10px] font-bold tracking-widest text-zinc-400 uppercase mt-2">Resource Flow</p>
            </div>
            <button onClick={() => navigate('/tasks')} className="text-[10px] font-black tracking-widest text-zinc-400 hover:text-black uppercase underline underline-offset-8">ALL LOGS</button>
          </div>

          <div className="space-y-4">
            {recentTasks.length > 0 ? (
              <div className="border border-zinc-100 divide-y divide-zinc-100">
                {recentTasks.map(task => (
                  <div key={task.id} className="p-8 flex items-center justify-between group cursor-pointer hover:bg-zinc-50 transition-all" onClick={() => navigate('/tasks')}>
                    <div>
                      <p className={`text-[11px] font-bold tracking-wider uppercase ${task.status === 'Completed' ? 'text-zinc-300 line-through' : 'text-black'}`}>
                        {task.title}
                      </p>
                      <p className="text-[9px] font-black text-zinc-400 uppercase tracking-widest mt-1.5">{task.priority} / {task.status}</p>
                    </div>
                    <ChevronRight size={14} className="text-zinc-200 group-hover:text-black transition-all" />
                  </div>
                ))}
              </div>
            ) : (
              <p className="text-[10px] text-center text-zinc-300 uppercase tracking-[0.2em] py-10">ARCHIVE EMPTY</p>
            )}

            <div className="pt-8">
              <div className="bg-black p-10 text-white">
                <p className="text-[9px] font-black uppercase tracking-[0.4em] text-zinc-500 mb-4">SYSTEM INTEL / SPRINT ALPHA</p>
                <p className="text-sm font-medium tracking-tight leading-relaxed">
                  OBJECTIVE SATURATION AT 85%. SYNC CONTINUITY IS OPTIMAL ACROSS ALL CHANNELS.
                </p>
                <div className="mt-8 pt-8 border-t border-zinc-800 flex items-center justify-between">
                  <span className="text-[9px] font-black tracking-[0.3em] text-zinc-500">VERSION 1.0.4 CORE</span>
                  <Zap size={14} className="text-zinc-500" />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Sub-components
const StatCard = ({ label, value, delta, onClick }: any) => (
  <div
    onClick={onClick}
    className="bg-white p-10 cursor-pointer hover:bg-zinc-50 transition-all relative group overflow-hidden"
  >
    <div className="relative z-10 flex flex-col h-full">
      <p className="text-[10px] font-black text-zinc-400 tracking-[0.2em] uppercase mb-10">{label}</p>
      <div className="mt-auto flex items-end justify-between">
        <h3 className="text-5xl font-bold tracking-tighter text-black leading-none">{value}</h3>
        <div className="opacity-0 group-hover:opacity-100 transition-all transform translate-y-2 group-hover:translate-y-0">
          <ArrowUpRight size={20} className="text-black" />
        </div>
      </div>
      <p className="text-[9px] font-bold text-zinc-300 tracking-widest uppercase mt-4">{delta}</p>
    </div>
  </div>
);

export default DashboardPage;
