import React, { useState } from "react";

interface Meeting {
  title: string;
  time: string;
  date: string;
}

const CalendarPage: React.FC = () => {
  const [currentDate, setCurrentDate] = useState(new Date());

  const monthNames = [
    "January", "February", "March", "April",
    "May", "June", "July", "August",
    "September", "October", "November", "December"
  ];

  const daysOfWeek = ["S", "M", "T", "W", "T", "F", "S"];

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();

  const firstDay = new Date(year, month, 1);
  const lastDay = new Date(year, month + 1, 0);
  const startingDay = firstDay.getDay();
  const totalDays = lastDay.getDate();

  const daysArray: number[] = [];
  for (let i = 1; i <= totalDays; i++) daysArray.push(i);

  const today = new Date();
  const todayStr = today.toISOString().split("T")[0];

  const meetings: Meeting[] = [
    { title: "Sprint Planning", time: "10:00 AM ‚Äì 11:00 AM", date: todayStr },
    { title: "Design Review", time: "4:00 PM ‚Äì 5:00 PM", date: "2025-10-30" },
  ];

  const selectedDateStr = currentDate.toISOString().split("T")[0];
  const todayMeetings = meetings.filter((m) => m.date === selectedDateStr);

  return (
    <div className="min-h-screen bg-gray-50 text-gray-800 p-10">
      <header className="flex justify-between items-center mb-10">
        <h1 className="text-3xl font-bold text-gray-900">Calendar</h1>
        <button
          onClick={() => setCurrentDate(new Date())}
          className="px-4 py-2 text-sm font-medium bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
        >
          Today
        </button>
      </header>

      {/* Month Navigation */}
      <div className="flex items-center justify-between mb-6">
        <button
          onClick={() => setCurrentDate(new Date(year, month - 1, 1))}
          className="text-gray-500 hover:text-blue-600 text-2xl"
        >
          ‚Äπ
        </button>
        <h3 className="text-xl font-semibold">
          {monthNames[month]} {year}
        </h3>
        <button
          onClick={() => setCurrentDate(new Date(year, month + 1, 1))}
          className="text-gray-500 hover:text-blue-600 text-2xl"
        >
          ‚Ä∫
        </button>
      </div>

      {/* Calendar Grid */}
      <div className="bg-white rounded-2xl border border-gray-200 p-6 mb-10 shadow-sm">
        <div className="grid grid-cols-7 text-center font-semibold text-gray-500 mb-3">
          {daysOfWeek.map((d) => (
            <div key={d}>{d}</div>
          ))}
        </div>

        <div className="grid grid-cols-7 text-center gap-y-2">
          {Array(startingDay)
            .fill(null)
            .map((_, i) => (
              <div key={`empty-${i}`} />
            ))}
          {daysArray.map((day) => {
            const dateStr = new Date(year, month, day).toISOString().split("T")[0];
            const isToday = dateStr === todayStr;
            const hasMeeting = meetings.some((m) => m.date === dateStr);

            return (
              <div
                key={day}
                onClick={() => setCurrentDate(new Date(year, month, day))}
                className={`py-3 rounded-lg cursor-pointer text-sm transition ${
                  isToday
                    ? "bg-blue-600 text-white font-semibold"
                    : dateStr === selectedDateStr
                    ? "bg-blue-50 text-blue-600 font-medium border border-blue-200"
                    : "hover:bg-gray-100"
                }`}
              >
                <div>{day}</div>
                {hasMeeting && <div className="w-2 h-2 mx-auto mt-1 bg-blue-500 rounded-full"></div>}
              </div>
            );
          })}
        </div>
      </div>

      {/* Meeting List */}
      <div className="bg-white border border-gray-200 rounded-2xl shadow-sm p-6">
        <h2 className="text-xl font-semibold mb-4">
          Meetings on{" "}
          <span className="text-blue-600">
            {currentDate.toDateString()}
          </span>
        </h2>

        {todayMeetings.length > 0 ? (
          <ul className="space-y-3">
            {todayMeetings.map((meeting, i) => (
              <li
                key={i}
                className="flex items-center justify-between p-4 bg-gray-50 rounded-lg border border-gray-100 hover:shadow-sm transition"
              >
                <div>
                  <p className="font-medium text-gray-800">{meeting.title}</p>
                  <p className="text-sm text-gray-500">{meeting.time}</p>
                </div>
                <span className="text-blue-500 text-sm font-semibold">üìç Zoom</span>
              </li>
            ))}
          </ul>
        ) : (
          <div className="text-gray-500 text-center py-6">
            No meetings on this date.
          </div>
        )}
      </div>
    </div>
  );
};

export default CalendarPage;
