import React, { useEffect, useState, useRef } from "react";
import { Menu, Search, Bell, Sparkles } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { authService } from "../services/authService";
import { User } from "../services/userService";
import { useNotifications } from "../context/NotificationContext";
import { AnimatePresence, motion } from "framer-motion";

interface TopbarProps {
  onMenuClick: () => void;
  isSidebarOpen: boolean;
}

const Topbar: React.FC<TopbarProps> = ({ onMenuClick, isSidebarOpen }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState<User | null>(null);
  const { notifications, unreadCount, markAsRead, markAllAsRead } = useNotifications();
  const [showNotifications, setShowNotifications] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const currentUser = await authService.getCurrentUser();
        setUser(currentUser);
      } catch (error) {
        console.error("Failed to fetch user", error);
      }
    };
    fetchUser();
  }, []);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setShowNotifications(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const getInitials = (name: string) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase()
      .substring(0, 2);
  };

  return (
    <header className="sticky top-0 z-30 w-full bg-white border-b border-zinc-100 flex items-center justify-between px-10 py-5">
      {/* Search Section */}
      <div className="flex items-center gap-6 flex-1">
        <button
          onClick={onMenuClick}
          className="lg:hidden p-2 text-black hover:bg-zinc-50 transition-all"
        >
          <Menu size={20} />
        </button>

        <div className="hidden sm:flex items-center w-full max-w-sm bg-zinc-50 border border-zinc-100 px-4 py-2.5 focus-within:bg-white focus-within:border-black transition-all duration-200 group">
          <Search size={14} className="text-zinc-400 mr-3 group-focus-within:text-black transition-colors" />
          <input
            type="text"
            placeholder="SEARCH..."
            className="bg-transparent border-none outline-none text-[10px] font-bold tracking-widest w-full text-black placeholder:text-zinc-300 uppercase"
          />
        </div>
      </div>

      {/* Actions Section */}
      <div className="flex items-center gap-8">
        {/* Pro Badge */}
        <div className="hidden md:flex items-center gap-2 px-3 py-1 bg-black text-white text-[9px] font-black uppercase tracking-[0.2em]">
          <Sparkles size={10} />
          Premium
        </div>

        {/* Notifications */}
        <div className="relative" ref={dropdownRef}>
          <button
            onClick={() => setShowNotifications(!showNotifications)}
            className={`relative p-2 transition-all duration-200 ${showNotifications ? "text-black" : "text-zinc-400 hover:text-black"}`}
          >
            <Bell size={20} />
            {unreadCount > 0 && (
              <span className="absolute top-1 right-1 w-2 h-2 bg-black rounded-full" />
            )}
          </button>

          <AnimatePresence>
            {showNotifications && (
              <motion.div
                initial={{ opacity: 0, y: 5 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: 5 }}
                className="absolute right-0 mt-6 w-80 bg-white border border-zinc-100 shadow-2xl z-50 overflow-hidden"
              >
                <div className="p-6 border-b border-zinc-50 flex justify-between items-center bg-zinc-50/50">
                  <h3 className="text-[10px] font-black uppercase tracking-[0.2em] text-black">Notifications</h3>
                  {unreadCount > 0 && (
                    <button
                      onClick={() => markAllAsRead()}
                      className="text-[9px] font-black uppercase tracking-[0.2em] text-zinc-400 hover:text-black underline underline-offset-4"
                    >
                      CLEAR
                    </button>
                  )}
                </div>
                <div className="max-h-[400px] overflow-y-auto">
                  {notifications.length > 0 ? (
                    notifications.map((notif) => (
                      <div
                        key={notif.id}
                        onClick={() => markAsRead(notif.id)}
                        className={`p-6 border-b border-zinc-50 cursor-pointer transition-colors flex gap-4 items-start ${!notif.is_read ? 'bg-zinc-50/30' : 'hover:bg-zinc-50'}`}
                      >
                        <div className={`w-1.5 h-1.5 mt-1.5 shrink-0 ${!notif.is_read ? 'bg-black' : 'bg-transparent'}`} />
                        <div className="flex-1 min-w-0">
                          <p className="text-[11px] font-bold text-black leading-tight mb-1">{notif.title}</p>
                          <p className="text-[10px] text-zinc-500 font-medium leading-relaxed line-clamp-2">{notif.description}</p>
                          <p className="text-[9px] font-bold text-zinc-300 mt-2 uppercase tracking-widest">{new Date(notif.created_at).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</p>
                        </div>
                      </div>
                    ))
                  ) : (
                    <div className="py-20 text-center">
                      <h4 className="text-[10px] font-black text-black uppercase tracking-widest">No Updates</h4>
                      <p className="text-[10px] text-zinc-400 font-medium mt-1">All protocols executed.</p>
                    </div>
                  )}
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>

        {/* Profile */}
        <button
          onClick={() => navigate("/profile")}
          className="flex items-center gap-4 transition-all duration-200 group border-l border-zinc-100 pl-8 ml-2"
        >
          <div className="w-8 h-8 bg-zinc-100 flex items-center justify-center text-black font-black text-[10px] tracking-tighter group-hover:bg-black group-hover:text-white transition-all">
            {user?.name ? getInitials(user.name) : "U"}
          </div>
          <div className="hidden lg:block text-left">
            <p className="text-[10px] font-black text-black uppercase tracking-[0.15em] leading-none mb-1">
              {user?.name || "USER"}
            </p>
            <p className="text-[9px] font-bold text-zinc-400 uppercase tracking-widest">{user?.role || "MEMBER"}</p>
          </div>
        </button>
      </div>
    </header>
  );
};

export default Topbar;
