import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  Calendar,
  Clock,
  Plus,
  Trash2,
  ExternalLink,
  Users,
  Video,
  History,
  AlertCircle,
  MapPin,
  ChevronRight,
  ArrowRight,
  Layers,
  Zap,
  Shield,
  Star,
  Activity,
  ArrowUpRight
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { meetingService, Meeting } from "../../services/meetingService";


const MeetingsPage: React.FC = () => {
  const navigate = useNavigate();
  const [meetings, setMeetings] = useState<Meeting[]>([]);
  const [loading, setLoading] = useState(true);
  const [currentTime, setCurrentTime] = useState(new Date());

  const fetchMeetings = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const data = await meetingService.getAllMeetings();
      setMeetings(data);
    } catch (error) {
      console.error("Failed to fetch meetings", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    fetchMeetings();
    const pollInterval = setInterval(() => {
      fetchMeetings(false);
    }, 60000); // Poll every 60 seconds for meetings

    return () => {
      clearInterval(pollInterval);
    };
  }, []);

  const handleDelete = async (id: number) => {
    if (window.confirm("ARE YOU SURE?")) {
      try {
        await meetingService.deleteMeeting(id);
        setMeetings(prev => prev.filter(m => m.id !== id));
      } catch (error) {
        console.error("Failed to delete meeting", error);
      }
    }
  };

  const getStatus = (start: string, end: string) => {
    const now = currentTime;
    const startTime = new Date(start);
    const endTime = new Date(end);
    if (now >= startTime && now <= endTime) return 'present';
    if (now > endTime) return 'past';
    return 'upcoming';
  };

  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    });
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString([], { weekday: 'long', month: 'long', day: 'numeric' });
  };

  const isToday = (dateString: string) => {
    const d = new Date(dateString);
    const today = new Date();
    return d.getFullYear() === today.getFullYear() &&
      d.getMonth() === today.getMonth() &&
      d.getDate() === today.getDate();
  };

  const presentMeetings = meetings.filter(m => getStatus(m.start_time, m.end_time) === 'present');
  const upcomingMeetings = meetings.filter(m => {
    return getStatus(m.start_time, m.end_time) === 'upcoming' && isToday(m.start_time);
  }).sort((a, b) => new Date(a.start_time).getTime() - new Date(b.start_time).getTime());

  const pastMeetings = meetings.filter(m => {
    if (getStatus(m.start_time, m.end_time) !== 'past') return false;
    const endTime = new Date(m.end_time);
    const fortyEightHoursAgo = new Date(currentTime.getTime() - (48 * 60 * 60 * 1000));
    return endTime > fortyEightHoursAgo;
  }).sort((a, b) => new Date(b.start_time).getTime() - new Date(a.start_time).getTime());

  const currentOrNext = presentMeetings.length > 0 ? presentMeetings[0] : (upcomingMeetings.length > 0 ? upcomingMeetings[0] : null);

  return (
    <div className="min-h-screen bg-white text-black p-10 lg:p-16 animate-fade-in relative font-sans">
      <div className="max-w-[1200px] mx-auto">

        {/* Header Area */}
        <div className="flex flex-col md:flex-row md:items-end justify-between gap-10 mb-20 border-b border-zinc-100 pb-12">
          <div>
            <p className="text-[10px] font-black tracking-[0.3em] text-zinc-400 mb-2 uppercase">SYNC / COORDINATION</p>
            <h1 className="text-5xl font-bold tracking-tighter leading-none uppercase">MEETINGS</h1>
          </div>

          <div className="flex items-center gap-6">
            <button
              onClick={() => navigate("/calendar")}
              className="px-10 py-4 bg-white border border-black text-black text-[11px] font-black tracking-widest uppercase hover:bg-zinc-50 transition-all active:scale-95"
            >
              CALENDAR ARCHIVE
            </button>
            <button
              onClick={() => navigate("/meetings/schedule")}
              className="flex items-center gap-4 px-10 py-4 bg-black text-white hover:bg-zinc-800 transition-all active:scale-95"
            >
              <span className="text-[11px] font-black tracking-widest uppercase">SCHEDULE SYNC</span>
              <Plus size={16} />
            </button>
          </div>
        </div>

        {loading ? (
          <div className="py-40 text-center">
            <Loader2 className="w-8 h-8 text-black animate-spin mx-auto mb-4" />
            <p className="text-[10px] font-black tracking-widest text-zinc-400 uppercase">SYNCHRONIZING TIMELINE</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-16">
            {/* Left/Middle Column: Main Feed */}
            <div className="lg:col-span-2 space-y-20">
              {/* Highlight Card */}
              {currentOrNext ? (
                <div className="bg-black text-white p-12 lg:p-16 relative overflow-hidden">
                  <div className="relative z-10">
                    <div className="flex items-center gap-4 mb-10">
                      <div className="px-4 py-1.5 bg-white text-black text-[9px] font-black uppercase tracking-[0.2em]">
                        {presentMeetings.length > 0 ? "NOW IN SESSION" : "UP NEXT"}
                      </div>
                      <span className="text-zinc-500 text-[11px] font-bold tracking-widest">{formatTime(currentOrNext.start_time).toUpperCase()}</span>
                    </div>

                    <h2 className="text-5xl font-bold tracking-tighter uppercase mb-8 leading-none">{currentOrNext.title}</h2>

                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-10 mb-12 border-t border-zinc-800 pt-10">
                      <div className="flex items-start gap-4">
                        <Users size={18} className="text-zinc-500 mt-1" />
                        <div>
                          <p className="text-[9px] font-black text-zinc-600 uppercase tracking-widest mb-1">PERSONNEL</p>
                          <p className="text-[11px] font-bold tracking-widest uppercase">{currentOrNext.participants?.length || 0} OPERATIVES</p>
                        </div>
                      </div>
                      <div className="flex items-start gap-4">
                        <MapPin size={18} className="text-zinc-500 mt-1" />
                        <div>
                          <p className="text-[9px] font-black text-zinc-600 uppercase tracking-widest mb-1">COORDINATE</p>
                          <p className="text-[11px] font-bold tracking-widest uppercase">{currentOrNext.location || "VIRTUAL ACCESS"}</p>
                        </div>
                      </div>
                    </div>

                    {currentOrNext.meeting_link && (
                      <button
                        onClick={() => window.open(currentOrNext.meeting_link, '_blank')}
                        className="px-10 py-5 bg-white text-black text-[11px] font-black tracking-[0.2em] uppercase hover:bg-zinc-100 transition-all flex items-center gap-4"
                      >
                        INITIALIZE CONNECTION
                        <ArrowUpRight size={18} />
                      </button>
                    )}
                  </div>
                </div>
              ) : (
                <div className="p-20 text-center border border-zinc-100">
                  <p className="text-[11px] font-black text-zinc-300 tracking-[0.4em] uppercase leading-relaxed">SYSTEM STANDBY / NO ACTIVE TRANSMISSIONS</p>
                </div>
              )}

              {/* Timeline Section */}
              <div className="space-y-12">
                <div className="flex items-end justify-between border-b border-zinc-100 pb-6">
                  <div>
                    <h3 className="text-2xl font-bold tracking-tighter uppercase leading-none">TODAY'S TIMELINE</h3>
                    <p className="text-[10px] font-bold tracking-widest text-zinc-400 uppercase mt-2">Active Sequences</p>
                  </div>
                </div>

                {upcomingMeetings.length > 0 ? (
                  <div className="space-y-px bg-zinc-100 border border-zinc-100">
                    {upcomingMeetings.map((m) => (
                      <div key={m.id} className="bg-white p-10 lg:p-12 flex items-center gap-10 group hover:bg-zinc-50 transition-all cursor-pointer">
                        <div className="w-24 shrink-0">
                          <p className="text-[11px] font-black text-black tracking-widest uppercase">{formatTime(m.start_time)}</p>
                        </div>
                        <div className="flex-1 min-w-0">
                          <h4 className="text-xl font-bold text-black tracking-tight uppercase group-hover:underline underline-offset-4 decoration-1">{m.title}</h4>
                          <p className="text-[10px] text-zinc-400 font-bold uppercase tracking-widest mt-1.5">{m.participants?.length || 0} OPERATIVES / {m.location || "ONLINE"}</p>
                        </div>
                        <div className="flex items-center gap-6 opacity-0 group-hover:opacity-100 transition-all">
                          {m.meeting_link && (
                            <button
                              onClick={() => window.open(m.meeting_link, '_blank')}
                              className="p-3 text-zinc-300 hover:text-black transition-all"
                            >
                              <ExternalLink size={18} />
                            </button>
                          )}
                          <button
                            onClick={() => handleDelete(m.id)}
                            className="p-3 text-zinc-300 hover:text-black transition-all"
                          >
                            <Trash2 size={18} />
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="py-20 text-center bg-zinc-50/50 border border-zinc-100">
                    <p className="text-[11px] font-black text-zinc-300 tracking-[0.3em] uppercase">NO ADDITIONAL SEQUENCES SCHEDULED</p>
                  </div>
                )}
              </div>
            </div>

            {/* Right Column: Side Info */}
            <div className="space-y-20">
              {/* Analytics Summary */}
              <div className="bg-black text-white p-10 lg:p-12">
                <p className="text-[10px] font-black uppercase tracking-[0.3em] text-zinc-500 mb-10">ARCHIVE ANALYTICS</p>
                <div className="grid grid-cols-2 gap-px bg-zinc-800 border border-zinc-800 mb-10">
                  <div className="bg-black p-6">
                    <p className="text-4xl font-bold tracking-tighter">{meetings.length}</p>
                    <p className="text-[9px] font-black text-zinc-600 uppercase tracking-widest mt-2">TOTAL LOGS</p>
                  </div>
                  <div className="bg-black p-6">
                    <p className="text-4xl font-bold tracking-tighter">12H</p>
                    <p className="text-[9px] font-black text-zinc-600 uppercase tracking-widest mt-2">AIRTIME</p>
                  </div>
                </div>
                <div className="pt-10 border-t border-zinc-800">
                  <div className="flex items-center justify-between text-[10px] font-black uppercase tracking-widest mb-4">
                    <span className="text-zinc-500">MONTHLY VELOCITY</span>
                    <span className="text-white">72%</span>
                  </div>
                  <div className="w-full h-1 bg-zinc-800">
                    <div className="w-[72%] h-full bg-white" />
                  </div>
                </div>
              </div>

              {/* History Section */}
              <div className="space-y-12">
                <div className="flex items-end justify-between border-b border-zinc-100 pb-6">
                  <div>
                    <h3 className="text-2xl font-bold tracking-tighter uppercase leading-none">RECENT HISTORY</h3>
                    <p className="text-[10px] font-bold tracking-widest text-zinc-400 uppercase mt-2">Archive Retention</p>
                  </div>
                </div>

                <div className="space-y-px bg-zinc-100 border border-zinc-100">
                  {pastMeetings.slice(0, 5).map(m => (
                    <div key={m.id} className="p-8 bg-white flex items-center justify-between hover:bg-zinc-50 transition-all group cursor-pointer">
                      <div className="flex items-center gap-6">
                        <div className="w-10 h-10 bg-zinc-50 flex items-center justify-center text-zinc-200 group-hover:bg-black group-hover:text-white transition-all">
                          <History size={16} />
                        </div>
                        <div>
                          <h4 className="text-[11px] font-bold text-black uppercase tracking-wider truncate max-w-[150px]">{m.title}</h4>
                          <p className="text-[9px] font-black text-zinc-300 uppercase tracking-[0.1em] mt-1.5">{formatDate(m.start_time).split(',')[0]}</p>
                        </div>
                      </div>
                      <button onClick={(e) => { e.stopPropagation(); handleDelete(m.id); }} className="p-2 text-zinc-100 hover:text-black transition-all">
                        <Trash2 size={16} />
                      </button>
                    </div>
                  ))}
                  {pastMeetings.length === 0 && <p className="text-[10px] text-center text-zinc-200 uppercase tracking-widest py-10 bg-white">NULL ARCHIVE</p>}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

const Loader2 = ({ className }: { className?: string }) => (
  <svg className={className} xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <path d="M21 12a9 9 0 1 1-6.219-8.56" />
  </svg>
);

export default MeetingsPage;
