import React, { useState, useEffect } from "react";

import {
    User,
    Bell,
    ShieldCheck,
    Save,
    Loader2,
    CheckCircle2,
    Eye,
    Smartphone,
    Globe,
    Lock
} from "lucide-react";
import { authService } from "../../services/authService";

const SettingsPage: React.FC = () => {
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [success, setSuccess] = useState(false);
    const [settings, setSettings] = useState<any>(null);
    const [show2FAModal, setShow2FAModal] = useState(false);
    const [otpCode, setOtpCode] = useState("");
    const [is2FALoading, setIs2FALoading] = useState(false);
    const [testNotifStatus, setTestNotifStatus] = useState<string | null>(null);

    const translations: any = {
        "English (US)": {
            title: "Settings",
            subtitle: "Manage your account preferences and site configuration.",
            save: "Save Changes",
            saving: "Saving...",
            saved: "Saved Successfully"
        },
        "Hindi (HI)": {
            title: "सेटिंग्स",
            subtitle: "अपने खाते की प्राथमिकताओं और साइट कॉन्फ़िगरेशन को व्यवस्थित करें।",
            save: "परिवर्तन सहेजें",
            saving: "सहेज रहा है...",
            saved: "सफलतापूर्वक सहेजा गया"
        },
        "Spanish (ES)": {
            title: "Configuración",
            subtitle: "Administre las preferencias de su cuenta y la configuración del sitio.",
            save: "Guardar cambios",
            saving: "Guardando...",
            saved: "Guardado correctamente"
        }
    };

    const t = settings ? (translations[settings.language] || translations["English (US)"]) : translations["English (US)"];

    useEffect(() => {
        const fetchSettings = async () => {
            try {
                const data = await authService.getCurrentUser();
                setSettings(data);
            } catch (error) {
                console.error("Failed to load settings", error);
            } finally {
                setLoading(false);
            }
        };
        fetchSettings();
    }, []);

    const handleUpdate = (field: string, value: any) => {
        setSettings((prev: any) => ({ ...prev, [field]: value }));
    };

    const handleSave = async () => {
        try {
            setSaving(true);
            setSuccess(false);
            const { id, email, hashed_password, last_seen, status, ...updateData } = settings;
            await authService.updateProfile(updateData);
            setSuccess(true);
            setTimeout(() => setSuccess(false), 3000);
        } catch (error) {
            console.error("Failed to save settings", error);
        } finally {
            setSaving(false);
        }
    };

    const handleSendTestNotif = () => {
        setTestNotifStatus("Sending...");
        setTimeout(() => {
            setTestNotifStatus("Sent!");
            setTimeout(() => setTestNotifStatus(null), 3000);
        }, 1500);
    };

    const handleEnable2FA = () => {
        if (settings.two_factor_enabled) {
            handleUpdate('two_factor_enabled', false);
        } else {
            setShow2FAModal(true);
        }
    };

    const handleVerifyOTP = () => {
        setIs2FALoading(true);
        // Simulate OTP verification
        setTimeout(() => {
            if (otpCode === "123456") {
                handleUpdate('two_factor_enabled', true);
                setShow2FAModal(false);
                setOtpCode("");
            } else {
                alert("Invalid OTP! Try 123456");
            }
            setIs2FALoading(false);
        }, 1000);
    };

    if (loading || !settings) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-white">
                <Loader2 className="w-8 h-8 text-indigo-600 animate-spin" />
            </div>
        );
    }

    const Section = ({ icon: Icon, title, desc, children }: any) => (
        <section className="bg-white border border-gray-200 rounded-xl overflow-hidden mb-8 shadow-sm">
            <div className="px-6 py-4 bg-gray-50/50 border-b border-gray-100 flex items-center gap-3">
                <Icon size={18} className="text-gray-500" />
                <div>
                    <h2 className="text-base font-bold text-gray-900">{title}</h2>
                    <p className="text-xs text-gray-500">{desc}</p>
                </div>
            </div>
            <div className="p-6">{children}</div>
        </section>
    );

    const Toggle = ({ active, label, subtitle, field, disabled }: any) => (
        <div className={`flex items-center justify-between py-4 first:pt-0 border-b border-gray-50 last:border-0 last:pb-0 ${disabled ? 'opacity-50' : ''}`}>
            <div>
                <p className="text-sm font-semibold text-gray-700">{label}</p>
                <p className="text-xs text-gray-400">{subtitle}</p>
            </div>
            <button
                onClick={() => !disabled && handleUpdate(field, !active)}
                disabled={disabled}
                className={`relative inline-flex h-5 w-10 items-center rounded-full transition-colors focus:outline-none ${active ? 'bg-indigo-600' : 'bg-gray-200'} ${disabled ? 'cursor-not-allowed' : ''}`}
            >
                <span className={`inline-block h-3 w-3 transform rounded-full bg-white transition-transform ${active ? 'translate-x-6' : 'translate-x-1'}`} />
            </button>
        </div>
    );

    return (
        <div className="max-w-4xl mx-auto p-6 space-y-8 pb-32">
            {/* Standard Header */}
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-4 border-b border-gray-100 pb-6">
                <div>
                    <h1 className="text-2xl font-bold text-gray-900 font-outfit uppercase tracking-tight">{t.title}</h1>
                    <p className="text-sm text-gray-500">{t.subtitle}</p>
                </div>
                <button
                    onClick={handleSave}
                    disabled={saving}
                    className={`flex items-center justify-center gap-2 px-6 py-2.5 rounded-lg font-semibold text-sm transition-all ${success ? 'bg-green-600 text-white' : 'bg-indigo-600 text-white hover:bg-indigo-700 shadow-md'
                        }`}
                >
                    {saving ? <Loader2 size={16} className="animate-spin" /> : success ? <CheckCircle2 size={16} /> : <Save size={16} />}
                    {saving ? t.saving : success ? t.saved : t.save}
                </button>
            </div>

            {/* Profile Section */}
            <Section icon={User} title="Profile Information" desc="Public profile and identity">
                <div className="space-y-6">
                    <div className="flex items-center gap-6">
                        <div className="relative">
                            <div className="w-20 h-20 rounded-full bg-slate-100 border border-slate-200 overflow-hidden flex items-center justify-center">
                                {settings.avatar ? (
                                    <img src={settings.avatar} alt="Avatar" className="w-full h-full object-cover text-xs" />
                                ) : (
                                    <span className="text-2xl font-bold text-slate-400">{settings.name?.charAt(0)}</span>
                                )}
                            </div>
                        </div>
                        <div>
                            <h3 className="text-sm font-semibold text-gray-900">Profile Photo</h3>
                            <p className="text-xs text-gray-500 mt-1">Managed via Profile Page.</p>
                        </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="space-y-1">
                            <label className="text-xs font-semibold text-gray-600">Full Name</label>
                            <input
                                type="text"
                                value={settings.name || ''}
                                disabled
                                className="w-full px-3 py-2 bg-gray-50 border border-gray-200 rounded-md text-gray-500 text-sm cursor-not-allowed"
                            />
                        </div>
                        <div className="space-y-1">
                            <label className="text-xs font-semibold text-gray-600">Job Title</label>
                            <input
                                type="text"
                                value={settings.job_title || ''}
                                disabled
                                className="w-full px-3 py-2 bg-gray-50 border border-gray-200 rounded-md text-gray-500 text-sm cursor-not-allowed"
                            />
                        </div>
                        <div className="space-y-1 md:col-span-2">
                            <label className="text-xs font-semibold text-gray-600">Email Address (Read-only)</label>
                            <div className="relative">
                                <input
                                    type="text"
                                    value={settings.email || ''}
                                    disabled
                                    className="w-full px-3 py-2 bg-gray-50 border border-gray-200 rounded-md text-gray-500 text-sm cursor-not-allowed"
                                />
                                <Lock size={14} className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-300" />
                            </div>
                        </div>
                    </div>
                </div>
            </Section>

            {/* Localization Section */}
            <Section icon={Globe} title="Regional Settings" desc="Language and timezone preferences">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className="space-y-1">
                        <label className="text-xs font-semibold text-gray-600">Language</label>
                        <select
                            value={settings.language}
                            onChange={(e) => handleUpdate('language', e.target.value)}
                            className="w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:ring-1 focus:ring-indigo-500 focus:border-indigo-500 outline-none text-sm transition-all"
                        >
                            <option>English (US)</option>
                            <option>Hindi (HI)</option>
                            <option>Spanish (ES)</option>
                        </select>
                    </div>
                    <div className="space-y-1">
                        <label className="text-xs font-semibold text-gray-600">Timezone</label>
                        <select
                            value={settings.timezone}
                            onChange={(e) => handleUpdate('timezone', e.target.value)}
                            className="w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:ring-1 focus:ring-indigo-500 focus:border-indigo-500 outline-none text-sm transition-all"
                        >
                            <option value="(GMT+05:30) Mumbai, New Delhi">India (GMT+5:30)</option>
                            <option value="(GMT-08:00) Pacific Time">Pacific Time (GMT-8:00)</option>
                            <option value="(GMT+00:00) London, UTC">London (GMT+0:00)</option>
                        </select>
                    </div>
                </div>
            </Section>

            {/* Privacy Section */}
            <Section icon={Eye} title="Privacy" desc="Manage your visibility and data">
                <div className="space-y-1 shadow-inner rounded-lg px-4 bg-gray-50/10 border border-gray-100">
                    <Toggle active={settings.show_online_status} label="Show Online Status" subtitle="Allow others to see when you are active" field="show_online_status" />
                    <Toggle active={settings.share_local_time} label="Share Local Time" subtitle="Display your timezone for better scheduling" field="share_local_time" />
                    <Toggle active={true} label="Anonymous Statistics" subtitle="Help us improve with usage data (Default: Enabled)" field="anonymous_reporting" disabled={true} />
                </div>
            </Section>

            {/* Notifications Section */}
            <Section icon={Bell} title="Notifications" desc="Configure how you receive updates">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                    <div className="space-y-1">
                        <h4 className="text-[10px] font-bold text-gray-400 uppercase tracking-widest mb-3">Email</h4>
                        <Toggle active={settings.email_tasks} label="Task Assignments" subtitle="Daily task updates" field="email_tasks" />
                        <Toggle active={settings.email_summary} label="Weekly Digest" subtitle="Workspace summary" field="email_summary" />
                    </div>
                    <div className="space-y-1">
                        <h4 className="text-[10px] font-bold text-gray-400 uppercase tracking-widest mb-3">Push</h4>
                        <Toggle active={settings.push_meetings} label="Meeting Invites" subtitle="Meeting reminders" field="push_meetings" />
                        <Toggle active={settings.push_chat} label="New Messages" subtitle="Chat notifications" field="push_chat" />
                    </div>
                </div>
                <div className="mt-6 pt-6 border-t border-gray-100">
                    <button
                        onClick={handleSendTestNotif}
                        disabled={!!testNotifStatus}
                        className="flex items-center gap-2 px-4 py-2 bg-blue-50 text-blue-700 rounded-lg text-xs font-bold hover:bg-blue-100 transition-all"
                    >
                        {testNotifStatus === "Sent!" ? <CheckCircle2 size={14} /> : <Bell size={14} />}
                        {testNotifStatus || "Send Test Notification"}
                    </button>
                </div>
            </Section>

            {/* Security Section */}
            <Section icon={ShieldCheck} title="Security" desc="Secure your account with 2FA">
                <div className="space-y-4">
                    <div className="p-4 rounded-lg border border-gray-200 bg-gray-50/50 flex flex-col md:flex-row items-center justify-between gap-4">
                        <div className="flex items-center gap-4">
                            <div className={`p-2 rounded-lg ${settings.two_factor_enabled ? 'bg-green-100 text-green-600' : 'bg-orange-100 text-orange-600'}`}>
                                <ShieldCheck size={20} />
                            </div>
                            <div>
                                <p className="text-sm font-semibold text-gray-900">Two-Factor Authentication</p>
                                <p className="text-xs text-gray-500 mt-0.5">{settings.two_factor_enabled ? 'Active and protecting your account' : 'Enhance your security level'}</p>
                            </div>
                        </div>
                        <button
                            onClick={handleEnable2FA}
                            className={`px-4 py-1.5 rounded-md text-xs font-bold transition-all ${settings.two_factor_enabled
                                ? 'bg-white text-gray-600 border border-gray-300'
                                : 'bg-slate-900 text-white shadow-lg'
                                }`}
                        >
                            {settings.two_factor_enabled ? 'Disable 2FA' : 'Enable 2FA'}
                        </button>
                    </div>

                    {show2FAModal && (
                        <div className="p-6 rounded-xl border border-blue-100 bg-blue-50/30 space-y-4 animate-in fade-in slide-in-from-top-4 shadow-inner">
                            <div className="flex items-center gap-2 text-blue-700">
                                <Smartphone size={18} />
                                <h4 className="text-sm font-bold">2-Step Verification</h4>
                            </div>
                            <p className="text-xs text-gray-600 font-medium">Enter the 6-digit OTP sent to your registered device. (For demo: use <span className="text-blue-600 font-bold">123456</span>)</p>
                            <div className="flex flex-wrap gap-3">
                                <input
                                    type="text"
                                    maxLength={6}
                                    placeholder="000000"
                                    value={otpCode}
                                    onChange={(e) => setOtpCode(e.target.value)}
                                    className="w-32 px-3 py-2 border border-blue-200 rounded-lg text-center text-sm font-bold tracking-[0.5em] focus:ring-2 focus:ring-blue-500 outline-none transition-all shadow-sm"
                                />
                                <button
                                    onClick={handleVerifyOTP}
                                    disabled={otpCode.length !== 6 || is2FALoading}
                                    className="px-6 py-2 bg-blue-600 text-white rounded-lg text-xs font-bold hover:bg-blue-700 transition-all shadow-md disabled:bg-blue-300"
                                >
                                    {is2FALoading ? <Loader2 size={14} className="animate-spin" /> : "Verify & Enable"}
                                </button>
                                <button
                                    onClick={() => setShow2FAModal(false)}
                                    className="px-4 py-2 text-xs font-bold text-gray-500 hover:text-gray-700 transition"
                                >
                                    Cancel
                                </button>
                            </div>
                        </div>
                    )}
                </div>
            </Section>

        </div>
    );
};

export default SettingsPage;
