import api from './api';
import { User } from './userService';

export interface Task {
    id: number;
    title: string;
    description?: string;
    status: string;
    priority: string;
    due_date?: string;
    owner_id: number;
    assignee_id?: number;
    owner?: User;
    assignee?: User;
    // Mapped properties for frontend compatibility
    assigned_to_user?: User;
    is_overdue?: boolean;
}

export const taskService = {
    getTasks: async (filters?: { status?: string }): Promise<Task[]> => {
        const response = await api.get('/tasks/');
        const tasks: Task[] = response.data;

        // Map backend response to frontend requirements
        return tasks.map(t => ({
            ...t,
            assigned_to_user: t.assignee, // Map assignee to assigned_to_user
            is_overdue: t.due_date ? new Date(t.due_date) < new Date() && t.status !== 'Completed' : false
        })).filter(t => !filters?.status || t.status === filters.status);
    },

    getStats: async () => {
        // Mock stats for now
        return {
            total: 10,
            completed: 5,
            pending: 3,
            overdue: 2
        };
    },

    createTask: async (task: { title: string; description?: string; status?: string; priority?: string; due_date?: string; assigned_to?: number; assignee_id?: number }) => {
        // Map frontend assigned_to to backend assignee_id
        const payload = {
            ...task,
            assignee_id: task.assigned_to || task.assignee_id
        };
        const response = await api.post('/tasks/', payload);
        return response.data;
    },

    updateTask: async (id: number, task: Partial<Task>) => {
        const response = await api.put(`/tasks/${id}`, task);
        return response.data;
    },

    deleteTask: async (id: number) => {
        const response = await api.delete(`/tasks/${id}`);
        return response.data;
    }
};
