import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
    ChevronLeft,
    Save,
    Loader2,
    Calendar,
    User,
    AlignLeft,
    Type
} from "lucide-react";
import { taskService } from "../../services/taskService";
import { userService } from "../../services/userService";

const AddTaskPage: React.FC = () => {
    const navigate = useNavigate();
    const { id } = useParams<{ id: string }>();
    const isEditing = Boolean(id);

    const [users, setUsers] = useState<any[]>([]);
    const [submitting, setSubmitting] = useState(false);
    const [loading, setLoading] = useState(isEditing);

    const [formData, setFormData] = useState({
        title: "",
        description: "",
        status: "Upcoming",
        priority: "Normal", // Hidden
        assigned_to: "",
        due_date: new Date().toISOString().split('T')[0]
    });

    useEffect(() => {
        const loadData = async () => {
            try {
                const [usersData, taskData] = await Promise.all([
                    userService.getAllUsers(),
                    isEditing && id ? taskService.getTaskById(Number(id)) : Promise.resolve(null)
                ]);
                setUsers(usersData);
                if (taskData) {
                    setFormData({
                        title: taskData.title,
                        description: taskData.description || "",
                        status: taskData.status === "todo" ? "Upcoming" : taskData.status === "in_progress" ? "In Progress" : taskData.status === "done" ? "Completed" : taskData.status,
                        priority: taskData.priority,
                        assigned_to: taskData.assignee_id ? String(taskData.assignee_id) : "",
                        due_date: taskData.due_date ? taskData.due_date.split("T")[0] : new Date().toISOString().split('T')[0]
                    });
                }
            } catch (e) {
                console.error("Failed to load data", e);
            } finally {
                setLoading(false);
            }
        };
        loadData();
    }, [id, isEditing, navigate]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setSubmitting(true);
        try {
            const payload = {
                ...formData,
                status: formData.status as any,
                priority: formData.priority as any,
                assigned_to: formData.assigned_to ? Number(formData.assigned_to) : undefined
            };

            if (isEditing && id) {
                await taskService.updateTask(Number(id), payload);
            } else {
                await taskService.createTask(payload);
            }
            navigate("/tasks");
        } catch (error) {
            console.error(error);
            setSubmitting(false);
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-slate-50 flex items-center justify-center">
                <Loader2 className="w-8 h-8 text-indigo-600 animate-spin" />
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-slate-50 py-12 px-4 sm:px-6 lg:px-8 font-sans">
            <div className="max-w-3xl mx-auto">

                {/* üîô Header / Breadcrumb */}
                <div className="flex items-center gap-4 mb-8">
                    <button
                        onClick={() => navigate(-1)}
                        className="p-2 bg-white border border-slate-200 rounded-lg text-slate-500 hover:text-slate-800 hover:border-slate-300 transition-all shadow-sm"
                    >
                        <ChevronLeft size={20} />
                    </button>
                    <div>
                        <h1 className="text-2xl font-bold text-slate-900 tracking-tight">
                            {isEditing ? 'Edit Task Details' : 'Create New Task'}
                        </h1>
                        <p className="text-sm text-slate-500 mt-1">
                            {isEditing ? 'Update the information below to modify the task.' : 'Fill out the form below to add a new task to your board.'}
                        </p>
                    </div>
                </div>

                {/* üìù Main Form Card */}
                <form onSubmit={handleSubmit} className="bg-white rounded-xl shadow-lg border border-slate-200 overflow-hidden">

                    <div className="p-8 space-y-8">

                        {/* Section 1: General Info */}
                        <div className="space-y-6">
                            <h2 className="text-sm font-semibold text-slate-900 uppercase tracking-wider border-b border-slate-100 pb-2">
                                General Information
                            </h2>

                            {/* Title (Full Width) */}
                            <div>
                                <label className="block text-sm font-medium text-slate-700 mb-2">
                                    Task Name <span className="text-red-500">*</span>
                                </label>
                                <div className="relative">
                                    <div className="absolute left-3 top-3 text-slate-400">
                                        <Type size={18} />
                                    </div>
                                    <input
                                        type="text"
                                        value={formData.title}
                                        onChange={e => setFormData({ ...formData, title: e.target.value })}
                                        placeholder="e.g., Redesign Homepage"
                                        required
                                        className="w-full pl-10 pr-4 py-2.5 bg-slate-50 border border-slate-200 rounded-lg text-slate-900 placeholder:text-slate-400 focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 outline-none transition-all font-medium"
                                    />
                                </div>
                            </div>

                            {/* Description (Full Width) */}
                            <div>
                                <label className="block text-sm font-medium text-slate-700 mb-2">
                                    Description
                                </label>
                                <div className="relative">
                                    <div className="absolute left-3 top-3 text-slate-400">
                                        <AlignLeft size={18} />
                                    </div>
                                    <textarea
                                        value={formData.description}
                                        onChange={e => setFormData({ ...formData, description: e.target.value })}
                                        placeholder="Describe the task requirements..."
                                        rows={6}
                                        className="w-full pl-10 pr-4 py-3 bg-slate-50 border border-slate-200 rounded-lg text-slate-800 placeholder:text-slate-400 focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 outline-none transition-all resize-none leading-relaxed"
                                    />
                                </div>
                            </div>
                        </div>

                        {/* Section 2: Logistics */}
                        <div className="space-y-6 pt-2">
                            <h2 className="text-sm font-semibold text-slate-900 uppercase tracking-wider border-b border-slate-100 pb-2">
                                Logistics & Schedule
                            </h2>

                            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">

                                {/* Assignee Dropdown */}
                                <div>
                                    <label className="block text-sm font-medium text-slate-700 mb-2">
                                        Assigned To
                                    </label>
                                    <div className="relative">
                                        <div className="absolute left-3 top-2.5 text-slate-400">
                                            <User size={18} />
                                        </div>
                                        <select
                                            value={formData.assigned_to}
                                            onChange={e => setFormData({ ...formData, assigned_to: e.target.value })}
                                            className="w-full pl-10 pr-4 py-2.5 bg-slate-50 border border-slate-200 rounded-lg text-slate-800 focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 outline-none appearance-none cursor-pointer"
                                        >
                                            <option value="">Select a team member...</option>
                                            {users.map(u => (
                                                <option key={u.id} value={u.id}>{u.name || u.email}</option>
                                            ))}
                                        </select>
                                    </div>
                                </div>

                                {/* Due Date Picker */}
                                <div>
                                    <label className="block text-sm font-medium text-slate-700 mb-2">
                                        Due Date
                                    </label>
                                    <div className="relative">
                                        <div className="absolute left-3 top-2.5 text-slate-400">
                                            <Calendar size={18} />
                                        </div>
                                        <input
                                            type="date"
                                            value={formData.due_date}
                                            onChange={e => setFormData({ ...formData, due_date: e.target.value })}
                                            className="w-full pl-10 pr-4 py-2.5 bg-slate-50 border border-slate-200 rounded-lg text-slate-800 focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 outline-none cursor-pointer"
                                        />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* ü¶∂ Form Footer */}
                    <div className="bg-slate-50 px-8 py-5 border-t border-slate-200 flex items-center justify-end gap-3">
                        <button
                            type="button"
                            onClick={() => navigate(-1)}
                            className="px-5 py-2.5 text-sm font-medium text-slate-700 bg-white border border-slate-300 rounded-lg hover:bg-slate-50 focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition-all"
                        >
                            Cancel
                        </button>
                        <button
                            type="submit"
                            disabled={submitting}
                            className="px-6 py-2.5 text-sm font-bold text-white bg-indigo-600 rounded-lg hover:bg-indigo-700 focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 shadow-sm flex items-center gap-2 transition-all disabled:opacity-70 disabled:cursor-not-allowed"
                        >
                            {submitting ? <Loader2 size={16} className="animate-spin" /> : <Save size={16} />}
                            {isEditing ? 'Save Changes' : 'Create Task'}
                        </button>
                    </div>

                </form>
            </div>
        </div>
    );
};

export default AddTaskPage;
