import React, { useState } from "react";
import { Outlet } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import Sidebar from "../components/Sidebar";
import Topbar from "../components/Topbar";
import Footer from "../components/Footer";

const MainLayout: React.FC = () => {
  const [isSidebarOpen, setSidebarOpen] = useState(false);

  return (
    <div className="flex min-h-screen bg-[#020617] text-slate-300 font-sans selection:bg-blue-500/30 selection:text-white">
      {/* ðŸŒŒ Midnight Orbit Background */}
      <div className="fixed inset-0 pointer-events-none overflow-hidden -z-10">
        <div className="absolute top-[-20%] right-[-10%] w-[60%] h-[60%] rounded-full bg-blue-600/10 blur-[150px] animate-pulse" />
        <div className="absolute bottom-[-10%] left-[-5%] w-[50%] h-[50%] rounded-full bg-indigo-600/5 blur-[120px]" />
        <div className="absolute top-[30%] left-[20%] w-[1px] h-[1px] bg-white shadow-[0_0_100px_40px_rgba(255,255,255,0.03)]" />
      </div>

      {/* Sidebar */}
      <Sidebar isOpen={isSidebarOpen} onClose={() => setSidebarOpen(false)} />

      {/* Main Content Area */}
      <div className="flex flex-col flex-1 lg:ml-80 transition-all duration-500">
        <div className="p-4 sm:px-6 lg:px-8">
          <Topbar
            isSidebarOpen={isSidebarOpen}
            onMenuClick={() => setSidebarOpen((p) => !p)}
          />
        </div>

        <main className="flex-1 w-full max-w-[1600px] mx-auto px-4 sm:px-10 lg:px-12 py-6">
          <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.4, ease: "easeOut" }}
          >
            <Outlet />
          </motion.div>
        </main>

        <Footer />
      </div>
    </div>
  );
};

export default MainLayout;
