import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import {
    User,
    Lock,
    Bell,
    ShieldCheck,
    Save,
    Camera,
    Globe,
    Palette,
    Smartphone,
    CreditCard,
    Link as LinkIcon,
    HelpCircle,
    Mail,
    HardDrive,
    Zap,
    Loader2,
    CheckCircle2,
    Share2,
    Eye,
    Github,
    Chrome,
    Database
} from "lucide-react";
import { authService } from "../../services/authService";

const SettingsPage: React.FC = () => {
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [success, setSuccess] = useState(false);
    const [settings, setSettings] = useState<any>(null);

    useEffect(() => {
        const fetchSettings = async () => {
            try {
                const data = await authService.getCurrentUser();
                setSettings(data);
            } catch (error) {
                console.error("Failed to load settings", error);
            } finally {
                setLoading(false);
            }
        };
        fetchSettings();
    }, []);

    const handleUpdate = (field: string, value: any) => {
        setSettings((prev: any) => ({ ...prev, [field]: value }));
    };

    const handleSave = async () => {
        try {
            setSaving(true);
            setSuccess(false);
            const { id, email, hashed_password, last_seen, status, ...updateData } = settings;
            await authService.updateProfile(updateData);
            setSuccess(true);
            setTimeout(() => setSuccess(false), 3000);
        } catch (error) {
            console.error("Failed to save settings", error);
        } finally {
            setSaving(false);
        }
    };

    if (loading || !settings) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-slate-50/50">
                <div className="flex flex-col items-center gap-4">
                    <Loader2 className="w-10 h-10 text-indigo-600 animate-spin" />
                    <p className="text-sm font-medium text-slate-500">Loading your preferences...</p>
                </div>
            </div>
        );
    }

    const Section = ({ icon: Icon, title, desc, children }: any) => (
        <div className="bg-white rounded-3xl border border-slate-100 shadow-sm overflow-hidden mb-8">
            <div className="p-6 border-b border-slate-50 bg-slate-50/30 flex items-center gap-4">
                <div className="p-2.5 bg-indigo-50 text-indigo-600 rounded-2xl"><Icon size={20} /></div>
                <div>
                    <h3 className="text-lg font-black text-slate-900 tracking-tight">{title}</h3>
                    <p className="text-xs font-bold text-slate-400 uppercase tracking-widest">{desc}</p>
                </div>
            </div>
            <div className="p-8">{children}</div>
        </div>
    );

    const Toggle = ({ active, label, field }: any) => (
        <div className="flex items-center justify-between py-4 first:pt-0 last:pb-0">
            <span className="text-sm font-bold text-slate-700">{label}</span>
            <button
                onClick={() => handleUpdate(field, !active)}
                className={`relative inline-flex h-6 w-12 items-center rounded-full transition-colors ${active ? 'bg-indigo-600' : 'bg-slate-200'}`}
            >
                <span className={`inline-block h-4 w-4 transform rounded-full bg-white transition-transform ${active ? 'translate-x-7' : 'translate-x-1'}`} />
            </button>
        </div>
    );

    return (
        <div className="p-6 max-w-4xl mx-auto space-y-8 pb-32">
            {/* Header */}
            <motion.div
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                className="flex flex-col md:flex-row md:items-center justify-between gap-6"
            >
                <div>
                    <h1 className="text-4xl font-black text-slate-900 tracking-tight">System Settings</h1>
                    <p className="text-slate-500 font-medium">Global configuration for your Collabryta workspace.</p>
                </div>
                <button
                    onClick={handleSave}
                    disabled={saving}
                    className={`flex items-center justify-center gap-2 px-8 py-4 rounded-[1.5rem] font-black text-sm transition-all shadow-xl active:scale-95 ${success ? 'bg-green-500 text-white shadow-green-500/20' : 'bg-black text-white hover:bg-indigo-600 shadow-indigo-500/10'
                        }`}
                >
                    {saving ? <Loader2 size={18} className="animate-spin" /> : success ? <CheckCircle2 size={18} /> : <Save size={18} />}
                    {saving ? 'Synchronizing...' : success ? 'System Updated' : 'Push Changes'}
                </button>
            </motion.div>

            {/* Profile Information */}
            <Section icon={User} title="Public Profile" desc="Personal Identity & Role">
                <div className="space-y-8">
                    <div className="flex items-center gap-8">
                        <div className="relative group">
                            <div className="w-24 h-24 rounded-3xl bg-indigo-100 flex items-center justify-center text-indigo-700 text-3xl font-black border-4 border-white shadow-lg overflow-hidden">
                                {settings.avatar ? <img src={settings.avatar} alt="Avatar" className="w-full h-full object-cover" /> : settings.name?.charAt(0)}
                            </div>
                            <button
                                onClick={() => {/* Avatar Upload Logic */ }}
                                className="absolute -bottom-2 -right-2 p-2.5 bg-indigo-600 text-white rounded-xl shadow-lg border-2 border-white hover:scale-110 transition-transform"
                            >
                                <Camera size={14} />
                            </button>
                        </div>
                        <div>
                            <h4 className="font-black text-slate-900">Workspace Avatar</h4>
                            <p className="text-sm text-slate-500 font-medium">This will be displayed on the team board and messages.</p>
                        </div>
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div className="space-y-2">
                            <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-1">Display Name</label>
                            <input
                                type="text"
                                value={settings.name || ''}
                                onChange={(e) => handleUpdate('name', e.target.value)}
                                className="w-full px-5 py-3.5 bg-slate-50 border border-slate-100 rounded-[1.25rem] focus:outline-none focus:ring-2 focus:ring-indigo-500/10 focus:border-indigo-500 font-bold text-slate-800"
                            />
                        </div>
                        <div className="space-y-2">
                            <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-1">Professional Title</label>
                            <input
                                type="text"
                                value={settings.job_title || ''}
                                onChange={(e) => handleUpdate('job_title', e.target.value)}
                                className="w-full px-5 py-3.5 bg-slate-50 border border-slate-100 rounded-[1.25rem] focus:outline-none focus:ring-2 focus:ring-indigo-500/10 focus:border-indigo-500 font-bold text-slate-800"
                            />
                        </div>
                    </div>
                </div>
            </Section>

            {/* Localization */}
            <Section icon={Globe} title="Localization" desc="Regional & Language Alignment">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="space-y-2">
                        <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-1">System Language</label>
                        <select
                            value={settings.language}
                            onChange={(e) => handleUpdate('language', e.target.value)}
                            className="w-full px-5 py-3.5 bg-slate-50 border border-slate-100 rounded-[1.25rem] focus:outline-none focus:ring-2 focus:ring-indigo-500/10 focus:border-indigo-500 font-bold text-slate-800 appearance-none"
                        >
                            <option value="English (US)">English (US)</option>
                            <option value="Hindi (HI)">Hindi (HI)</option>
                            <option value="Spanish (ES)">Spanish (ES)</option>
                        </select>
                    </div>
                    <div className="space-y-2">
                        <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-1">Global Timezone</label>
                        <select
                            value={settings.timezone}
                            onChange={(e) => handleUpdate('timezone', e.target.value)}
                            className="w-full px-5 py-3.5 bg-slate-50 border border-slate-100 rounded-[1.25rem] focus:outline-none focus:ring-2 focus:ring-indigo-500/10 focus:border-indigo-500 font-bold text-slate-800 appearance-none"
                        >
                            <option value="(GMT+05:30) Mumbai, New Delhi">India Standard Time (GMT+5:30)</option>
                            <option value="(GMT-08:00) Pacific Time">Pacific Standard Time (GMT-8:00)</option>
                        </select>
                    </div>
                </div>
            </Section>

            {/* Privacy Section */}
            <Section icon={Eye} title="Privacy & Status" desc="Visibility & Activity Control">
                <div className="divide-y divide-slate-50">
                    <Toggle active={settings.show_online_status} label="Broadcast Online Presence" field="show_online_status" />
                    <Toggle active={settings.share_local_time} label="Share Local Timezone with Team" field="share_local_time" />
                    <Toggle active={settings.anonymous_reporting} label="Anonymous Usage Analytics" field="anonymous_reporting" />
                </div>
            </Section>

            {/* Communications */}
            <Section icon={Bell} title="Communications" desc="Smart Notification Matrix">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-x-12">
                    <div>
                        <h4 className="text-[10px] font-black text-indigo-600 uppercase tracking-widest mb-4">Email Dispatch</h4>
                        <Toggle active={settings.email_tasks} label="New Task Assignments" field="email_tasks" />
                        <Toggle active={settings.email_summary} label="Weekly Workspace Digest" field="email_summary" />
                    </div>
                    <div className="pt-6 md:pt-0">
                        <h4 className="text-[10px] font-black text-indigo-600 uppercase tracking-widest mb-4">Push Notifications</h4>
                        <Toggle active={settings.push_meetings} label="Meeting Reminders" field="push_meetings" />
                        <Toggle active={settings.push_chat} label="Direct Message Alerts" field="push_chat" />
                    </div>
                </div>
            </Section>

            {/* Technical Parameters */}
            <Section icon={HardDrive} title="Storage & Cache" desc="Data Persistence & Cloud Usage">
                <div className="space-y-6">
                    <div className="p-6 bg-slate-50 border border-slate-100 rounded-[1.5rem]">
                        <div className="flex justify-between items-center mb-3">
                            <span className="text-xs font-black text-slate-500 uppercase">Cloud Capacity</span>
                            <span className="text-xs font-black text-indigo-600">42% Consumed</span>
                        </div>
                        <div className="w-full h-3 bg-white rounded-full overflow-hidden border border-slate-100 p-0.5">
                            <div className="w-[42%] h-full bg-indigo-600 rounded-full" />
                        </div>
                    </div>
                    <button className="flex items-center gap-3 px-6 py-3 bg-white border border-slate-200 rounded-2xl text-xs font-black text-slate-600 hover:border-indigo-300 hover:text-indigo-600 transition-all">
                        <Database size={16} /> Execute Local Cache Purge
                    </button>
                </div>
            </Section>

            {/* Neural Beta */}
            <Section icon={Zap} title="Beta Program" desc="Experimental Feature Access">
                <div className="divide-y divide-slate-50">
                    <Toggle active={settings.beta_ai_summaries} label="Neural AI-Powered Project Summaries" field="beta_ai_summaries" />
                    <Toggle active={settings.beta_voice_commands} label="Voice-Activated Workspace Control" field="beta_voice_commands" />
                    <Toggle active={settings.beta_collab_editing} label="Real-time Multi-cursor Collaboration" field="beta_collab_editing" />
                </div>
            </Section>

            {/* Security */}
            <Section icon={ShieldCheck} title="Identity Security" desc="Encryption & Auth Protocols">
                <div className="space-y-6">
                    <div className="flex flex-col md:flex-row items-start md:items-center justify-between p-4 bg-orange-50 border border-orange-100 rounded-2xl gap-4">
                        <div className="flex items-center gap-4">
                            <div className="p-3 bg-white rounded-xl text-orange-600 shadow-sm"><Lock size={20} /></div>
                            <div>
                                <h4 className="text-sm font-black text-orange-950">Enhanced Protection (2FA)</h4>
                                <p className="text-xs font-bold text-orange-700">Currently {settings.two_factor_enabled ? 'Active' : 'Not Configured'}</p>
                            </div>
                        </div>
                        <button
                            onClick={() => handleUpdate('two_factor_enabled', !settings.two_factor_enabled)}
                            className={`px-6 py-2 rounded-xl text-[10px] font-black uppercase tracking-wider transition-all ${settings.two_factor_enabled ? 'bg-white text-orange-700 border border-orange-200' : 'bg-orange-600 text-white shadow-lg shadow-orange-600/20'
                                }`}
                        >
                            {settings.two_factor_enabled ? 'Deactivate' : 'Enable Setup'}
                        </button>
                    </div>
                </div>
            </Section>

            {/* Utility Links */}
            <div className="flex items-center justify-center gap-8 py-10 opacity-40">
                <button className="flex items-center gap-2 text-xs font-black hover:opacity-100 transition-opacity"><HelpCircle size={14} /> Neural Support</button>
                <button className="flex items-center gap-2 text-xs font-black hover:opacity-100 transition-opacity"><CreditCard size={14} /> Subscription</button>
                <button className="flex items-center gap-2 text-xs font-black hover:opacity-100 transition-opacity"><Zap size={14} /> System Status</button>
            </div>
        </div >
    );
};

export default SettingsPage;
