import React, { useState } from "react";
import Topbar from "../components/Topbar";
import { Outlet, useLocation, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { userService } from "../services/userService";
import { authService } from "../services/authService";
import { Home, MessageSquare, CheckSquare, Calendar, Folder, Settings, User } from "lucide-react";

const MainLayout: React.FC = () => {
  const [isSidebarOpen, setSidebarOpen] = useState(false); // Kept for topbar compat
  const location = useLocation();
  const navigate = useNavigate();
  const isMessagesPage = location.pathname.startsWith("/messages");

  React.useEffect(() => {
    if (authService.isAuthenticated()) {
      userService.updateStatus("Online").catch(console.error);
    }
    const interval = setInterval(() => {
      if (authService.isAuthenticated()) {
        userService.updateStatus("Online").catch(console.error);
      }
    }, 30000);
    return () => clearInterval(interval);
  }, []);

  const navItems = [
    { icon: <Home size={24} />, label: "Home", path: "/dashboard" },
    { icon: <CheckSquare size={24} />, label: "Tasks", path: "/tasks" },
    { icon: <MessageSquare size={24} />, label: "Chat", path: "/messages" },
    { icon: <Calendar size={24} />, label: "Cal", path: "/calendar" },
    { icon: <Folder size={24} />, label: "Files", path: "/files" },
    { icon: <Settings size={24} />, label: "Settings", path: "/settings" },
  ];

  return (
    <div className="flex min-h-screen bg-gradient-to-br from-indigo-50/50 via-white to-violet-50/30 font-sans text-slate-900 pb-24">

      {/* Main Content Area */}
      <div className="flex-1 flex flex-col w-full">
        <div className="p-4 sm:p-6 lg:p-8 flex flex-col flex-1 w-full max-w-7xl mx-auto">
          <Topbar
            isSidebarOpen={false}
            onMenuClick={() => { }} // No sidebar toggle needed
          />

          <main className={`flex-1 w-full mt-6 ${isMessagesPage ? 'p-0' : ''}`}>
            <motion.div
              key={location.pathname}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3 }}
              className="h-full"
            >
              <Outlet />
            </motion.div>
          </main>
        </div>
      </div>

      {/* Footer Type Navigation (Dock Style) */}
      <div className="fixed bottom-0 left-0 right-0 z-50 flex justify-center pb-6 pointer-events-none">
        <div className="pointer-events-auto bg-white/80 backdrop-blur-2xl border border-white/50 shadow-[0_8px_32px_rgba(0,0,0,0.12)] rounded-full px-6 py-4 flex items-center gap-2 transform transition-all hover:scale-[1.02]">
          {navItems.map((item) => {
            const isActive = location.pathname.startsWith(item.path);
            return (
              <button
                key={item.path}
                onClick={() => navigate(item.path)}
                className={`relative p-3 rounded-full transition-all duration-300 flex flex-col items-center gap-1 min-w-[64px] group ${isActive
                    ? "text-white bg-gradient-to-b from-indigo-500 to-violet-600 shadow-lg shadow-indigo-200 -translate-y-2 scale-110"
                    : "text-slate-400 hover:text-indigo-600 hover:bg-indigo-50"
                  }`}
              >
                {item.icon}
                {isActive && (
                  <motion.div
                    layoutId="activeDot"
                    className="absolute -bottom-2 w-1 h-1 bg-indigo-500 rounded-full"
                  />
                )}
                <span className={`text-[10px] font-bold ${isActive ? 'opacity-100' : 'opacity-0 group-hover:opacity-100'} transition-opacity absolute -top-8 bg-slate-900 text-white px-2 py-1 rounded-lg pointer-events-none whitespace-nowrap`}>
                  {item.label}
                </span>
              </button>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default MainLayout;
