import { Bell, Menu, X } from "lucide-react";
import { useNavigate } from "react-router-dom";

interface TopbarProps {
  onMenuClick: () => void;
  isSidebarOpen: boolean;
}

const Topbar: React.FC<TopbarProps> = ({ onMenuClick, isSidebarOpen }) => {
  const navigate = useNavigate();

  return (
    <header className="sticky top-0 z-30 bg-white shadow-sm px-6 py-4 flex justify-between">
      <div className="flex items-center gap-4">
        <button onClick={onMenuClick} className="lg:hidden">
          {isSidebarOpen ? <X /> : <Menu />}
        </button>
        <h1 className="font-semibold">
          Welcome back, <span className="text-blue-600">Ashok ðŸ‘‹</span>
        </h1>
      </div>

      <div className="flex items-center gap-4">
        <Bell />
        <button
          onClick={() => navigate("/profile")}
          className="w-9 h-9 rounded-full bg-blue-100 text-blue-700"
        >
          A
        </button>
      </div>
    </header>
  );
};

export default Topbar;
