import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  FileText,
  FileSpreadsheet,
  Presentation,
  Image as ImageIcon,
  File,
  Video,
  Upload,
  Bell,
  Search,
  MoreHorizontal,
  Download,
  Trash2,
  Clock,
  Zap,
  ChevronRight,
  HardDrive
} from "lucide-react";
import { motion } from "framer-motion";

const FilesPage: React.FC = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState("");

  const fileCategories = [
    { name: "Reports", count: 12, icon: <FileText size={20} />, color: "bg-blue-500", text: "text-blue-500" },
    { name: "Spreadsheets", count: 8, icon: <FileSpreadsheet size={20} />, color: "bg-emerald-500", text: "text-emerald-500" },
    { name: "Keynotes", count: 5, icon: <Presentation size={20} />, color: "bg-purple-500", text: "text-purple-500" },
    { name: "Assets", count: 24, icon: <ImageIcon size={20} />, color: "bg-pink-500", text: "text-pink-500" },
    { name: "Hardcopy", count: 10, icon: <File size={20} />, color: "bg-amber-500", text: "text-amber-500" },
    { name: "Motion", count: 4, icon: <Video size={20} />, color: "bg-indigo-500", text: "text-indigo-500" },
  ];

  const [recentFiles] = useState([
    { id: 1, name: "Project_Architecture_V2.pdf", type: "PDF", size: "12.4 MB", date: "2026-01-03T10:00:00Z", category: "Reports" },
    { id: 2, name: "Financial_Forecast_Q1.xlsx", type: "Excel", size: "2.1 MB", date: "2026-01-02T15:30:00Z", category: "Spreadsheets" },
    { id: 3, name: "Brand_Asset_Logo_White.svg", type: "SVG", size: "450 KB", date: "2025-12-28T12:00:00Z", category: "Assets" },
    { id: 4, name: "Team_Sync_Meeting_Dec.mp4", type: "Video", size: "890 MB", date: "2025-12-20T09:15:00Z", category: "Motion" },
  ]);

  // üïí Auto-Status Logic for Files
  const getFileStatus = (dateString: string) => {
    const uploadDate = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.abs(now.getTime() - uploadDate.getTime()) / 36e5;

    if (diffInHours < 24) return { label: "NEW", class: "bg-indigo-500 text-white shadow-lg shadow-indigo-200" };
    if (diffInHours < 168) return { label: "RECENT", class: "bg-slate-900 text-white" };
    return { label: "ARCHIVED", class: "bg-slate-100 text-slate-400" };
  };

  return (
    <div className="min-h-screen bg-[#F8FAFC] p-6 lg:p-12 space-y-12 animate-in fade-in duration-500">

      {/* üíé Premium Storage Header */}
      <div className="max-w-7xl mx-auto flex flex-col md:flex-row md:items-end justify-between gap-8">
        <div className="space-y-2">
          <div className="flex items-center gap-3">
            <div className="w-12 h-12 bg-slate-900 rounded-2xl flex items-center justify-center text-white shadow-2xl">
              <HardDrive size={24} />
            </div>
            <h1 className="text-4xl font-black text-slate-900 tracking-tighter">CLOUD STORAGE</h1>
          </div>
          <p className="text-slate-500 font-medium pl-1">Intelligent file monitoring & resource management.</p>
        </div>

        <div className="flex items-center gap-4">
          <div className="relative group hidden sm:block">
            <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-indigo-500 transition-colors" size={20} />
            <input
              type="text"
              placeholder="Find assets..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="bg-white border border-slate-200 rounded-2xl pl-12 pr-6 py-3 text-slate-700 font-bold focus:ring-4 focus:ring-indigo-100 focus:border-indigo-400 transition-all outline-none w-64 shadow-sm"
            />
          </div>
          <button
            onClick={() => navigate("/files/upload")}
            className="bg-indigo-600 hover:bg-indigo-700 text-white px-8 py-3.5 rounded-2xl font-bold flex items-center gap-2 shadow-2xl shadow-indigo-200 transition-all active:scale-95 group"
          >
            <Upload size={20} className="group-hover:-translate-y-1 transition-transform" />
            <span>Push Data</span>
          </button>
          <button className="relative w-12 h-12 bg-white border border-slate-200 rounded-2xl flex items-center justify-center text-slate-500 hover:text-indigo-600 hover:border-indigo-100 transition-all shadow-sm">
            <Bell size={20} />
            <span className="absolute top-3 right-3 w-2 h-2 bg-rose-500 rounded-full border-2 border-white"></span>
          </button>
        </div>
      </div>

      {/* üìä Capacity Summary */}
      <div className="max-w-7xl mx-auto grid grid-cols-1 md:grid-cols-2 lg:grid-cols-6 gap-4">
        {fileCategories.map((cat, i) => (
          <motion.div
            key={i}
            whileHover={{ y: -5 }}
            className="bg-white p-6 rounded-[32px] border border-slate-100 shadow-sm flex flex-col justify-between h-40 group cursor-pointer"
          >
            <div className="flex items-center justify-between">
              <div className={`${cat.color} w-10 h-10 rounded-2xl flex items-center justify-center text-white shadow-xl shadow-current/20`}>
                {cat.icon}
              </div>
              <MoreHorizontal size={18} className="text-slate-300 group-hover:text-slate-500 transition-colors" />
            </div>
            <div>
              <p className="text-[10px] font-black text-slate-400 uppercase tracking-widest leading-none mb-1">{cat.name}</p>
              <h3 className="text-xl font-black text-slate-800 tracking-tight">{cat.count} Files</h3>
            </div>
          </motion.div>
        ))}
      </div>

      {/* üï∞Ô∏è Smart Intelligent File Registry */}
      <div className="max-w-7xl mx-auto space-y-6">
        <div className="flex items-center justify-between px-2">
          <h2 className="text-xl font-black text-slate-900 tracking-tighter flex items-center gap-2">
            <Zap size={20} className="text-amber-500" /> RECENTLY ACCESSED
          </h2>
          <button className="text-xs font-bold text-indigo-600 hover:underline">View All Registry</button>
        </div>

        <div className="space-y-3">
          {recentFiles.map((file, i) => {
            const status = getFileStatus(file.date);
            return (
              <motion.div
                key={file.id}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: i * 0.1 }}
                className="bg-white p-5 rounded-[28px] border border-slate-100 shadow-sm hover:shadow-2xl hover:shadow-indigo-100/10 hover:border-indigo-100 transition-all flex flex-col md:flex-row items-center gap-6 group cursor-pointer"
              >
                {/* File Type Icon */}
                <div className="w-14 h-14 bg-slate-50 rounded-2xl flex items-center justify-center text-slate-400 group-hover:bg-indigo-50 group-hover:text-indigo-600 transition-all border border-transparent group-hover:border-indigo-100">
                  {file.type === 'PDF' && <FileText size={24} />}
                  {file.type === 'Excel' && <FileSpreadsheet size={24} />}
                  {file.type === 'SVG' && <ImageIcon size={24} />}
                  {file.type === 'Video' && <Video size={24} />}
                </div>

                {/* Content Details */}
                <div className="flex-1 min-w-0 w-full space-y-1">
                  <div className="flex items-center gap-3">
                    <h3 className="text-lg font-black text-slate-800 truncate uppercase tracking-tight leading-none group-hover:text-indigo-600 transition-colors">{file.name}</h3>
                    <span className={`px-2 py-0.5 rounded-lg text-[8px] font-black tracking-widest ${status.class}`}>
                      {status.label}
                    </span>
                  </div>
                  <p className="text-slate-400 text-xs font-medium uppercase tracking-[0.1em]">{file.category} ‚Ä¢ {file.size} ‚Ä¢ <span className="italic">{new Date(file.date).toLocaleDateString()}</span></p>
                </div>

                {/* Metadata Grid */}
                <div className="flex items-center gap-8 px-4">
                  <div className="flex items-center gap-3 bg-slate-50 px-4 py-2 rounded-2xl border border-slate-100/50">
                    <div className="w-8 h-8 rounded-full bg-white flex items-center justify-center text-[10px] font-black text-indigo-600 shadow-sm border border-slate-100">
                      SA
                    </div>
                    <div className="hidden lg:block text-left">
                      <p className="text-[8px] font-black text-slate-400 uppercase tracking-tighter">Owner</p>
                      <p className="text-[10px] font-bold text-slate-700">System Admin</p>
                    </div>
                  </div>

                  <div className="hidden xl:flex items-center gap-3">
                    <div className="w-10 h-10 rounded-2xl bg-indigo-50 flex items-center justify-center text-indigo-500">
                      <Clock size={16} />
                    </div>
                    <div className="text-left">
                      <p className="text-[8px] font-black text-slate-400 uppercase tracking-tighter">Processed</p>
                      <p className="text-[10px] font-bold text-slate-700">{new Date(file.date).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</p>
                    </div>
                  </div>
                </div>

                {/* Actions Wing */}
                <div className="flex items-center gap-2">
                  <button className="w-11 h-11 flex items-center justify-center rounded-2xl text-slate-300 hover:text-indigo-600 hover:bg-indigo-50 transition-all">
                    <Download size={18} />
                  </button>
                  <button className="w-11 h-11 flex items-center justify-center rounded-2xl text-slate-300 hover:text-rose-500 hover:bg-rose-50 transition-all">
                    <Trash2 size={18} />
                  </button>
                  <div className="w-11 h-11 bg-slate-900 border border-slate-800 rounded-2xl flex items-center justify-center text-white group-hover:bg-indigo-600 transition-all shadow-xl shadow-slate-200">
                    <ChevronRight size={18} />
                  </div>
                </div>
              </motion.div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default FilesPage;
