import React, { useState } from "react";
import { Calendar, Clock, FileText, ArrowLeft, CheckCircle2, Link as LinkIcon, Sparkles, Activity, Shield, Zap, Globe, Briefcase, Loader2, Video } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { meetingService } from "../../services/meetingService";

const ScheduleMeetingPage: React.FC = () => {
  const navigate = useNavigate();
  const [meeting, setMeeting] = useState({
    title: "",
    date: "",
    time: "",
    end_time: "",
    notes: "",
    location: "",
    meeting_link: "",
    is_recurring_mon_fri: false
  });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!meeting.title.trim() || !meeting.date || !meeting.time || !meeting.end_time) {
      return;
    }

    setLoading(true);
    try {
      const createMeetingData = (dateStr: string) => ({
        title: meeting.title,
        description: meeting.notes,
        start_time: new Date(`${dateStr}T${meeting.time}:00`).toISOString(),
        end_time: new Date(`${dateStr}T${meeting.end_time}:00`).toISOString(),
        meeting_link: meeting.meeting_link,
        location: meeting.location
      });

      if (meeting.is_recurring_mon_fri) {
        const baseDate = new Date(meeting.date);
        const datesToCreate: string[] = [];
        const day = baseDate.getDay();
        const diff = baseDate.getDate() - day + (day === 0 ? -6 : 1);
        const monday = new Date(baseDate.setDate(diff));

        for (let i = 0; i < 5; i++) {
          const d = new Date(monday);
          d.setDate(monday.getDate() + i);
          datesToCreate.push(d.toISOString().split('T')[0]);
        }
        await Promise.all(datesToCreate.map(d => meetingService.createMeeting(createMeetingData(d))));
      } else {
        await meetingService.createMeeting(createMeetingData(meeting.date));
      }
      navigate("/meetings");
    } catch (error) {
      console.error("Failed to schedule meeting", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-[#fcfdfe] text-slate-900 font-sans">
      <div className="max-w-[800px] mx-auto px-6 py-10 lg:py-12 relative z-10">

        {/* Header */}
        <div className="flex items-center justify-between mb-12">
          <button
            onClick={() => navigate("/meetings")}
            className="flex items-center gap-2 text-slate-400 hover:text-slate-900 font-bold transition-all text-sm"
          >
            <ArrowLeft size={20} />
            Back to Meetings
          </button>
          <div className="flex items-center gap-2 px-4 py-1.5 bg-indigo-50 text-indigo-600 rounded-full text-[11px] font-bold uppercase tracking-wider border border-indigo-100 shadow-sm">
            <Video size={14} />
            Sync Protocol
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-12 gap-12 items-start">
          {/* Left: Info */}
          <div className="lg:col-span-4 space-y-8">
            <div>
              <h1 className="text-4xl font-bold tracking-tight text-slate-900 mb-2">
                Schedule <br />
                <span className="text-indigo-600">Event.</span>
              </h1>
              <p className="text-slate-500 font-medium leading-relaxed italic">
                "Calibrate the temporal coordinates for your next high-impact squad alignment."
              </p>
            </div>

            <div className="space-y-4 pt-6">
              <div className="flex items-start gap-4 p-5 bg-white rounded-2xl border border-slate-100 shadow-sm">
                <div className="p-2 bg-indigo-50 text-indigo-600 rounded-lg shrink-0"><Shield size={16} /></div>
                <div>
                  <p className="text-xs font-bold text-slate-900 mb-0.5">Secure Link</p>
                  <p className="text-[11px] font-medium text-slate-400 leading-tight">Identity verified meeting access.</p>
                </div>
              </div>
              <div className="flex items-start gap-4 p-5 bg-white rounded-2xl border border-slate-100 shadow-sm">
                <div className="p-2 bg-emerald-50 text-emerald-600 rounded-lg shrink-0"><Zap size={16} /></div>
                <div>
                  <p className="text-xs font-bold text-slate-900 mb-0.5">Auto Sync</p>
                  <p className="text-[11px] font-medium text-slate-400 leading-tight">Calendar integration across squad.</p>
                </div>
              </div>
            </div>
          </div>

          {/* Right: Form */}
          <div className="lg:col-span-8">
            <div className="bg-white rounded-[32px] border border-slate-100 p-10 shadow-sm">
              <form onSubmit={handleSubmit} className="space-y-8">
                <div className="space-y-2">
                  <label className="text-[11px] font-bold text-slate-400 uppercase tracking-wider ml-1">Event Title</label>
                  <input
                    type="text"
                    value={meeting.title}
                    onChange={(e) => setMeeting({ ...meeting, title: e.target.value })}
                    placeholder="Sync Mission..."
                    className="w-full px-5 py-3.5 bg-slate-50 border border-slate-100 rounded-2xl text-slate-900 font-semibold text-sm focus:bg-white focus:border-indigo-500 outline-none transition-all placeholder:text-slate-300"
                    required
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div className="space-y-2">
                    <label className="text-[11px] font-bold text-slate-400 uppercase tracking-wider ml-1">Deployment Date</label>
                    <input
                      type="date"
                      value={meeting.date}
                      onChange={(e) => setMeeting({ ...meeting, date: e.target.value })}
                      className="w-full px-5 py-3.5 bg-slate-50 border border-slate-100 rounded-2xl text-slate-900 font-semibold text-sm focus:bg-white focus:border-indigo-500 outline-none transition-all"
                      required
                    />
                  </div>
                  <div className="grid grid-cols-2 gap-3">
                    <div className="space-y-2">
                      <label className="text-[11px] font-bold text-slate-400 uppercase tracking-wider ml-1">Start</label>
                      <input
                        type="time"
                        value={meeting.time}
                        onChange={(e) => setMeeting({ ...meeting, time: e.target.value })}
                        className="w-full px-4 py-3.5 bg-slate-50 border border-slate-100 rounded-2xl text-slate-900 font-semibold text-sm focus:bg-white focus:border-indigo-500 outline-none transition-all"
                        required
                      />
                    </div>
                    <div className="space-y-2">
                      <label className="text-[11px] font-bold text-slate-400 uppercase tracking-wider ml-1">End</label>
                      <input
                        type="time"
                        value={meeting.end_time}
                        onChange={(e) => setMeeting({ ...meeting, end_time: e.target.value })}
                        className="w-full px-4 py-3.5 bg-slate-50 border border-slate-100 rounded-2xl text-slate-900 font-semibold text-sm focus:bg-white focus:border-indigo-500 outline-none transition-all"
                        required
                      />
                    </div>
                  </div>
                </div>

                <div
                  className={`p-5 rounded-2xl border transition-all cursor-pointer flex items-center justify-between gap-4 ${meeting.is_recurring_mon_fri ? 'bg-indigo-50 border-indigo-100 shadow-sm' : 'bg-slate-50 border-slate-50'}`}
                  onClick={() => setMeeting({ ...meeting, is_recurring_mon_fri: !meeting.is_recurring_mon_fri })}
                >
                  <div className="flex items-center gap-4">
                    <div className={`w-10 h-10 rounded-xl flex items-center justify-center transition-all ${meeting.is_recurring_mon_fri ? 'bg-indigo-600 text-white' : 'bg-white border border-slate-100 text-slate-300'}`}>
                      <Activity size={18} />
                    </div>
                    <div>
                      <p className="text-sm font-bold text-slate-900">Weekly Pulse Loop</p>
                      <p className="text-[11px] font-semibold text-slate-400 uppercase tracking-wider">Mon - Fri Sync</p>
                    </div>
                  </div>
                  <div className={`w-10 h-5 rounded-full relative transition-all ${meeting.is_recurring_mon_fri ? 'bg-indigo-600' : 'bg-slate-200'}`}>
                    <div className={`absolute top-1 w-3 h-3 bg-white rounded-full transition-all ${meeting.is_recurring_mon_fri ? 'left-6' : 'left-1'}`} />
                  </div>
                </div>

                <div className="space-y-2">
                  <label className="text-[11px] font-bold text-slate-400 uppercase tracking-wider ml-1">Neural Entry (Link)</label>
                  <div className="relative">
                    <LinkIcon className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-300" size={16} />
                    <input
                      type="url"
                      value={meeting.meeting_link}
                      onChange={(e) => setMeeting({ ...meeting, meeting_link: e.target.value })}
                      placeholder="https://vc.nexus/stream/..."
                      className="w-full px-11 py-3.5 bg-slate-50 border border-slate-100 rounded-2xl text-slate-900 font-semibold text-sm focus:bg-white focus:border-indigo-500 outline-none transition-all placeholder:text-slate-300"
                    />
                  </div>
                </div>

                <div className="space-y-2">
                  <label className="text-[11px] font-bold text-slate-400 uppercase tracking-wider ml-1">Mission Brief (Notes)</label>
                  <textarea
                    value={meeting.notes}
                    onChange={(e) => setMeeting({ ...meeting, notes: e.target.value })}
                    rows={4}
                    placeholder="Outline the operational goals..."
                    className="w-full px-5 py-3.5 bg-slate-50 border border-slate-100 rounded-2xl text-slate-900 font-semibold text-sm focus:bg-white focus:border-indigo-500 outline-none transition-all placeholder:text-slate-300 resize-none"
                  />
                </div>

                <div className="pt-6 flex items-center justify-end gap-8 border-t border-slate-50">
                  <button
                    type="button"
                    onClick={() => navigate("/meetings")}
                    className="text-xs font-bold text-slate-400 hover:text-slate-600 transition-colors uppercase tracking-widest"
                  >
                    Abort
                  </button>
                  <button
                    type="submit"
                    disabled={loading}
                    className="flex items-center gap-2 px-8 py-3.5 bg-indigo-600 hover:bg-indigo-700 text-white rounded-2xl font-bold text-sm shadow-xl shadow-indigo-100 transition-all active:scale-[0.98]"
                  >
                    {loading ? <Loader2 size={18} className="animate-spin" /> : <CheckCircle2 size={18} />}
                    Commit Sync
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ScheduleMeetingPage;
