import React from "react";
import {
  BrowserRouter,
  Routes,
  Route,
  Navigate,
  useLocation,
} from "react-router-dom";
import { AnimatePresence } from "framer-motion";

import MainLayout from "./layouts/MainLayout";
import ScrollToTop from "./components/ScrollToTop";

// Pages
import AuthPage from "./pages/Auth/AuthPage";
import RegisterPage from "./pages/Auth/RegisterPage";
import ProfilePage from "./pages/Auth/ProfilePage";
import DashboardPage from "./pages/Dashboard/DashboardPage";
import NotificationPage from "./pages/Notifications/NotificationPage";
import MessagesPage from "./pages/Messages/MessagesPage";  // Renamed from ChatPage
import TasksPage from "./pages/Tasks/TasksPage";
import AddTaskPage from "./pages/Tasks/AddTaskPage";
import MeetingsPage from "./pages/Meetings/MeetingsPage";
import ScheduleMeetingPage from "./pages/Meetings/ScheduleMeetingPage";
import CalendarPage from "./pages/Calendar/CalendarPage";
import FilesPage from "./pages/Files/FilesPage";
import UploadFilePage from "./pages/Files/UploadFilePage";
import TeamPage from "./pages/Team/TeamPage";
import SettingsPage from "./pages/Settings/SettingsPage";
import AboutPage from "./pages/footerpages/AboutPage";
import FeaturesPage from "./pages/footerpages/FeaturesPage";
import DocsPage from "./pages/footerpages/DocsPage";
import SupportPage from "./pages/footerpages/SupportPage";
import CareersPage from "./pages/footerpages/CareersPage";
import BlogPage from "./pages/footerpages/BlogPage";
import PressKitPage from "./pages/footerpages/PressKitPage";
import ContactPage from "./pages/footerpages/ContactPage";
import HelpCenterPage from "./pages/footerpages/HelpCenterPage";
import CommunityPage from "./pages/footerpages/CommunityPage";
import ApiStatusPage from "./pages/footerpages/ApiStatusPage";
import ReportBugPage from "./pages/footerpages/ReportBugPage";
import PrivacyPolicyPage from "./pages/footerpages/PrivacyPolicyPage";
import TermsPage from "./pages/footerpages/TermsPage";

/* ðŸ”’ Protected Route */
const ProtectedRoute: React.FC<{ children: React.ReactNode }> = ({ children }) =>
  true ? <>{children}</> : <Navigate to="/login" replace />;

/* ðŸŒ Routes Wrapper (for AnimatePresence) */
const AnimatedRoutes = () => {
  const location = useLocation();

  return (
    <AnimatePresence mode="wait">
      <Routes location={location} key={location.pathname}>
        <Route path="/" element={<Navigate to="/login" replace />} />
        <Route path="/login" element={<AuthPage />} />
        <Route path="/register" element={<RegisterPage />} />

        <Route
          element={
            <ProtectedRoute>
              <MainLayout />
            </ProtectedRoute>
          }
        >
          <Route path="/dashboard" element={<DashboardPage />} />
          <Route path="/notifications" element={<NotificationPage />} />
          <Route path="/messages" element={<MessagesPage />} />
          <Route path="/tasks" element={<TasksPage />} />
          <Route path="/tasks/new" element={<AddTaskPage />} />
          <Route path="/tasks/edit/:id" element={<AddTaskPage />} />
          <Route path="/meetings" element={<MeetingsPage />} />
          <Route path="/meetings/schedule" element={<ScheduleMeetingPage />} />
          <Route path="/calendar" element={<CalendarPage />} />
          <Route path="/files" element={<FilesPage />} />
          <Route path="/files/upload" element={<UploadFilePage />} />
          <Route path="/team" element={<TeamPage />} />
          <Route path="/settings" element={<SettingsPage />} />
          <Route path="/profile" element={<ProfilePage />} />
          <Route path="/about" element={<AboutPage />} />
          <Route path="/features" element={<FeaturesPage />} />
          <Route path="/docs" element={<DocsPage />} />
          <Route path="/support" element={<SupportPage />} />
          <Route path="/careers" element={<CareersPage />} />
          <Route path="/blog" element={<BlogPage />} />
          <Route path="/press" element={<PressKitPage />} />
          <Route path="/contact" element={<ContactPage />} />
          <Route path="/help-center" element={<HelpCenterPage />} />
          <Route path="/community" element={<CommunityPage />} />
          <Route path="/status" element={<ApiStatusPage />} />
          <Route path="/report-bug" element={<ReportBugPage />} />
          <Route path="/privacy" element={<PrivacyPolicyPage />} />
          <Route path="/terms" element={<TermsPage />} />
        </Route>

        <Route
          path="*"
          element={
            <div className="h-screen flex items-center justify-center text-gray-500 text-xl">
              404 â€” Page Not Found
            </div>
          }
        />
      </Routes>
    </AnimatePresence>
  );
};

import { NotificationProvider } from "./context/NotificationContext";

const App = () => (
  <NotificationProvider>
    <BrowserRouter>
      <ScrollToTop />
      <AnimatedRoutes />
    </BrowserRouter>
  </NotificationProvider>
);

export default App;
