import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import {
    User,
    Bell,
    ShieldCheck,
    Save,
    Camera,
    HardDrive,
    Zap,
    Loader2,
    CheckCircle2,
    Eye,
    Smartphone,
    CreditCard,
    Database,
    HelpCircle,
    Globe,
    Lock
} from "lucide-react";
import { authService } from "../../services/authService";

const SettingsPage: React.FC = () => {
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [success, setSuccess] = useState(false);
    const [settings, setSettings] = useState<any>(null);

    useEffect(() => {
        const fetchSettings = async () => {
            try {
                const data = await authService.getCurrentUser();
                setSettings(data);
            } catch (error) {
                console.error("Failed to load settings", error);
            } finally {
                setLoading(false);
            }
        };
        fetchSettings();
    }, []);

    const handleUpdate = (field: string, value: any) => {
        setSettings((prev: any) => ({ ...prev, [field]: value }));
    };

    const handleSave = async () => {
        try {
            setSaving(true);
            setSuccess(false);
            const { id, email, hashed_password, last_seen, status, ...updateData } = settings;
            await authService.updateProfile(updateData);
            setSuccess(true);
            setTimeout(() => setSuccess(false), 3000);
        } catch (error) {
            console.error("Failed to save settings", error);
        } finally {
            setSaving(false);
        }
    };

    if (loading || !settings) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-white">
                <Loader2 className="w-8 h-8 text-indigo-600 animate-spin" />
            </div>
        );
    }

    const Section = ({ icon: Icon, title, desc, children }: any) => (
        <section className="bg-white border border-gray-200 rounded-xl overflow-hidden mb-8 shadow-sm">
            <div className="px-6 py-4 bg-gray-50/50 border-b border-gray-100 flex items-center gap-3">
                <Icon size={18} className="text-gray-500" />
                <div>
                    <h2 className="text-base font-bold text-gray-900">{title}</h2>
                    <p className="text-xs text-gray-500">{desc}</p>
                </div>
            </div>
            <div className="p-6">{children}</div>
        </section>
    );

    const Toggle = ({ active, label, subtitle, field }: any) => (
        <div className="flex items-center justify-between py-4 first:pt-0 border-b border-gray-50 last:border-0 last:pb-0">
            <div>
                <p className="text-sm font-semibold text-gray-700">{label}</p>
                <p className="text-xs text-gray-400">{subtitle}</p>
            </div>
            <button
                onClick={() => handleUpdate(field, !active)}
                className={`relative inline-flex h-5 w-10 items-center rounded-full transition-colors focus:outline-none ${active ? 'bg-indigo-600' : 'bg-gray-200'}`}
            >
                <span className={`inline-block h-3 w-3 transform rounded-full bg-white transition-transform ${active ? 'translate-x-6' : 'translate-x-1'}`} />
            </button>
        </div>
    );

    return (
        <div className="max-w-4xl mx-auto p-6 space-y-8 pb-32">
            {/* Standard Header */}
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-4 border-b border-gray-100 pb-6">
                <div>
                    <h1 className="text-2xl font-bold text-gray-900">Settings</h1>
                    <p className="text-sm text-gray-500">Manage your account preferences and site configuration.</p>
                </div>
                <button
                    onClick={handleSave}
                    disabled={saving}
                    className={`flex items-center justify-center gap-2 px-6 py-2.5 rounded-lg font-semibold text-sm transition-all ${success ? 'bg-green-600 text-white' : 'bg-indigo-600 text-white hover:bg-indigo-700'
                        }`}
                >
                    {saving ? <Loader2 size={16} className="animate-spin" /> : success ? <CheckCircle2 size={16} /> : <Save size={16} />}
                    {saving ? 'Saving...' : success ? 'Saved Successfully' : 'Save Changes'}
                </button>
            </div>



            {/* Localization Section */}
            <Section icon={Globe} title="Regional Settings" desc="Language and timezone preferences">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div className="space-y-1">
                        <label className="text-xs font-semibold text-gray-600">Language</label>
                        <select
                            value={settings.language}
                            onChange={(e) => handleUpdate('language', e.target.value)}
                            className="w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:ring-1 focus:ring-indigo-500 focus:border-indigo-500 outline-none text-sm"
                        >
                            <option>English (US)</option>
                            <option>Hindi (HI)</option>
                            <option>Spanish (ES)</option>
                        </select>
                    </div>
                    <div className="space-y-1">
                        <label className="text-xs font-semibold text-gray-600">Timezone</label>
                        <select
                            value={settings.timezone}
                            onChange={(e) => handleUpdate('timezone', e.target.value)}
                            className="w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:ring-1 focus:ring-indigo-500 focus:border-indigo-500 outline-none text-sm"
                        >
                            <option value="(GMT+05:30) Mumbai, New Delhi">India (GMT+5:30)</option>
                            <option value="(GMT-08:00) Pacific Time">Pacific Time (GMT-8:00)</option>
                        </select>
                    </div>
                </div>
            </Section>

            {/* Privacy Section */}
            <Section icon={Eye} title="Privacy" desc="Manage your visibility and data">
                <div className="space-y-1 shadow-inner rounded-lg px-4 bg-gray-50/30 border border-gray-100">
                    <Toggle active={settings.show_online_status} label="Show Online Status" subtitle="Allow others to see when you are active" field="show_online_status" />
                    <Toggle active={settings.share_local_time} label="Share Local Time" subtitle="Display your timezone for better scheduling" field="share_local_time" />
                    <Toggle active={settings.anonymous_reporting} label="Anonymous Statistics" subtitle="Help us improve with usage data" field="anonymous_reporting" />
                </div>
            </Section>

            {/* Notifications Section */}
            <Section icon={Bell} title="Notifications" desc="Configure how you receive updates">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                    <div className="space-y-1">
                        <h4 className="text-xs font-bold text-gray-400 uppercase tracking-widest mb-3">Email</h4>
                        <Toggle active={settings.email_tasks} label="Task Assignments" subtitle="Daily task updates" field="email_tasks" />
                        <Toggle active={settings.email_summary} label="Weekly Digest" subtitle="Workspace summary" field="email_summary" />
                    </div>
                    <div className="space-y-1">
                        <h4 className="text-xs font-bold text-gray-400 uppercase tracking-widest mb-3">Push</h4>
                        <Toggle active={settings.push_meetings} label="Meeting Invites" subtitle="Meeting reminders" field="push_meetings" />
                        <Toggle active={settings.push_chat} label="New Messages" subtitle="Chat notifications" field="push_chat" />
                    </div>
                </div>
            </Section>

            {/* Security Section */}
            <Section icon={ShieldCheck} title="Security" desc="Secure your account with 2FA">
                <div className="p-4 rounded-lg border border-gray-200 bg-gray-50/50 flex flex-col md:flex-row items-center justify-between gap-4">
                    <div className="flex items-center gap-4">
                        <div className={`p-2 rounded-lg ${settings.two_factor_enabled ? 'bg-green-100 text-green-600' : 'bg-orange-100 text-orange-600'}`}>
                            <ShieldCheck size={20} />
                        </div>
                        <div>
                            <p className="text-sm font-semibold text-gray-900">Two-Factor Authentication</p>
                            <p className="text-xs text-gray-500 mt-0.5">{settings.two_factor_enabled ? 'Active and protecting your account' : 'Enhance your security level'}</p>
                        </div>
                    </div>
                    <button
                        onClick={() => handleUpdate('two_factor_enabled', !settings.two_factor_enabled)}
                        className={`px-4 py-1.5 rounded-md text-xs font-bold transition-all ${settings.two_factor_enabled
                            ? 'bg-white text-gray-600 border border-gray-300'
                            : 'bg-gray-900 text-white'
                            }`}
                    >
                        {settings.two_factor_enabled ? 'Disable 2FA' : 'Enable 2FA'}
                    </button>
                </div>
            </Section>

            {/* Storage Section */}
            <Section icon={HardDrive} title="Storage" desc="Resource usage and data export">
                <div className="space-y-6">
                    <div>
                        <div className="flex justify-between text-xs font-semibold text-gray-600 mb-2">
                            <span>Cloud Storage</span>
                            <span>4.2 GB used of 10 GB</span>
                        </div>
                        <div className="w-full h-2 bg-gray-100 rounded-full overflow-hidden">
                            <div className="h-full bg-indigo-600" style={{ width: '42%' }} />
                        </div>
                    </div>
                    <div className="flex gap-4">
                        <button className="flex items-center gap-2 px-4 py-2 border border-gray-300 rounded-md text-xs font-bold text-gray-600 hover:bg-gray-50 transition-colors">
                            <Database size={14} /> Export data
                        </button>
                        <button className="flex items-center gap-2 px-4 py-2 border border-gray-300 rounded-md text-xs font-bold text-gray-600 hover:bg-gray-50 transition-colors">
                            <Zap size={14} /> Clear cache
                        </button>
                    </div>
                </div>
            </Section>

            {/* Footer */}
            <div className="flex flex-wrap items-center justify-center gap-8 py-8 border-t border-gray-100 text-gray-400">
                <button className="flex items-center gap-1.5 text-xs font-semibold hover:text-gray-600"><HelpCircle size={14} /> Help</button>
                <button className="flex items-center gap-1.5 text-xs font-semibold hover:text-gray-600"><CreditCard size={14} /> Billing</button>
                <button className="flex items-center gap-1.5 text-xs font-semibold hover:text-gray-600"><Smartphone size={14} /> App</button>
            </div>
        </div>
    );
};

export default SettingsPage;
