import React, { useState, useEffect } from "react";
import {
  Plus,
  Search,
  Loader2,
  Trash2,
  Edit3,
  Calendar,
  User as UserIcon,
  Filter,
  AlertCircle,
  X,
  Clock,
  CheckCircle2,
  Zap,
  ArrowRight,
  Shield,
  Layers,
  ChevronRight,
  ArrowUpRight,
  MoreVertical,
  ClipboardList
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { taskService, Task } from "../../services/taskService";
import { motion, AnimatePresence } from "framer-motion";


interface TaskCardProps {
  task: Task;
  onClick: () => void;
  selected: boolean;
  getTimeStatus: (d: string | null | undefined, s: string) => { label: string; style: string };
  variant?: 'grid' | 'list';
}

const TaskCard: React.FC<TaskCardProps> = ({ task, onClick, selected, getTimeStatus, variant = 'grid' }) => {
  const timeStatus = getTimeStatus(task.due_date, task.status);

  const getTheme = () => {
    // Green -> In Process
    if (timeStatus.style.includes('emerald')) {
      return { bg: 'bg-emerald-50', text: 'text-emerald-600', icon: Clock, border: 'group-hover:border-emerald-300' };
    }
    // Gray -> Completed
    if (timeStatus.style.includes('slate')) {
      return { bg: 'bg-slate-100', text: 'text-slate-500', icon: CheckCircle2, border: 'group-hover:border-slate-300' };
    }
    // White -> Upcoming (Default)
    return { bg: 'bg-white border border-slate-200', text: 'text-slate-700', icon: Calendar, border: 'group-hover:border-slate-400' };
  };

  const theme = getTheme();
  const Icon = theme.icon;

  return (
    <motion.div
      layout
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, scale: 0.95 }}
      onClick={onClick}
      className={`group relative p-4 bg-white border rounded-[24px] transition-all duration-300 cursor-pointer flex items-center gap-5 ${selected
          ? 'border-slate-900 ring-2 ring-slate-900/10 shadow-xl z-10'
          : `border-slate-200 hover:shadow-xl hover:shadow-slate-200/50 hover:-translate-y-1 ${theme.border}`
        }`}
    >
      {/* Icon Box */}
      <div className={`w-14 h-14 rounded-2xl flex items-center justify-center shrink-0 transition-colors duration-300 ${theme.bg} ${theme.text}`}>
        <Icon size={24} strokeWidth={2} />
      </div>

      {/* Content */}
      <div className="flex-1 min-w-0">
        <div className="flex items-center gap-2 mb-1">
          <span className={`text-[10px] font-black uppercase tracking-wider ${theme.text}`}>
            {timeStatus.label}
          </span>
        </div>
        <h4 className={`text-base font-bold text-slate-900 truncate leading-snug ${task.status === 'Completed' ? 'line-through text-slate-400' : ''}`}>
          {task.title}
        </h4>
      </div>

      {/* Metadata (Clean & Minimal) */}
      <div className="flex items-center gap-4 shrink-0 pl-4 border-l border-slate-100">
        {/* Due Date Only */}
        {task.due_date ? (
          <div className="flex flex-col items-end">
            <span className="text-[9px] font-bold text-slate-400 uppercase tracking-wider">Due</span>
            <span className={`text-xs font-bold font-mono ${theme.text.replace('text-', 'text-slate-') === 'text-slate-' ? 'text-slate-700' : theme.text}`}>
              {new Date(task.due_date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' })}
            </span>
          </div>
        ) : (
          <span className="text-[10px] font-bold text-slate-300 uppercase tracking-widest">No Date</span>
        )}

        {/* Avatar */}
        {task.assigned_to_user ? (
          <div className="w-10 h-10 rounded-2xl bg-slate-900 text-white text-xs font-black flex items-center justify-center shadow-md shadow-slate-200">
            {task.assigned_to_user.name.substring(0, 1)}
          </div>
        ) : (
          <div className="w-10 h-10 rounded-2xl bg-slate-50 text-slate-300 flex items-center justify-center border border-dashed border-slate-200">
            <UserIcon size={18} />
          </div>
        )}
      </div>
    </motion.div>
  );
};

const TasksPage: React.FC = () => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("All");
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);
  const [viewMode, setViewMode] = useState("All");

  useEffect(() => {
    fetchTasks();
    const pollInterval = setInterval(() => {
      fetchTasks(false);
    }, 30000);

    return () => {
      clearInterval(pollInterval);
    };
  }, []);

  const fetchTasks = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  const normalizeStatus = (status: string) => {
    const s = status ? status.toLowerCase() : "";
    if (s === "todo" || s === "upcoming") return "Upcoming";
    if (s === "in_progress" || s === "in progress" || s === "inprogress") return "In Process";
    if (s === "completed" || s === "done") return "Completed";
    return "Upcoming";
  };

  const getTimeStatus = (dueDate: string | null | undefined, status: string) => {
    const normStatus = normalizeStatus(status);

    // Check if task is overdue
    let isOverdue = false;
    if (dueDate) {
      const today = new Date();
      today.setHours(0, 0, 0, 0);
      const due = new Date(dueDate);
      due.setHours(0, 0, 0, 0);
      if (due < today) isOverdue = true;
    }

    // Completed or Overdue -> Gray Theme
    if (normStatus === "Completed" || isOverdue) {
      return { label: "Completed", style: "bg-slate-100 text-slate-500 border-slate-200" };
    }

    // In Process -> Green Theme
    if (normStatus === "In Process") {
      return { label: "In Process", style: "bg-emerald-50 text-emerald-600 border-emerald-100" };
    }

    // Upcoming -> White/Default Theme
    return { label: "Upcoming", style: "bg-white text-slate-900 border-slate-200" };
  };

  const handleDelete = async (e: React.MouseEvent, id: number) => {
    e.stopPropagation();
    if (!window.confirm("Are you sure you want to delete this task?")) return;
    try {
      await taskService.deleteTask(id);
      setTasks(prev => prev.filter(t => t.id !== id));
      if (selectedTask?.id === id) setSelectedTask(null);
    } catch (error) {
      console.error("Failed to delete task");
    }
  };

  const baseFiltered = tasks.filter(task => {
    return task.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (task.description && task.description.toLowerCase().includes(searchQuery.toLowerCase()));
  });

  const today = new Date();
  today.setHours(0, 0, 0, 0);

  const sevenDaysAgo = new Date(today);
  sevenDaysAgo.setDate(today.getDate() - 7);

  const tasksInProcess = baseFiltered.filter(t => {
    const status = normalizeStatus(t.status);
    if (status === 'Completed') return false;

    if (t.due_date) {
      const d = new Date(t.due_date);
      d.setHours(0, 0, 0, 0);
      // If overdue (< today), move to Completed. If future (> today), move to Upcoming.
      if (d < today || d > today) return false;
    }
    // Matches: No Due Date OR Due Date === Today
    return true;
  });

  const tasksUpcoming = baseFiltered.filter(t => {
    const status = normalizeStatus(t.status);
    if (status === 'Completed') return false;
    if (t.due_date) {
      const d = new Date(t.due_date);
      d.setHours(0, 0, 0, 0);
      return d > today;
    }
    return false;
  });

  const tasksCompleted = baseFiltered.filter(t => {
    const status = normalizeStatus(t.status);

    let effectiveDate: Date | null = null;
    if (t.due_date) {
      effectiveDate = new Date(t.due_date);
      effectiveDate.setHours(0, 0, 0, 0);
    }

    // Determine if task belongs in Completed column (Explicitly Completed OR Overdue)
    const isExplicitlyCompleted = status === 'Completed';
    const isOverdue = effectiveDate && effectiveDate < today;

    if (!isExplicitlyCompleted && !isOverdue) return false;

    // Auto-Hide Logic: Hide if the effective date (due date) is older than 7 days
    if (effectiveDate && effectiveDate < sevenDaysAgo) {
      return false;
    }

    return true;
  });

  return (
    <div className="min-h-screen text-slate-900 pb-12 animate-fade-in relative">

      {/* Background Decor */}
      {/* Background Decor Removed */}

      {/* Main Content Area */}
      <div className="transition-all duration-500">
        <div className="max-w-[1600px] mx-auto px-4 sm:px-6 lg:px-8 py-8">

          <div className="flex flex-col md:flex-row items-end justify-between mb-10 gap-6">
            <div className="space-y-4 w-full md:w-auto">
              <h1 className="text-4xl font-black tracking-tight text-slate-900">My Tasks</h1>
              <div className="flex items-center gap-1 p-1.5 bg-slate-100/80 backdrop-blur-sm rounded-2xl w-fit border border-slate-200/50">
                {['All', 'In Process', 'Upcoming', 'Completed'].map(mode => (
                  <button
                    key={mode}
                    onClick={() => setViewMode(mode)}
                    className={`px-5 py-2.5 rounded-xl text-sm font-bold transition-all relative ${viewMode === mode
                      ? 'bg-white text-slate-900 shadow-sm ring-1 ring-slate-200 shadow-slate-200/50'
                      : 'text-slate-500 hover:text-slate-700 hover:bg-slate-200/50'}`}
                  >
                    {mode}
                    {viewMode === mode && (
                      <motion.span layoutId="activeTab" className="absolute inset-0 rounded-xl bg-transparent" />
                    )}
                  </button>
                ))}
              </div>
            </div>
            <div className="flex items-center gap-3 w-full md:w-auto">
              <button
                onClick={() => fetchTasks(true)}
                disabled={loading}
                className="w-12 h-12 rounded-2xl bg-white border border-slate-200 text-slate-500 flex items-center justify-center hover:bg-slate-50 hover:text-blue-600 transition-all shadow-sm hover:shadow-md active:scale-95 disabled:opacity-50"
                title="Refresh Tasks"
              >
                <Loader2 size={20} className={loading ? "animate-spin text-blue-600" : ""} />
              </button>
              <button
                onClick={() => navigate('/tasks/new')}
                className="btn-premium flex-1 md:flex-none h-12 px-6 flex items-center justify-center gap-2"
              >
                <Plus size={20} strokeWidth={3} />
                <span>New Task</span>
              </button>
            </div>
          </div>

          {!loading && (
            <div className={viewMode === 'All' ? "grid grid-cols-1 lg:grid-cols-3 gap-8 h-full" : "max-w-4xl mx-auto space-y-4"}>
              {/* Column 1: In Process */}
              {(viewMode === 'All' || viewMode === 'In Process') && (
                <div className={`flex flex-col gap-4 ${viewMode !== 'All' ? 'w-full' : ''}`}>
                  {/* Header Removed */}

                  <div className="space-y-3 min-h-[100px]">
                    <AnimatePresence>
                      {tasksInProcess.length === 0 && (
                        <div className="py-12 text-center border-2 border-dashed border-slate-100 rounded-[24px] bg-slate-50/30">
                          <p className="text-slate-400 font-bold text-xs uppercase tracking-widest">No active tasks</p>
                        </div>
                      )}
                      {tasksInProcess.map(task => (
                        <TaskCard
                          key={task.id}
                          task={task}
                          onClick={() => setSelectedTask(task)}
                          selected={selectedTask?.id === task.id}
                          getTimeStatus={getTimeStatus}
                          variant={viewMode === 'All' ? 'grid' : 'list'}
                        />
                      ))}
                    </AnimatePresence>
                  </div>
                </div>
              )}

              {/* Column 2: Upcoming */}
              {(viewMode === 'All' || viewMode === 'Upcoming') && (
                <div className={`flex flex-col gap-4 ${viewMode !== 'All' ? 'w-full' : ''}`}>
                  {/* Header Removed */}

                  <div className="space-y-3 min-h-[100px]">
                    <AnimatePresence>
                      {tasksUpcoming.length === 0 && (
                        <div className="py-12 text-center border-2 border-dashed border-slate-100 rounded-[24px] bg-slate-50/30">
                          <p className="text-slate-400 font-bold text-xs uppercase tracking-widest">No upcoming tasks</p>
                        </div>
                      )}
                      {tasksUpcoming.map(task => (
                        <TaskCard
                          key={task.id}
                          task={task}
                          onClick={() => setSelectedTask(task)}
                          selected={selectedTask?.id === task.id}
                          getTimeStatus={getTimeStatus}
                          variant={viewMode === 'All' ? 'grid' : 'list'}
                        />
                      ))}
                    </AnimatePresence>
                  </div>
                </div>
              )}

              {/* Column 3: Completed */}
              {(viewMode === 'All' || viewMode === 'Completed') && (
                <div className={`flex flex-col gap-4 ${viewMode !== 'All' ? 'w-full' : ''}`}>
                  {/* Header Removed */}

                  <div className={`space-y-3 min-h-[100px] ${viewMode === 'All' ? 'opacity-70 hover:opacity-100 transition-opacity' : ''}`}>
                    <AnimatePresence>
                      {tasksCompleted.length === 0 && (
                        <div className="py-12 text-center border-2 border-dashed border-slate-100 rounded-[24px] bg-slate-50/30">
                          <p className="text-slate-400 font-bold text-xs uppercase tracking-widest">No completed tasks</p>
                        </div>
                      )}
                      {tasksCompleted.map(task => (
                        <TaskCard
                          key={task.id}
                          task={task}
                          onClick={() => setSelectedTask(task)}
                          selected={selectedTask?.id === task.id}
                          getTimeStatus={getTimeStatus}
                          variant={viewMode === 'All' ? 'grid' : 'list'}
                        />
                      ))}
                    </AnimatePresence>
                  </div>
                </div>
              )}
            </div>
          )}

          {loading && (
            <div className="py-32 text-center bg-white border border-slate-200 shadow-sm rounded-xl">
              <div className="w-16 h-16 border-4 border-blue-50 border-t-blue-600 rounded-full animate-spin mx-auto mb-6" />
              <p className="text-sm font-bold text-slate-400 uppercase tracking-widest animate-pulse">Syncing tasks...</p>
            </div>
          )}

          {!loading && tasks.length === 0 && (
            <div className="py-32 text-center glass-card rounded-[32px]">
              <div className="w-20 h-20 bg-slate-50 rounded-[24px] flex items-center justify-center mx-auto mb-6 shadow-inner">
                <ClipboardList className="text-slate-300" size={32} />
              </div>
              <h4 className="text-xl font-bold text-slate-900">No tasks found</h4>
              <p className="text-slate-400 font-medium mt-2">Time to create a new one!</p>
            </div>
          )}

        </div>

        {/* Task Detail Modal */}
        <AnimatePresence>
          {selectedTask && (
            <div className="fixed inset-0 z-50 flex items-center justify-center p-4 lg:p-8">
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                className="absolute inset-0 bg-slate-900/20 backdrop-blur-sm"
                onClick={() => setSelectedTask(null)}
              />

              <motion.div
                layoutId={`task-${selectedTask.id}`}
                className="bg-white w-full max-w-2xl rounded-[32px] shadow-2xl overflow-hidden relative z-10 flex flex-col max-h-[90vh]"
                initial={{ scale: 0.95, opacity: 0, y: 20 }}
                animate={{ scale: 1, opacity: 1, y: 0 }}
                exit={{ scale: 0.95, opacity: 0, y: 20 }}
                transition={{ type: "spring", damping: 25, stiffness: 300 }}
              >
                {/* Modal Header */}
                <div className="p-8 pb-0 flex justify-between items-start shrink-0">
                  <div className="flex items-center gap-3">
                    <span className={`px-3 py-1.5 rounded-xl text-xs font-bold border flex items-center gap-2 ${getTimeStatus(selectedTask.due_date, selectedTask.status).style}`}>
                      <span className="w-1.5 h-1.5 rounded-full bg-current" />
                      {normalizeStatus(selectedTask.status).toUpperCase()}
                    </span>
                    {selectedTask.priority === 'High' && (
                      <span className="px-3 py-1.5 rounded-xl text-xs font-bold border bg-rose-50 text-rose-600 border-rose-100 flex items-center gap-1.5">
                        <AlertCircle size={14} strokeWidth={2.5} /> Urgent
                      </span>
                    )}
                  </div>
                  <button
                    onClick={() => setSelectedTask(null)}
                    className="w-10 h-10 rounded-full bg-slate-50 text-slate-400 hover:bg-slate-100 hover:text-slate-900 flex items-center justify-center transition-all"
                  >
                    <X size={20} />
                  </button>
                </div>

                {/* Modal Body */}
                <div className="p-8 overflow-y-auto custom-scrollbar">
                  <h2 className="text-3xl font-bold text-slate-900 leading-tight mb-8">
                    {selectedTask.title}
                  </h2>

                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
                    <div className="p-4 rounded-2xl bg-slate-50 border border-slate-100">
                      <div className="flex items-center gap-2 text-slate-400 mb-2">
                        <Calendar size={14} />
                        <span className="text-[10px] font-bold uppercase tracking-wider">Due Date</span>
                      </div>
                      <p className="text-sm font-bold text-slate-700">
                        {selectedTask.due_date ? new Date(selectedTask.due_date).toLocaleDateString(undefined, { weekday: 'short', month: 'short', day: 'numeric' }) : 'No date set'}
                      </p>
                    </div>
                    <div className="p-4 rounded-2xl bg-slate-50 border border-slate-100">
                      <div className="flex items-center gap-2 text-slate-400 mb-2">
                        <UserIcon size={14} />
                        <span className="text-[10px] font-bold uppercase tracking-wider">Assignee</span>
                      </div>
                      <p className="text-sm font-bold text-slate-700 flex items-center gap-2">
                        {selectedTask.assigned_to_user?.name ? (
                          <>
                            <span className="w-5 h-5 rounded-full bg-blue-100 text-blue-600 flex items-center justify-center text-[9px]">
                              {selectedTask.assigned_to_user.name.substring(0, 1)}
                            </span>
                            {selectedTask.assigned_to_user.name}
                          </>
                        ) : 'Unassigned'}
                      </p>
                    </div>
                    <div className="p-4 rounded-2xl bg-slate-50 border border-slate-100">
                      <div className="flex items-center gap-2 text-slate-400 mb-2">
                        <Zap size={14} />
                        <span className="text-[10px] font-bold uppercase tracking-wider">Impact</span>
                      </div>
                      <p className="text-sm font-bold text-slate-700">
                        {selectedTask.priority || 'Normal'} Priority
                      </p>
                    </div>
                  </div>

                  <div className="prose prose-slate prose-sm max-w-none">
                    <h3 className="text-sm font-bold text-slate-900 mb-3 flex items-center gap-2">
                      <Layers size={16} className="text-blue-500" />
                      Description & Notes
                    </h3>
                    <div className="p-6 rounded-2xl border border-slate-100 bg-white shadow-sm text-slate-600 leading-relaxed whitespace-pre-wrap">
                      {selectedTask.description || "No additional notes found for this task."}
                    </div>
                  </div>
                </div>

                {/* Modal Footer */}
                <div className="p-6 border-t border-slate-100 bg-slate-50/50 flex justify-end gap-3 shrink-0">
                  <button
                    onClick={(e) => handleDelete(e, selectedTask.id)}
                    className="px-5 py-2.5 rounded-xl font-bold text-sm text-rose-600 bg-white border border-slate-200 hover:bg-rose-50 hover:border-rose-100 hover:shadow-sm transition-all flex items-center gap-2"
                  >
                    <Trash2 size={16} /> Delete
                  </button>
                  <button
                    onClick={() => navigate(`/tasks/edit/${selectedTask.id}`)}
                    className="px-5 py-2.5 rounded-xl font-bold text-sm text-white bg-blue-600 hover:bg-blue-700 hover:shadow-lg hover:shadow-blue-200 hover:-translate-y-0.5 transition-all flex items-center gap-2"
                  >
                    <Edit3 size={16} /> Edit Task
                  </button>
                </div>
              </motion.div>
            </div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default TasksPage;
