import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  FileText,
  FileSpreadsheet,
  Image as ImageIcon,
  File,
  Video,
  Upload,
  Search,
  MoreHorizontal,
  Download,
  Trash2,
  Clock,
  ChevronRight,
  HardDrive,
  Zap,
  Layers,
  Activity,
  Shield,
  Star,
  Plus,
  Loader2
} from "lucide-react";
import { fileService, FileData } from "../../services/fileService";
import { webSocketService, WebSocketMessage } from "../../services/websocketService";
import { motion, AnimatePresence } from "framer-motion";

const FilesPage: React.FC = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState("");
  const [recentFiles, setRecentFiles] = useState<FileData[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState<string | null>(null);

  const fetchFiles = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const files = await fileService.getAllFiles();
      setRecentFiles(files);
    } catch (error) {
      console.error("Failed to load files", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  const getFileCounts = (files: FileData[]) => {
    const counts = { word: 0, pdf: 0, spread: 0, image: 0, other: 0 };
    files.forEach(f => {
      const ext = f.filename.split('.').pop()?.toLowerCase() || '';
      if (['doc', 'docx'].includes(ext)) counts.word++;
      else if (['pdf'].includes(ext)) counts.pdf++;
      else if (['xls', 'xlsx', 'csv'].includes(ext)) counts.spread++;
      else if (['jpg', 'jpeg', 'png', 'svg', 'gif'].includes(ext)) counts.image++;
      else counts.other++;
    });
    return counts;
  };

  const counts = getFileCounts(recentFiles);

  const fileCategories = [
    { id: "word", name: "Documents", count: counts.word, icon: <FileText size={20} />, light: "bg-blue-50 text-blue-600" },
    { id: "pdf", name: "PDF Files", count: counts.pdf, icon: <FileText size={20} />, light: "bg-rose-50 text-rose-600" },
    { id: "spread", name: "Sheets", count: counts.spread, icon: <FileSpreadsheet size={20} />, light: "bg-emerald-50 text-emerald-600" },
    { id: "image", name: "Images", count: counts.image, icon: <ImageIcon size={20} />, light: "bg-purple-50 text-purple-600" },
    { id: "other", name: "Others", count: counts.other, icon: <File size={20} />, light: "bg-slate-50 text-slate-600" },
  ];

  useEffect(() => {
    fetchFiles();
    const unsubscribe = webSocketService.subscribe((message: WebSocketMessage) => {
      if (message.type === 'file') {
        fetchFiles(false);
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);

  const formatSize = (bytes: number) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB', 'TB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(1)) + ' ' + sizes[i];
  };

  const mapFileType = (ftype: string | undefined, filename: string) => {
    const ext = filename.split('.').pop()?.toLowerCase() || '';
    if (['doc', 'docx'].includes(ext)) return <FileText size={24} className="text-blue-600" />;
    if (['pdf'].includes(ext)) return <FileText size={24} className="text-rose-600" />;
    if (['xls', 'xlsx', 'csv'].includes(ext)) return <FileSpreadsheet size={24} className="text-emerald-600" />;
    if (['jpg', 'jpeg', 'png', 'svg', 'gif'].includes(ext)) return <ImageIcon size={24} className="text-purple-600" />;
    if (['mp4', 'mov', 'avi'].includes(ext)) return <Video size={24} className="text-indigo-600" />;
    return <File size={24} className="text-slate-400" />;
  };

  const filteredFiles = recentFiles.filter(file => {
    const matchesSearch = file.filename.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (file.title && file.title.toLowerCase().includes(searchQuery.toLowerCase()));

    if (!selectedCategory) return matchesSearch;

    const ext = file.filename.split('.').pop()?.toLowerCase() || '';
    let isCategoryMatch = false;

    switch (selectedCategory) {
      case 'word': isCategoryMatch = ['doc', 'docx'].includes(ext); break;
      case 'pdf': isCategoryMatch = ['pdf'].includes(ext); break;
      case 'spread': isCategoryMatch = ['xls', 'xlsx', 'csv'].includes(ext); break;
      case 'image': isCategoryMatch = ['jpg', 'jpeg', 'png', 'svg', 'gif'].includes(ext); break;
      case 'other':
        isCategoryMatch = !['doc', 'docx', 'pdf', 'xls', 'xlsx', 'csv', 'jpg', 'jpeg', 'png', 'svg', 'gif'].includes(ext);
        break;
      default: isCategoryMatch = true;
    }

    return matchesSearch && isCategoryMatch;
  });

  return (
    <div className="min-h-screen bg-[#fcfdfe] text-slate-900 font-sans">
      <div className="max-w-[1200px] mx-auto px-6 py-10 lg:py-12 relative z-10">

        {/* Header */}
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-6 mb-12">
          <div>
            <h1 className="text-3xl font-bold tracking-tight text-slate-900 mb-1">Files</h1>
            <p className="text-slate-500 font-medium">Access and manage all your team assets and documentation.</p>
          </div>

          <div className="flex items-center gap-3">
            <div className="relative">
              <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={16} />
              <input
                className="w-64 bg-white border border-slate-100 rounded-xl py-2.5 pl-11 pr-4 text-sm font-medium focus:ring-1 focus:ring-indigo-100 outline-none shadow-sm"
                placeholder="Search files..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </div>
            <button
              onClick={() => navigate("/files/upload")}
              className="flex items-center justify-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white px-6 py-2.5 rounded-xl font-semibold transition-all shadow-lg shadow-indigo-100 active:scale-95"
            >
              <Plus size={18} />
              <span>Upload</span>
            </button>
          </div>
        </div>

        {/* Categories Grid */}
        <div className="grid grid-cols-2 md:grid-cols-5 gap-4 mb-12">
          {fileCategories.map((cat) => (
            <div
              key={cat.id}
              onClick={() => setSelectedCategory(selectedCategory === cat.id ? null : cat.id)}
              className={`p-6 rounded-2xl border transition-all cursor-pointer ${selectedCategory === cat.id
                  ? 'bg-indigo-600 border-indigo-600 text-white shadow-lg'
                  : 'bg-white border-slate-100 hover:border-slate-200 shadow-sm'
                }`}
            >
              <div className={`w-10 h-10 rounded-lg flex items-center justify-center mb-4 ${selectedCategory === cat.id ? 'bg-white/20' : cat.light}`}>
                {cat.icon}
              </div>
              <h3 className="text-sm font-bold truncate mb-1">{cat.name}</h3>
              <p className={`text-[10px] font-bold uppercase tracking-widest ${selectedCategory === cat.id ? 'text-indigo-100' : 'text-slate-400'}`}>
                {cat.count} files
              </p>
            </div>
          ))}
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-10">

          {/* File List */}
          <div className="lg:col-span-2 space-y-6">
            <h3 className="text-lg font-bold text-slate-900 border-b border-slate-50 pb-4">Recent Access</h3>

            {loading ? (
              <div className="py-20 text-center">
                <Loader2 className="w-8 h-8 text-indigo-600 animate-spin mx-auto mb-4" />
                <p className="text-sm font-medium text-slate-400">Loading your repository...</p>
              </div>
            ) : filteredFiles.length === 0 ? (
              <div className="py-20 bg-slate-50 border border-dashed border-slate-200 rounded-3xl text-center">
                <Layers size={32} className="text-slate-200 mx-auto mb-4" />
                <p className="text-sm font-semibold text-slate-400">No assets found matching your criteria.</p>
              </div>
            ) : (
              <div className="space-y-3">
                {filteredFiles.map((file) => (
                  <div key={file.id} className="p-4 bg-white border border-slate-100 rounded-2xl hover:border-indigo-100 hover:shadow-sm transition-all flex items-center justify-between group">
                    <div className="flex items-center gap-4">
                      <div className="w-12 h-12 bg-slate-50 rounded-xl flex items-center justify-center group-hover:bg-indigo-50 transition-colors">
                        {mapFileType(file.file_type, file.filename)}
                      </div>
                      <div className="min-w-0">
                        <h4 className="text-sm font-bold text-slate-700 truncate max-w-[200px] mb-0.5">{file.filename}</h4>
                        <div className="flex items-center gap-3 text-[10px] font-bold text-slate-400 uppercase tracking-tight">
                          <span>{formatSize(file.file_size_bytes)}</span>
                          <span>â€¢</span>
                          <span>{new Date(file.uploaded_at).toLocaleDateString()}</span>
                        </div>
                      </div>
                    </div>
                    <div className="flex items-center gap-1 opacity-0 group-hover:opacity-100 transition-all">
                      <button className="p-2 text-slate-400 hover:text-indigo-600"><Download size={18} /></button>
                      <button className="p-2 text-slate-400 hover:text-rose-600"><Trash2 size={18} /></button>
                      <button className="p-2 text-slate-400 hover:text-slate-900"><MoreHorizontal size={18} /></button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Side Module */}
          <div className="space-y-8">
            <div className="bg-slate-900 rounded-3xl p-8 text-white shadow-xl">
              <div className="flex items-center gap-3 mb-8">
                <HardDrive size={20} className="text-indigo-400" />
                <h3 className="text-sm font-bold uppercase tracking-wider text-slate-400">Space Utilization</h3>
              </div>

              <div className="space-y-6 mb-8">
                <div>
                  <div className="flex justify-between text-[11px] font-bold mb-2 uppercase tracking-widest text-slate-500">
                    <span>Workspace Cloud</span>
                    <span className="text-indigo-400">4.2 GB / 10 GB</span>
                  </div>
                  <div className="h-1.5 w-full bg-white/5 rounded-full overflow-hidden">
                    <div className="h-full w-[42%] bg-indigo-500 rounded-full" />
                  </div>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-3">
                <div className="p-4 bg-white/5 rounded-2xl border border-white/5">
                  <p className="text-2xl font-bold">{recentFiles.length}</p>
                  <p className="text-[10px] font-bold text-slate-500 uppercase tracking-widest mt-1">Directives</p>
                </div>
                <div className="p-4 bg-white/5 rounded-2xl border border-white/5">
                  <p className="text-2xl font-bold">128</p>
                  <p className="text-[10px] font-bold text-slate-500 uppercase tracking-widest mt-1">Shared Link</p>
                </div>
              </div>
            </div>

            <div className="p-8 bg-indigo-600 rounded-3xl text-white shadow-xl shadow-indigo-100 relative overflow-hidden group">
              <Star size={24} className="text-white/20 mb-6" />
              <h4 className="text-lg font-bold mb-2">Extended Archive</h4>
              <p className="text-sm font-medium text-indigo-100 mb-8 leading-relaxed">
                Need more storage? Scale your team's cloud capacity instantly with Pro features.
              </p>
              <button className="w-full py-3 bg-white text-indigo-600 rounded-xl font-bold text-xs hover:bg-slate-50 transition-all shadow-lg">
                Scale Infrastructure
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FilesPage;
