import React, { useState } from "react";
import { Eye, EyeOff, Shield, Zap, Activity, Star, ChevronRight, Mail, Lock } from "lucide-react";
import { Link, useNavigate } from "react-router-dom";
import { authService } from "../../services/authService";
import { motion, AnimatePresence } from "framer-motion";

const AuthPage: React.FC = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      await authService.login(email, password);
      navigate("/dashboard");
    } catch (error: any) {
      console.error("Login failed:", error);
      alert(error.response?.data?.detail || "Login failed. Please check your credentials.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-[#f8fafc] flex items-center justify-center p-6 lg:p-0 overflow-hidden relative font-sans">

      {/* Background Orbs */}
      <div className="fixed top-0 right-0 w-[900px] h-[900px] bg-indigo-50/50 rounded-full blur-[180px] -mr-96 -mt-96 animate-pulse" />
      <div className="fixed bottom-0 left-0 w-[700px] h-[700px] bg-sky-50/50 rounded-full blur-[150px] -ml-72 -mb-72" />

      <div className="w-full max-w-[1200px] bg-white lg:min-h-[800px] rounded-[64px] shadow-2xl shadow-indigo-500/10 border-2 border-slate-50 relative z-10 flex flex-col lg:flex-row overflow-hidden">

        {/* Left Section - Hero Brand */}
        <div className="lg:w-[45%] bg-slate-900 p-16 lg:p-24 flex flex-col justify-between relative overflow-hidden">
          {/* Animated Patterns */}
          <div className="absolute top-0 left-0 w-full h-full opacity-10 pointer-events-none">
            <div className="absolute top-[10%] left-[10%] w-64 h-64 border-2 border-indigo-500 rounded-full animate-pulse" />
            <div className="absolute bottom-[20%] right-[10%] w-96 h-96 border-2 border-sky-500 rounded-full opacity-50" />
          </div>

          <div className="relative z-10">
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              className="w-20 h-20 bg-indigo-600 rounded-[24px] flex items-center justify-center shadow-2xl shadow-indigo-500/20 mb-12"
            >
              <Activity size={32} className="text-white" />
            </motion.div>
            <motion.h1
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.2 }}
              className="text-6xl font-[1000] text-white tracking-tighter leading-[0.9] mb-8"
            >
              Neural <br />
              <span className="text-indigo-400">Workspace.</span>
            </motion.h1>
            <motion.p
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.3 }}
              className="text-white/40 text-xl font-medium leading-relaxed max-w-sm"
            >
              Connect with your team in a high-performance matrix driven by Collabryta artificial intelligence.
            </motion.p>
          </div>

          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 0.5 }}
            transition={{ delay: 0.5 }}
            className="relative z-10 pt-12"
          >
            <div className="flex items-center gap-4 text-white/30">
              <Shield size={16} />
              <span className="text-[10px] font-black uppercase tracking-[0.3em]">Quantum Encrypted Tunnel</span>
            </div>
          </motion.div>
        </div>

        {/* Right Section - Auth Form */}
        <div className="lg:w-[55%] p-16 lg:p-32 flex flex-col justify-center bg-white relative">
          <motion.div
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.4 }}
            className="max-w-md w-full mx-auto"
          >
            <div className="mb-12">
              <h2 className="text-4xl font-[1000] text-slate-900 tracking-tight leading-none uppercase">Initialize Access</h2>
              <p className="text-[10px] font-black text-slate-300 uppercase tracking-[0.4em] mt-4">Security Protocol Sequence</p>
            </div>

            <form onSubmit={handleLogin} className="space-y-8">
              <div className="space-y-3">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Operative Identity (Email)</label>
                <div className="relative group">
                  <Mail className="absolute left-6 top-1/2 -translate-y-1/2 text-slate-300 group-focus-within:text-indigo-600 transition-colors" size={18} />
                  <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    className="w-full bg-slate-50/50 border-2 border-slate-50 rounded-[28px] py-5 pl-16 pr-8 text-sm font-bold text-slate-900 focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all placeholder:text-slate-200"
                    placeholder="name@matrix.net"
                    required
                  />
                </div>
              </div>

              <div className="space-y-3">
                <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-4">Access Key (Password)</label>
                <div className="relative group">
                  <Lock className="absolute left-6 top-1/2 -translate-y-1/2 text-slate-300 group-focus-within:text-indigo-600 transition-colors" size={18} />
                  <input
                    type={showPassword ? "text" : "password"}
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="w-full bg-slate-50/50 border-2 border-slate-50 rounded-[28px] py-5 pl-16 pr-14 text-sm font-bold text-slate-900 focus:bg-white focus:border-indigo-600/20 focus:outline-none transition-all placeholder:text-slate-200"
                    placeholder="••••••••"
                    required
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-6 top-1/2 -translate-y-1/2 text-slate-300 hover:text-indigo-600 transition-colors"
                  >
                    {showPassword ? <EyeOff size={18} /> : <Eye size={18} />}
                  </button>
                </div>
              </div>

              <div className="flex items-center justify-between px-2">
                <label className="flex items-center space-x-3 cursor-pointer group">
                  <div className="relative flex items-center">
                    <input
                      type="checkbox"
                      className="peer h-6 w-6 cursor-pointer appearance-none rounded-lg border-2 border-slate-100 bg-white transition-all checked:bg-indigo-600 checked:border-indigo-600"
                    />
                    <div className="pointer-events-none absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 text-white opacity-0 peer-checked:opacity-100">
                      <Zap size={10} className="fill-white" />
                    </div>
                  </div>
                  <span className="text-[10px] font-black text-slate-400 uppercase tracking-widest group-hover:text-slate-900 transition-colors">Persistent Auth</span>
                </label>
                <Link
                  to="/forgot-password"
                  className="text-[10px] font-black text-indigo-600 uppercase tracking-widest hover:underline"
                >
                  Lost Key?
                </Link>
              </div>

              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                type="submit"
                disabled={isLoading}
                className="w-full bg-slate-900 text-white rounded-[28px] py-6 font-black uppercase tracking-[0.3em] text-[10px] shadow-2xl shadow-indigo-500/10 hover:bg-indigo-600 transition-all flex items-center justify-center gap-4 group"
              >
                {isLoading ? <Zap className="animate-[spin_0.5s_linear_infinite]" size={20} /> : (
                  <>
                    Establish Link
                    <ChevronRight size={16} className="group-hover:translate-x-1 transition-transform" />
                  </>
                )}
              </motion.button>
            </form>

            <div className="mt-16 text-center">
              <p className="text-[10px] font-black text-slate-300 uppercase tracking-widest">
                New Operative?{" "}
                <Link to="/register" className="text-indigo-600 hover:underline">
                  Request Induction
                </Link>
              </p>
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
