import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
    Plus,
    MoreHorizontal,
    Search,
    Filter,
    Layout,
    GripVertical,
    Calendar,
    User as UserIcon
} from "lucide-react";
import { taskService, Task } from "../../services/taskService";

interface Column {
    id: string;
    title: string;
    tasks: Task[];
}

const ProjectBoard: React.FC = () => {
    const [columns, setColumns] = useState<Column[]>([
        { id: 'Upcoming', title: 'Upcoming', tasks: [] },
        { id: 'In Progress', title: 'In Progress', tasks: [] },
        { id: 'Completed', title: 'Completed', tasks: [] },
    ]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchTasks = async () => {
            try {
                const tasks = await taskService.getTasks();
                const newColumns = columns.map(col => ({
                    ...col,
                    tasks: tasks.filter(t => {
                        const status = t.status?.toLowerCase() || "";
                        if (col.id === 'Upcoming') return status === 'todo' || status === 'upcoming';
                        if (col.id === 'In Progress') return status === 'in_progress' || status === 'in progress';
                        if (col.id === 'Completed') return status === 'completed' || status === 'done';
                        return false;
                    })
                }));
                setColumns(newColumns);
            } catch (error) {
                console.error("Failed to fetch tasks for board", error);
            } finally {
                setLoading(false);
            }
        };
        fetchTasks();
    }, []);

    return (
        <div className="p-6 h-[calc(100vh-80px)] overflow-hidden flex flex-col space-y-6">
            {/* Header */}
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-4 flex-shrink-0">
                <div>
                    <h1 className="text-2xl font-bold text-gray-900 tracking-tight flex items-center gap-2">
                        <Layout className="text-indigo-600" size={24} />
                        Project Board
                    </h1>
                    <p className="text-gray-500 text-sm">Drag and drop tasks across columns to track progress.</p>
                </div>
                <div className="flex items-center gap-3">
                    <div className="relative">
                        <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={16} />
                        <input
                            type="text"
                            placeholder="Search board..."
                            className="pl-9 pr-4 py-2 bg-white border border-gray-200 rounded-lg text-sm focus:ring-2 focus:ring-indigo-500/20 outline-none"
                        />
                    </div>
                    <button className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-lg text-sm font-semibold flex items-center gap-2 transition-all shadow-sm">
                        <Plus size={18} /> New Column
                    </button>
                </div>
            </div>

            {/* Board Layout */}
            <div className="flex-1 overflow-x-auto pb-4 custom-scrollbar">
                <div className="flex gap-6 h-full min-w-max px-px">
                    {columns.map((column) => (
                        <div key={column.id} className="w-80 flex flex-col h-full rounded-2xl bg-gray-50/50 border border-gray-100">
                            {/* Column Header */}
                            <div className="p-4 flex items-center justify-between">
                                <div className="flex items-center gap-2">
                                    <h3 className="font-bold text-gray-900 text-sm uppercase tracking-wide">{column.title}</h3>
                                    <span className="bg-white border border-gray-200 text-gray-500 text-[10px] font-bold px-2 py-0.5 rounded-full">
                                        {column.tasks.length}
                                    </span>
                                </div>
                                <button className="text-gray-400 hover:text-gray-600 p-1 hover:bg-white rounded transition-colors">
                                    <MoreHorizontal size={16} />
                                </button>
                            </div>

                            {/* Tasks Area */}
                            <div className="flex-1 overflow-y-auto p-3 space-y-3">
                                {column.tasks.map((task) => (
                                    <motion.div
                                        key={task.id}
                                        layoutId={`task-${task.id}`}
                                        className="bg-white p-4 rounded-xl border border-gray-100 shadow-sm hover:shadow-md hover:border-indigo-100 transition-all cursor-grab active:cursor-grabbing group"
                                    >
                                        <div className="flex items-start justify-between mb-3">
                                            <span className={`text-[10px] font-bold px-2 py-0.5 rounded uppercase tracking-wider ${task.priority === 'High' ? 'bg-red-50 text-red-600' :
                                                    task.priority === 'Medium' ? 'bg-amber-50 text-amber-600' :
                                                        'bg-blue-50 text-blue-600'
                                                }`}>
                                                {task.priority || 'Low'}
                                            </span>
                                            <button className="opacity-0 group-hover:opacity-100 text-gray-400 hover:text-gray-600 transition-opacity">
                                                <GripVertical size={14} />
                                            </button>
                                        </div>

                                        <h4 className="text-sm font-bold text-gray-900 mb-2 leading-snug">{task.title}</h4>
                                        {task.description && (
                                            <p className="text-xs text-gray-500 line-clamp-2 mb-4 leading-relaxed font-medium">
                                                {task.description}
                                            </p>
                                        )}

                                        <div className="flex items-center justify-between pt-3 border-t border-gray-50">
                                            <div className="flex -space-x-2">
                                                <div className="w-6 h-6 rounded-full border-2 border-white bg-indigo-100 flex items-center justify-center text-[10px] font-bold text-indigo-700 shadow-sm">
                                                    {task.assigned_to_user?.name.charAt(0) || 'U'}
                                                </div>
                                            </div>
                                            <div className="flex items-center gap-1.5 text-gray-400">
                                                <Calendar size={12} />
                                                <span className="text-[10px] font-semibold">
                                                    {task.due_date ? new Date(task.due_date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' }) : 'No date'}
                                                </span>
                                            </div>
                                        </div>
                                    </motion.div>
                                ))}

                                {/* Empty State / Add Task */}
                                <button className="w-full py-3 border-2 border-dashed border-gray-200 rounded-xl text-gray-400 text-sm font-medium hover:bg-white hover:border-indigo-200 hover:text-indigo-600 transition-all flex items-center justify-center gap-2">
                                    <Plus size={16} />
                                    Add Task
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default ProjectBoard;
