import React, { useState } from "react";
import { Eye, EyeOff, Shield, Zap, Activity, Star, ChevronRight, Mail, Lock, Loader2, ArrowUpRight } from "lucide-react";
import { Link, useNavigate } from "react-router-dom";
import { authService } from "../../services/authService";
import { motion, AnimatePresence } from "framer-motion";

const AuthPage: React.FC = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      await authService.login(email, password);
      navigate("/dashboard");
    } catch (error: any) {
      console.error("Login failed:", error);
      alert(error.response?.data?.detail || "Login failed. Please check your credentials.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-white flex flex-col items-center justify-center p-10 font-sans relative overflow-hidden">

      {/* Structural Elements */}
      <div className="absolute inset-0 pointer-events-none opacity-[0.03]">
        <div className="absolute top-0 left-1/2 -translate-x-1/2 w-px h-full bg-black" />
        <div className="absolute top-1/2 left-0 -translate-y-1/2 w-full h-px bg-black" />
      </div>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="w-full max-w-lg relative z-10"
      >
        {/* Logo Area */}
        <div className="flex flex-col items-center mb-20">
          <div className="w-16 h-16 bg-black flex items-center justify-center mb-10">
            <Activity size={32} className="text-white" />
          </div>
          <p className="text-[10px] font-black tracking-[0.5em] text-zinc-400 mb-4 uppercase">COLLABRYTA / ACCESS</p>
          <h1 className="text-6xl font-black tracking-tighter leading-none uppercase text-center">INITIALIZE<br />SESSION</h1>
        </div>

        {/* Login Form */}
        <div className="bg-white border border-zinc-100 p-12 lg:p-16 shadow-2xl relative">
          <div className="absolute -top-px -left-px w-10 h-10 border-t border-l border-black" />
          <div className="absolute -bottom-px -right-px w-10 h-10 border-b border-r border-black" />

          <form onSubmit={handleLogin} className="space-y-12">
            <div className="space-y-4">
              <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em] ml-1">IDENTIFICATION</label>
              <div className="relative group border-b border-zinc-200 focus-within:border-black transition-all">
                <Mail className="absolute left-0 top-1/2 -translate-y-1/2 text-zinc-200 group-focus-within:text-black transition-colors" size={16} />
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="w-full bg-transparent py-5 pl-8 pr-4 text-[11px] font-bold tracking-widest text-black outline-none transition-all placeholder:text-zinc-100 uppercase"
                  placeholder="OPERATIVE@CORE.NET"
                  required
                />
              </div>
            </div>

            <div className="space-y-4">
              <div className="flex items-center justify-between px-1">
                <label className="text-[10px] font-black text-zinc-400 uppercase tracking-[0.3em]">ENCRYPTION KEY</label>
                <Link to="/forgot-password" size={18} className="text-[10px] font-black text-zinc-300 hover:text-black uppercase tracking-widest transition-all">RECOVER</Link>
              </div>
              <div className="relative group border-b border-zinc-200 focus-within:border-black transition-all">
                <Lock className="absolute left-0 top-1/2 -translate-y-1/2 text-zinc-200 group-focus-within:text-black transition-colors" size={16} />
                <input
                  type={showPassword ? "text" : "password"}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full bg-transparent py-5 pl-8 pr-12 text-[11px] font-bold tracking-widest text-black outline-none transition-all placeholder:text-zinc-100 uppercase"
                  placeholder="••••••••••••"
                  required
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-0 top-1/2 -translate-y-1/2 text-zinc-200 hover:text-black transition-colors"
                >
                  {showPassword ? <EyeOff size={16} /> : <Eye size={16} />}
                </button>
              </div>
            </div>

            <div className="flex items-center gap-4 px-1">
              <input
                type="checkbox"
                className="w-4 h-4 rounded-none border-zinc-200 text-black focus:ring-black"
              />
              <span className="text-[10px] font-black text-zinc-400 uppercase tracking-widest">PERSISTENT SESSION</span>
            </div>

            <button
              disabled={isLoading}
              type="submit"
              className="w-full bg-black hover:bg-zinc-800 text-white py-6 font-black text-[11px] tracking-[0.4em] uppercase transition-all flex items-center justify-center gap-6 active:scale-[0.98]"
            >
              {isLoading ? <Loader2 className="animate-spin" size={20} /> : (
                <>
                  ESTABLISH CONNECTION
                  <ArrowUpRight size={18} />
                </>
              )}
            </button>
          </form>

          <div className="mt-16 pt-10 border-t border-zinc-50 text-center">
            <p className="text-[10px] font-black text-zinc-300 uppercase tracking-[0.2em]">
              NEW OPERATIVE?{" "}
              <Link to="/register" className="text-black hover:underline font-black ml-4 decoration-2 underline-offset-8">
                REQUEST INDUCTION
              </Link>
            </p>
          </div>
        </div>

        <div className="mt-12 flex items-center justify-center gap-8 text-[9px] font-black text-zinc-200 uppercase tracking-[0.4em]">
          <div className="flex items-center gap-2">
            <Shield size={12} />
            <span>SECURE ACCESS</span>
          </div>
          <div className="w-1 h-1 bg-zinc-100" />
          <span>CORE NODE 01</span>
        </div>
      </motion.div>
    </div>
  );
};

export default AuthPage;
