import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Bell, Menu, X, Search, Settings } from "lucide-react";

interface TopbarProps {
  onMenuClick: () => void;
  isSidebarOpen: boolean;
}

const Topbar: React.FC<TopbarProps> = ({ onMenuClick, isSidebarOpen }) => {
  const navigate = useNavigate();
  const [searchFocused, setSearchFocused] = useState(false);

  return (
    <div className="flex items-center justify-between px-4 sm:px-6 py-3 bg-white border-b border-slate-200">
      {/* Left Section */}
      <div className="flex items-center gap-4 flex-1">
        {/* Mobile Menu */}
        <button
          onClick={onMenuClick}
          className="lg:hidden p-2 rounded-lg hover:bg-slate-100 transition-colors"
          aria-label={isSidebarOpen ? "Close menu" : "Open menu"}
        >
          {isSidebarOpen ? <X size={20} /> : <Menu size={20} />}
        </button>

        {/* Welcome */}
        <div className="hidden md:block">
          <h1 className="text-lg font-bold text-slate-900">
            Welcome back, <span className="text-blue-600">Ashok</span> ðŸ‘‹
          </h1>
          <p className="text-xs text-slate-500">
            {new Date().toLocaleDateString("en-US", {
              weekday: "long",
              month: "short",
              day: "numeric",
            })}
          </p>
        </div>

        {/* Search */}
        <div
          className={`
          hidden sm:flex items-center gap-2 px-4 py-2 rounded-lg border ml-auto lg:ml-6 max-w-md flex-1
          transition-all duration-200
          ${searchFocused
              ? "border-blue-400 bg-blue-50/50"
              : "border-slate-200 bg-slate-50"
            }
        `}
        >
          <Search
            size={18}
            className={searchFocused ? "text-blue-600" : "text-slate-400"}
          />
          <input
            type="text"
            placeholder="Search..."
            onFocus={() => setSearchFocused(true)}
            onBlur={() => setSearchFocused(false)}
            className="flex-1 bg-transparent text-sm text-slate-700 placeholder-slate-400 focus:outline-none"
          />
        </div>
      </div>

      {/* Right Section */}
      <div className="flex items-center gap-2">
        {/* Settings */}
        <button
          onClick={() => navigate("/settings")}
          className="hidden sm:flex p-2 rounded-lg hover:bg-slate-100 transition-colors"
          aria-label="Settings"
        >
          <Settings size={18} className="text-slate-600" />
        </button>

        {/* Notifications */}
        <button
          className="relative p-2 rounded-lg hover:bg-slate-100 transition-colors"
          aria-label="Notifications"
        >
          <Bell size={18} className="text-slate-600" />
          <span className="absolute top-1.5 right-1.5 w-2 h-2 bg-red-500 rounded-full"></span>
        </button>

        {/* Profile */}
        <button
          onClick={() => navigate("/profile")}
          className="flex items-center gap-2 pl-2 pr-3 py-1.5 rounded-lg hover:bg-slate-100 transition-colors"
        >
          <div className="w-8 h-8 rounded-lg bg-gradient-to-br from-blue-500 to-purple-600 flex items-center justify-center text-white font-bold text-sm">
            A
          </div>
          <div className="hidden lg:block text-left">
            <p className="text-sm font-semibold text-slate-900">Ashok</p>
            <p className="text-xs text-slate-500">Admin</p>
          </div>
        </button>
      </div>
    </div>
  );
};

export default Topbar;
