import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  Calendar,
  Clock,
  Plus,
  Trash2,
  ExternalLink,
  Users,
  Video,
  History,
  AlertCircle,
  MapPin,
  ChevronRight,
  ArrowRight,
  Layers,
  Zap,
  Shield,
  Star,
  Activity
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { meetingService, Meeting } from "../../services/meetingService";
import { webSocketService, WebSocketMessage } from "../../services/websocketService";

const MeetingsPage: React.FC = () => {
  const navigate = useNavigate();
  const [meetings, setMeetings] = useState<Meeting[]>([]);
  const [loading, setLoading] = useState(true);
  const [currentTime, setCurrentTime] = useState(new Date());

  const fetchMeetings = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const data = await meetingService.getAllMeetings();
      setMeetings(data);
    } catch (error) {
      console.error("Failed to fetch meetings", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    fetchMeetings();
    const unsubscribe = webSocketService.subscribe((message: WebSocketMessage) => {
      if (message.type === 'meeting') {
        fetchMeetings(false);
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);

  const handleDelete = async (id: number) => {
    if (window.confirm("Are you sure you want to delete this meeting?")) {
      try {
        await meetingService.deleteMeeting(id);
        setMeetings(prev => prev.filter(m => m.id !== id));
      } catch (error) {
        console.error("Failed to delete meeting", error);
      }
    }
  };

  const getStatus = (start: string, end: string) => {
    const now = currentTime;
    const startTime = new Date(start);
    const endTime = new Date(end);
    if (now >= startTime && now <= endTime) return 'present';
    if (now > endTime) return 'past';
    return 'upcoming';
  };

  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    });
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString([], { weekday: 'long', month: 'long', day: 'numeric' });
  };

  const isToday = (dateString: string) => {
    const d = new Date(dateString);
    const today = new Date();
    return d.getFullYear() === today.getFullYear() &&
      d.getMonth() === today.getMonth() &&
      d.getDate() === today.getDate();
  };

  const presentMeetings = meetings.filter(m => getStatus(m.start_time, m.end_time) === 'present');
  const upcomingMeetings = meetings.filter(m => {
    return getStatus(m.start_time, m.end_time) === 'upcoming' && isToday(m.start_time);
  }).sort((a, b) => new Date(a.start_time).getTime() - new Date(b.start_time).getTime());

  const pastMeetings = meetings.filter(m => {
    if (getStatus(m.start_time, m.end_time) !== 'past') return false;
    const endTime = new Date(m.end_time);
    const fortyEightHoursAgo = new Date(currentTime.getTime() - (48 * 60 * 60 * 1000));
    return endTime > fortyEightHoursAgo;
  }).sort((a, b) => new Date(b.start_time).getTime() - new Date(a.start_time).getTime());

  const currentOrNext = presentMeetings.length > 0 ? presentMeetings[0] : (upcomingMeetings.length > 0 ? upcomingMeetings[0] : null);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1, transition: { staggerChildren: 0.1 } }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 15 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <div className="min-h-screen bg-[#fcfdfe] text-slate-900 font-sans">
      <div className="max-w-[1200px] mx-auto px-6 py-10 lg:py-12 relative z-10">

        {/* Header */}
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-6 mb-12">
          <div>
            <h1 className="text-3xl font-bold tracking-tight text-slate-900 mb-1">Meetings</h1>
            <p className="text-slate-500 font-medium">Coordinate and synchronize with your team efficiently.</p>
          </div>

          <div className="flex items-center gap-3">
            <button
              onClick={() => navigate("/calendar")}
              className="px-5 py-2.5 bg-white border border-slate-200 text-slate-700 rounded-xl font-semibold hover:bg-slate-50 transition-all shadow-sm"
            >
              Calendar View
            </button>
            <button
              onClick={() => navigate("/meetings/schedule")}
              className="flex items-center justify-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white px-6 py-3 rounded-xl font-semibold transition-all shadow-lg shadow-indigo-100 active:scale-95"
            >
              <Plus size={18} />
              <span>Schedule Sync</span>
            </button>
          </div>
        </div>

        {loading ? (
          <div className="py-20 text-center">
            <Loader2 className="w-10 h-10 text-indigo-600 animate-spin mx-auto mb-4" />
            <p className="text-sm font-medium text-slate-400">Updating schedule...</p>
          </div>
        ) : (
          <motion.div
            variants={containerVariants}
            initial="hidden"
            animate="visible"
            className="grid grid-cols-1 lg:grid-cols-3 gap-10"
          >
            {/* Left/Middle Column: Main Feed */}
            <div className="lg:col-span-2 space-y-10">
              {/* Highlight Card */}
              {currentOrNext && (
                <div className="bg-indigo-600 rounded-3xl p-8 lg:p-10 text-white shadow-xl shadow-indigo-100 relative overflow-hidden group">
                  <div className="absolute top-0 right-0 w-64 h-64 bg-white/10 rounded-full blur-3xl -mr-32 -mt-32" />

                  <div className="relative z-10">
                    <div className="flex items-center gap-3 mb-6">
                      <div className="px-3 py-1 bg-white/10 backdrop-blur-md rounded-lg text-[10px] font-bold uppercase tracking-wider">
                        {presentMeetings.length > 0 ? "NOW IN SESSION" : "UP NEXT"}
                      </div>
                      <span className="text-indigo-100 text-xs font-semibold">{formatTime(currentOrNext.start_time)}</span>
                    </div>

                    <h2 className="text-3xl font-bold mb-4">{currentOrNext.title}</h2>

                    <div className="flex flex-wrap items-center gap-6 mb-8">
                      <div className="flex items-center gap-2">
                        <Users size={16} className="text-indigo-200" />
                        <span className="text-sm font-medium">{currentOrNext.participants?.length || 0} participants</span>
                      </div>
                      {currentOrNext.location && (
                        <div className="flex items-center gap-2">
                          <MapPin size={16} className="text-indigo-200" />
                          <span className="text-sm font-medium">{currentOrNext.location}</span>
                        </div>
                      )}
                    </div>

                    <div className="flex items-center gap-4">
                      {currentOrNext.meeting_link && (
                        <button
                          onClick={() => window.open(currentOrNext.meeting_link, '_blank')}
                          className="px-6 py-3 bg-white text-indigo-600 rounded-xl font-bold text-sm shadow-lg hover:bg-slate-50 transition-all flex items-center gap-2"
                        >
                          Join Now
                        </button>
                      )}
                    </div>
                  </div>
                </div>
              )}

              {/* Timeline Section */}
              <div className="space-y-6">
                <h3 className="text-lg font-bold text-slate-900 border-b border-slate-50 pb-4">Today's Timeline</h3>
                {upcomingMeetings.length > 0 ? (
                  <div className="space-y-4">
                    {upcomingMeetings.map((m) => (
                      <div key={m.id} className="bg-white p-6 rounded-2xl border border-slate-100 shadow-sm flex items-center gap-6 group">
                        <div className="w-20 shrink-0 text-center border-r border-slate-50 py-2">
                          <p className="text-xs font-bold text-indigo-600">{formatTime(m.start_time)}</p>
                        </div>
                        <div className="flex-1 min-w-0">
                          <h4 className="font-bold text-slate-900 mb-1 group-hover:text-indigo-600 transition-colors uppercase-case">{m.title}</h4>
                          <p className="text-xs text-slate-400 font-medium">{m.participants?.length || 0} participants â€¢ {m.location || "Online"}</p>
                        </div>
                        <div className="flex items-center gap-2">
                          {m.meeting_link && (
                            <button
                              onClick={() => window.open(m.meeting_link, '_blank')}
                              className="p-2 text-slate-400 hover:text-indigo-600 hover:bg-indigo-50 rounded-lg transition-all"
                            >
                              <ExternalLink size={18} />
                            </button>
                          )}
                          <button
                            onClick={() => handleDelete(m.id)}
                            className="p-2 text-slate-400 hover:text-rose-600 hover:bg-rose-50 rounded-lg transition-all"
                          >
                            <Trash2 size={18} />
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="py-12 bg-slate-50/50 rounded-2xl border-2 border-dashed border-slate-100 text-center">
                    <p className="text-sm font-medium text-slate-400">No more meetings scheduled for today.</p>
                  </div>
                )}
              </div>
            </div>

            {/* Right Column: Side Info */}
            <div className="space-y-10">
              {/* Analytics Summary */}
              <div className="bg-slate-900 text-white rounded-3xl p-8 shadow-xl">
                <h3 className="text-sm font-bold uppercase tracking-widest text-slate-400 mb-6">Schedule Overview</h3>
                <div className="grid grid-cols-2 gap-4 mb-8">
                  <div className="bg-white/5 rounded-2xl p-4 border border-white/5">
                    <p className="text-2xl font-bold">{meetings.length}</p>
                    <p className="text-[10px] font-bold text-slate-500 uppercase tracking-widest mt-1">Total Logs</p>
                  </div>
                  <div className="bg-white/5 rounded-2xl p-4 border border-white/5">
                    <p className="text-2xl font-bold">12h</p>
                    <p className="text-[10px] font-bold text-slate-500 uppercase tracking-widest mt-1">Airtime</p>
                  </div>
                </div>
                <div className="space-y-4">
                  <div className="flex items-center justify-between text-xs font-semibold mb-2">
                    <span className="text-slate-400">Monthly Sync Progress</span>
                    <span className="text-indigo-400">72%</span>
                  </div>
                  <div className="w-full h-1.5 bg-white/5 rounded-full overflow-hidden">
                    <div className="w-[72%] h-full bg-indigo-500 rounded-full" />
                  </div>
                </div>
              </div>

              {/* History Section */}
              <div className="space-y-6">
                <h3 className="text-lg font-bold text-slate-900">Recent History</h3>
                <div className="space-y-3">
                  {pastMeetings.slice(0, 4).map(m => (
                    <div key={m.id} className="p-4 bg-white border border-slate-50 rounded-xl flex items-center justify-between hover:shadow-sm transition-all group">
                      <div className="flex items-center gap-4">
                        <div className="w-10 h-10 bg-slate-50 rounded-lg flex items-center justify-center text-slate-400 group-hover:text-slate-600 transition-colors">
                          <History size={16} />
                        </div>
                        <div>
                          <h4 className="text-sm font-bold text-slate-700 truncate max-w-[120px]">{m.title}</h4>
                          <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">{formatDate(m.start_time).split(',')[0]}</p>
                        </div>
                      </div>
                      <button onClick={() => handleDelete(m.id)} className="p-2 text-slate-200 hover:text-rose-500 transition-colors">
                        <Trash2 size={16} />
                      </button>
                    </div>
                  ))}
                  {pastMeetings.length === 0 && <p className="text-sm text-center text-slate-400 py-4">No recent history.</p>}
                </div>
              </div>
            </div>
          </motion.div>
        )}
      </div>
    </div>
  );
};

const Loader2 = ({ className }: { className?: string }) => (
  <svg className={className} xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
    <path d="M21 12a9 9 0 1 1-6.219-8.56" />
  </svg>
);

export default MeetingsPage;
