import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
    ChevronLeft,
    Calendar,
    User,
    AlignLeft,
    Save,
    Type,
    Loader2
} from "lucide-react";
import { taskService } from "../../services/taskService";
import { userService } from "../../services/userService";

const AddTaskPage: React.FC = () => {
    const navigate = useNavigate();
    const { id } = useParams<{ id: string }>();
    const isEditing = Boolean(id);

    const [users, setUsers] = useState<any[]>([]);
    const [submitting, setSubmitting] = useState(false);
    const [loading, setLoading] = useState(isEditing);

    const [formData, setFormData] = useState({
        title: "",
        description: "",
        status: "Upcoming", // Default, hidden
        priority: "Normal", // Default, hidden
        assigned_to: "",
        due_date: new Date().toISOString().split('T')[0]
    });

    useEffect(() => {
        const loadData = async () => {
            try {
                const [usersData, taskData] = await Promise.all([
                    userService.getAllUsers(),
                    isEditing && id ? taskService.getTaskById(Number(id)) : Promise.resolve(null)
                ]);

                setUsers(usersData);

                if (taskData) {
                    setFormData({
                        title: taskData.title,
                        description: taskData.description || "",
                        status: taskData.status === "todo" ? "Upcoming" : taskData.status === "in_progress" ? "In Progress" : taskData.status === "done" ? "Completed" : taskData.status,
                        priority: taskData.priority,
                        assigned_to: taskData.assignee_id ? String(taskData.assignee_id) : "",
                        due_date: taskData.due_date ? taskData.due_date.split("T")[0] : new Date().toISOString().split('T')[0]
                    });
                }
            } catch (e) {
                console.error("Failed to load data", e);
                // navigate("/tasks"); // Optional: redirect on error
            } finally {
                setLoading(false);
            }
        };
        loadData();
    }, [id, isEditing, navigate]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setSubmitting(true);
        try {
            const payload = {
                ...formData,
                status: formData.status as any,
                priority: formData.priority as any,
                assigned_to: formData.assigned_to ? Number(formData.assigned_to) : undefined
            };

            if (isEditing && id) {
                await taskService.updateTask(Number(id), payload);
            } else {
                await taskService.createTask(payload);
            }
            navigate("/tasks");
        } catch (error) {
            console.error(error);
            alert(`Failed to ${isEditing ? 'update' : 'create'} task`);
            setSubmitting(false);
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-slate-50 flex items-center justify-center">
                <Loader2 className="w-8 h-8 text-indigo-600 animate-spin" />
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-white flex flex-col">
            {/* Minimal Header */}
            <div className="px-6 py-6 max-w-4xl mx-auto w-full flex items-center justify-between">
                <button
                    onClick={() => navigate(-1)}
                    className="flex items-center gap-2 text-slate-500 hover:text-slate-800 transition-colors font-medium"
                >
                    <ChevronLeft size={20} />
                    Back
                </button>
                <h1 className="text-lg font-bold text-slate-900">{isEditing ? 'Edit Task' : 'New Task'}</h1>
                <div className="w-16"></div> {/* Spacer for centering */}
            </div>

            {/* "Long Bar" Type Form */}
            <div className="flex-1 max-w-3xl mx-auto w-full p-6 animate-in fade-in duration-500">
                <form onSubmit={handleSubmit} className="space-y-8">

                    {/* Title Input - Large & Clean */}
                    <div className="relative group">
                        <div className="absolute left-0 top-3 text-slate-400 group-focus-within:text-indigo-600 transition-colors">
                            <Type size={24} />
                        </div>
                        <input
                            type="text"
                            value={formData.title}
                            onChange={e => setFormData({ ...formData, title: e.target.value })}
                            placeholder="Task Title"
                            required
                            className="w-full pl-10 pr-4 py-2 text-3xl font-bold text-slate-800 placeholder:text-slate-300 border-b-2 border-slate-100 focus:border-indigo-600 outline-none transition-all bg-transparent"
                            autoFocus
                        />
                    </div>

                    {/* Metadata Bars */}
                    <div className="space-y-4">

                        {/* Assignee Bar */}
                        <div className="flex items-center gap-4 p-4 bg-slate-50 rounded-xl border border-slate-100 focus-within:ring-2 focus-within:ring-indigo-100 transition-all hover:bg-slate-100/80">
                            <div className="w-10 h-10 rounded-full bg-white flex items-center justify-center text-slate-400 shadow-sm">
                                <User size={20} />
                            </div>
                            <div className="flex-1">
                                <label className="block text-xs font-bold text-slate-500 uppercase tracking-wide mb-0.5">Assigned To</label>
                                <select
                                    value={formData.assigned_to}
                                    onChange={e => setFormData({ ...formData, assigned_to: e.target.value })}
                                    className="w-full bg-transparent font-medium text-slate-800 outline-none cursor-pointer"
                                >
                                    <option value="">Unassigned</option>
                                    {users.map(u => (
                                        <option key={u.id} value={u.id}>{u.name || u.email} {u.role && `(${u.role})`}</option>
                                    ))}
                                </select>
                            </div>
                        </div>

                        {/* Due Date Bar */}
                        <div className="flex items-center gap-4 p-4 bg-slate-50 rounded-xl border border-slate-100 focus-within:ring-2 focus-within:ring-indigo-100 transition-all hover:bg-slate-100/80">
                            <div className="w-10 h-10 rounded-full bg-white flex items-center justify-center text-slate-400 shadow-sm">
                                <Calendar size={20} />
                            </div>
                            <div className="flex-1">
                                <label className="block text-xs font-bold text-slate-500 uppercase tracking-wide mb-0.5">Due Date</label>
                                <input
                                    type="date"
                                    value={formData.due_date}
                                    onChange={e => setFormData({ ...formData, due_date: e.target.value })}
                                    className="w-full bg-transparent font-medium text-slate-800 outline-none"
                                />
                            </div>
                        </div>

                    </div>

                    {/* Description Area */}
                    <div className="pt-4">
                        <label className="flex items-center gap-2 text-sm font-bold text-slate-500 uppercase tracking-wider mb-4">
                            <AlignLeft size={16} /> Description
                        </label>
                        <textarea
                            value={formData.description}
                            onChange={e => setFormData({ ...formData, description: e.target.value })}
                            placeholder="Add detailed description, notes, or requirements..."
                            rows={10}
                            className="w-full p-4 bg-slate-50 rounded-xl border border-slate-100 focus:bg-white focus:ring-2 focus:ring-indigo-100 focus:border-indigo-200 outline-none transition-all resize-none text-slate-600 leading-relaxed"
                        />
                    </div>

                    {/* Action Bar */}
                    <div className="pt-6 border-t border-slate-100 flex items-center justify-end gap-4">
                        <button
                            type="button"
                            onClick={() => navigate(-1)}
                            className="px-6 py-3 text-slate-500 font-semibold hover:bg-slate-50 hover:text-slate-700 rounded-xl transition"
                        >
                            Cancel
                        </button>
                        <button
                            type="submit"
                            disabled={submitting}
                            className="flex items-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white px-8 py-3 rounded-xl font-bold shadow-lg shadow-indigo-200 hover:shadow-xl hover:-translate-y-0.5 transition-all active:scale-95 disabled:opacity-70 disabled:cursor-not-allowed"
                        >
                            {submitting ? <Loader2 size={20} className="animate-spin" /> : <Save size={20} />}
                            {isEditing ? 'Update Task' : 'Create Task'}
                        </button>
                    </div>

                </form>
            </div>
        </div>
    );
};

export default AddTaskPage;
