import React, { useEffect, useState, useRef } from "react";
import { Menu, Search, Bell, Sparkles, ChevronDown } from "lucide-react";
import { useNavigate } from "react-router-dom";
import { authService } from "../services/authService";
import { User } from "../services/userService";
import { useNotifications } from "../context/NotificationContext";
import { AnimatePresence, motion } from "framer-motion";

interface TopbarProps {
  onMenuClick: () => void;
  isSidebarOpen: boolean;
}

const Topbar: React.FC<TopbarProps> = ({ onMenuClick, isSidebarOpen }) => {
  const navigate = useNavigate();
  const [user, setUser] = useState<User | null>(null);
  const { notifications, unreadCount, markAsRead, markAllAsRead } = useNotifications();
  const [showNotifications, setShowNotifications] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const currentUser = await authService.getCurrentUser();
        setUser(currentUser);
      } catch (error) {
        console.error("Failed to fetch user", error);
      }
    };
    fetchUser();
  }, []);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setShowNotifications(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const getInitials = (name: string) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase()
      .substring(0, 2);
  };

  return (
    <header className="sticky top-0 z-30 w-full bg-white/80 backdrop-blur-md border-b border-slate-100 flex items-center justify-between px-8 py-4">
      {/* Search Section */}
      <div className="flex items-center gap-6 flex-1">
        <button
          onClick={onMenuClick}
          className="lg:hidden p-2 text-slate-600 hover:bg-slate-50 rounded-xl transition-all"
        >
          <Menu size={20} />
        </button>

        <div className="hidden sm:flex items-center w-full max-w-sm bg-slate-50 border border-slate-100 px-4 py-2.5 rounded-xl focus-within:bg-white focus-within:border-indigo-500 focus-within:ring-4 focus-within:ring-indigo-500/5 transition-all duration-200 group">
          <Search size={18} className="text-slate-400 mr-3 group-focus-within:text-indigo-600 transition-colors" />
          <input
            type="text"
            placeholder="Search anything..."
            className="bg-transparent border-none outline-none text-sm font-medium w-full text-slate-900 placeholder:text-slate-400"
          />
        </div>
      </div>

      {/* Actions Section */}
      <div className="flex items-center gap-6">
        {/* Pro Badge */}
        <div className="hidden md:flex items-center gap-2 px-3 py-1.5 bg-indigo-50 text-indigo-600 text-[11px] font-bold uppercase tracking-wider rounded-lg border border-indigo-100">
          <Sparkles size={12} />
          Pro Member
        </div>

        {/* Notifications */}
        <div className="relative" ref={dropdownRef}>
          <button
            onClick={() => setShowNotifications(!showNotifications)}
            className={`relative p-2.5 rounded-xl transition-all duration-200 ${showNotifications ? "bg-indigo-50 text-indigo-600" : "text-slate-400 hover:bg-slate-50 hover:text-slate-900"
              }`}
          >
            <Bell size={20} />
            {unreadCount > 0 && (
              <span className="absolute top-2 right-2.5 w-2 h-2 bg-indigo-600 border-2 border-white rounded-full" />
            )}
          </button>

          <AnimatePresence>
            {showNotifications && (
              <motion.div
                initial={{ opacity: 0, y: 10, scale: 0.95 }}
                animate={{ opacity: 1, y: 0, scale: 1 }}
                exit={{ opacity: 0, y: 10, scale: 0.95 }}
                className="absolute right-0 mt-3 w-80 bg-white border border-slate-100 shadow-2xl rounded-[24px] z-50 overflow-hidden"
              >
                <div className="p-5 border-b border-slate-50 flex justify-between items-center bg-slate-50/50">
                  <h3 className="text-[11px] font-bold uppercase tracking-wider text-slate-900">Notifications</h3>
                  {unreadCount > 0 && (
                    <button
                      onClick={() => markAllAsRead()}
                      className="text-[10px] font-bold text-indigo-600 hover:text-indigo-700 underline underline-offset-4"
                    >
                      Clear All
                    </button>
                  )}
                </div>
                <div className="max-h-[400px] overflow-y-auto custom-scrollbar">
                  {notifications.length > 0 ? (
                    notifications.map((notif) => (
                      <div
                        key={notif.id}
                        onClick={() => markAsRead(notif.id)}
                        className={`p-5 border-b border-slate-50 cursor-pointer transition-colors flex gap-4 items-start ${!notif.is_read ? "bg-indigo-50/30" : "hover:bg-slate-50"
                          }`}
                      >
                        <div
                          className={`w-2 h-2 mt-2 shrink-0 rounded-full ${!notif.is_read ? "bg-indigo-600 shadow-lg shadow-indigo-100" : "bg-transparent"
                            }`}
                        />
                        <div className="flex-1 min-w-0">
                          <p className="text-[13px] font-bold text-slate-900 leading-tight mb-1">
                            {notif.title}
                          </p>
                          <p className="text-[12px] text-slate-500 font-medium leading-relaxed line-clamp-2">
                            {notif.description}
                          </p>
                          <p className="text-[10px] font-semibold text-slate-400 mt-2">
                            {new Date(notif.created_at).toLocaleTimeString([], {
                              hour: "2-digit",
                              minute: "2-digit",
                            })}
                          </p>
                        </div>
                      </div>
                    ))
                  ) : (
                    <div className="py-16 text-center">
                      <div className="w-12 h-12 bg-slate-50 rounded-2xl flex items-center justify-center mx-auto mb-4">
                        <Bell className="text-slate-300" size={24} />
                      </div>
                      <h4 className="text-sm font-bold text-slate-900">No new updates</h4>
                      <p className="text-xs text-slate-400 font-medium mt-1">You're all caught up!</p>
                    </div>
                  )}
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>

        {/* Profile */}
        <button
          onClick={() => navigate("/profile")}
          className="flex items-center gap-3 p-1 transition-all duration-200 bg-slate-50 hover:bg-slate-100 rounded-2xl border border-slate-100 group"
        >
          <div className="w-9 h-9 bg-indigo-600 rounded-xl flex items-center justify-center text-white font-bold text-xs shadow-lg shadow-indigo-100 group-hover:scale-105 transition-transform">
            {user?.name ? getInitials(user.name) : "U"}
          </div>
          <div className="hidden lg:block text-left pr-2">
            <p className="text-xs font-bold text-slate-900 leading-none mb-1">
              {user?.name || "User"}
            </p>
            <p className="text-[10px] font-semibold text-slate-400 uppercase tracking-wider">
              {user?.role || "Member"}
            </p>
          </div>
          <ChevronDown size={14} className="text-slate-400 mr-1" />
        </button>
      </div>
    </header>
  );
};

export default Topbar;
