import React from "react";
import { Github, Linkedin, Twitter, Mail, ShieldCheck, Heart, Globe } from "lucide-react";
import { motion } from "framer-motion";

const Footer: React.FC = () => {
  const year = new Date().getFullYear();

  const socialLinks = [
    { icon: Github, url: "https://github.com/", label: "GitHub" },
    { icon: Twitter, url: "https://twitter.com/", label: "Twitter" },
    { icon: Globe, url: "#", label: "Website" },
  ];

  const sections = [
    {
      title: "Product",
      links: [
        { name: "Features", url: "/features" },
        { name: "Pricing", url: "#" },
        { name: "API Docs", url: "/docs" },
      ],
    },
    {
      title: "Company",
      links: [
        { name: "About", url: "/about" },
        { name: "Blog", url: "#" },
        { name: "Support", url: "/support" },
      ],
    },
    {
      title: "Legal",
      links: [
        { name: "Term of Service", url: "#" },
        { name: "Privacy Policy", url: "#" },
      ],
    },
  ];

  return (
    <footer className="w-full bg-[#f7f7f8] border-t border-[#e2e2e4] px-8 py-16 sm:px-12 flex flex-col items-center">
      <div className="max-w-7xl w-full grid grid-cols-1 md:grid-cols-4 lg:grid-cols-5 gap-12 lg:gap-16">
        {/* Brand */}
        <div className="md:col-span-1 lg:col-span-2 space-y-6">
          <div className="flex items-center gap-2.5">
            <div className="w-6 h-6 bg-[#111113] rounded flex items-center justify-center">
              <img src="/logo1.png" alt="Logo" className="w-4 h-4 invert" />
            </div>
            <h2 className="text-sm font-bold text-[#111113]">Collabryta</h2>
          </div>
          <p className="text-xs font-medium text-slate-500 leading-relaxed max-w-[240px]">
            The workspace for modern teams. Built for high-performance and precision.
          </p>
          <div className="flex items-center gap-4">
            {socialLinks.map(({ icon: Icon, url, label }) => (
              <a
                key={label}
                href={url}
                className="text-slate-400 hover:text-[#5e6ad2] transition-colors"
              >
                <Icon size={16} />
              </a>
            ))}
          </div>
        </div>

        {/* Links */}
        {sections.map((section) => (
          <div key={section.title} className="space-y-4">
            <h3 className="text-[10px] font-black uppercase tracking-widest text-[#111113]">{section.title}</h3>
            <ul className="space-y-3">
              {section.links.map((link) => (
                <li key={link.name}>
                  <a
                    href={link.url}
                    className="text-xs font-medium text-slate-500 hover:text-[#5e6ad2] transition-colors"
                  >
                    {link.name}
                  </a>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>

      <div className="max-w-7xl w-full mt-16 pt-8 border-t border-[#e2e2e4]/60 flex flex-col md:flex-row justify-between items-center gap-6 text-[10px] font-bold text-slate-400 uppercase tracking-widest">
        <div className="flex items-center gap-1.5 hover:text-[#111113] cursor-help">
          <ShieldCheck size={12} className="text-emerald-500" />
          <span>Verified Security</span>
        </div>

        <p>Â© {year} Collabryta System. All sectors operational.</p>

        <div className="flex items-center gap-1.5">
          <span>Made with Heart</span>
          <Heart size={10} className="text-rose-500 fill-rose-500" />
        </div>
      </div>
    </footer>
  );
};

export default Footer;
