import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import {
    User,
    Bell,
    ShieldCheck,
    Save,
    Camera,
    HardDrive,
    Zap,
    Loader2,
    CheckCircle2,
    Eye,
    Smartphone,
    CreditCard,
    Database,
    Activity,
    HelpCircle
} from "lucide-react";
import { authService } from "../../services/authService";

const SettingsPage: React.FC = () => {
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [success, setSuccess] = useState(false);
    const [settings, setSettings] = useState<any>(null);

    useEffect(() => {
        const fetchSettings = async () => {
            try {
                const data = await authService.getCurrentUser();
                setSettings(data);
            } catch (error) {
                console.error("Failed to load settings", error);
            } finally {
                setLoading(false);
            }
        };
        fetchSettings();
    }, []);

    const handleUpdate = (field: string, value: any) => {
        setSettings((prev: any) => ({ ...prev, [field]: value }));
    };

    const handleSave = async () => {
        try {
            setSaving(true);
            setSuccess(false);
            const { id, email, hashed_password, last_seen, status, ...updateData } = settings;
            await authService.updateProfile(updateData);
            setSuccess(true);
            setTimeout(() => setSuccess(false), 3000);
        } catch (error) {
            console.error("Failed to save settings", error);
        } finally {
            setSaving(false);
        }
    };

    if (loading || !settings) {
        return (
            <div className="min-h-screen flex items-center justify-center bg-slate-50/50">
                <Loader2 className="w-10 h-10 text-indigo-600 animate-spin" />
            </div>
        );
    }

    const Section = ({ icon: Icon, title, desc, children }: any) => (
        <motion.section
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            className="bg-white rounded-[2.5rem] p-4 border border-slate-100 shadow-sm overflow-hidden mb-8"
        >
            <div className="flex items-center gap-5 p-4 md:p-6 mb-4">
                <div className="p-3 bg-indigo-50 text-indigo-600 rounded-2xl shadow-inner">
                    <Icon size={24} />
                </div>
                <div>
                    <h2 className="text-2xl font-black text-slate-900 tracking-tight leading-none">{title}</h2>
                    <p className="text-sm font-bold text-slate-400 mt-1 uppercase tracking-widest">{desc}</p>
                </div>
            </div>
            <div className="px-4 md:px-8 pb-8">{children}</div>
        </motion.section>
    );

    const Toggle = ({ active, label, subtitle, field }: any) => (
        <div className="flex items-center justify-between py-6 first:pt-0 border-b border-slate-50 last:border-0">
            <div>
                <p className="text-base font-bold text-slate-800">{label}</p>
                <p className="text-sm font-medium text-slate-500">{subtitle}</p>
            </div>
            <button
                onClick={() => handleUpdate(field, !active)}
                className={`relative inline-flex h-7 w-14 items-center rounded-full transition-all duration-300 focus:outline-none ring-offset-2 focus:ring-2 focus:ring-indigo-500/20 ${active ? 'bg-indigo-600' : 'bg-slate-200'}`}
            >
                <span className={`inline-block h-5 w-5 transform rounded-full bg-white shadow-md transition-transform duration-300 ${active ? 'translate-x-8' : 'translate-x-1'}`} />
            </button>
        </div>
    );

    return (
        <div className="max-w-4xl mx-auto p-6 space-y-12 pb-32">
            {/* Ultra Premium Header */}
            <div className="relative overflow-hidden rounded-[3rem] p-10 bg-black text-white shadow-2xl shadow-indigo-500/10">
                <div className="absolute top-0 right-0 w-96 h-96 bg-indigo-600/20 rounded-full blur-[100px] -mr-32 -mt-32" />
                <div className="relative z-10 flex flex-col md:flex-row md:items-center justify-between gap-8">
                    <div>
                        <div className="inline-flex items-center gap-2 px-3 py-1 bg-indigo-500/20 rounded-full mb-4">
                            <Activity size={14} className="text-indigo-400" />
                            <span className="text-[10px] font-black uppercase tracking-widest text-indigo-300">Workspace Control</span>
                        </div>
                        <h1 className="text-5xl font-black tracking-tighter mb-2 italic">Settings.</h1>
                        <p className="text-indigo-200/60 font-medium">Fine-tune your environment, security, and neural preferences.</p>
                    </div>
                    <button
                        onClick={handleSave}
                        disabled={saving}
                        className={`group relative flex items-center justify-center gap-3 px-10 py-5 rounded-[2rem] font-black text-sm transition-all shadow-2xl active:scale-95 overflow-hidden ${success ? 'bg-green-500' : 'bg-white text-black hover:bg-slate-100'
                            }`}
                    >
                        {saving ? <Loader2 size={20} className="animate-spin" /> : success ? <CheckCircle2 size={20} /> : <Save size={20} className="group-hover:translate-y-[-2px] transition-transform" />}
                        <span className="relative z-10">{saving ? 'Synchronizing...' : success ? 'Successfully Saved' : 'Commit Changes'}</span>
                    </button>
                </div>
            </div>

            {/* Public Identity Section */}
            <Section icon={User} title="Public Identity" desc="How the workspace sees you">
                <div className="space-y-10">
                    <div className="flex flex-col md:flex-row items-center gap-8 bg-slate-50/50 p-6 rounded-[2rem] border border-slate-100">
                        <div className="relative group">
                            <div className="w-32 h-32 rounded-[2.5rem] bg-white p-1 border border-slate-200 shadow-xl overflow-hidden ring-4 ring-indigo-50 transition-transform duration-500 group-hover:scale-105">
                                {settings.avatar ? <img src={settings.avatar} alt="User Avatar" className="w-full h-full object-cover rounded-[2rem]" /> : <div className="w-full h-full flex items-center justify-center bg-indigo-50 text-indigo-600 text-4xl font-black">{settings.name?.charAt(0)}</div>}
                            </div>
                            <button className="absolute -bottom-2 -right-2 p-3 bg-black text-white rounded-2xl shadow-xl transition-all hover:bg-indigo-600 hover:scale-110 active:scale-90">
                                <Camera size={16} />
                            </button>
                        </div>
                        <div className="text-center md:text-left space-y-1">
                            <h3 className="text-xl font-bold text-slate-800">Workspace Avatar</h3>
                            <p className="text-sm font-medium text-slate-500 max-w-sm">Use a professional photo to build trust with your team and partners.</p>
                            <div className="pt-3 flex gap-4 justify-center md:justify-start">
                                <button className="text-xs font-black text-indigo-600 hover:underline px-2">Update Photo</button>
                                <button className="text-xs font-black text-slate-400 hover:text-red-500 px-2 transition-colors">Reset</button>
                            </div>
                        </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                        <div className="space-y-2">
                            <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-2">Display Name</label>
                            <input type="text" value={settings.name || ''} onChange={(e) => handleUpdate('name', e.target.value)} className="w-full px-6 py-4 bg-slate-50 border border-slate-100 rounded-[1.5rem] focus:ring-4 focus:ring-indigo-500/5 focus:border-indigo-500/50 outline-none font-bold text-slate-800 transition-all" />
                        </div>
                        <div className="space-y-2">
                            <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-2">Current Role</label>
                            <input type="text" value={settings.job_title || ''} onChange={(e) => handleUpdate('job_title', e.target.value)} className="w-full px-6 py-4 bg-slate-50 border border-slate-100 rounded-[1.5rem] focus:ring-4 focus:ring-indigo-500/5 focus:border-indigo-500/50 outline-none font-bold text-slate-800 transition-all" />
                        </div>
                    </div>
                </div>
            </Section>

            {/* Neural Preferences */}
            <Section icon={Zap} title="Workspace Preferences" desc="Localization & Regional Settings">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                    <div className="space-y-2">
                        <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-2">Interface Language</label>
                        <select value={settings.language} onChange={(e) => handleUpdate('language', e.target.value)} className="w-full px-6 py-4 bg-slate-50 border border-slate-100 rounded-[1.5rem] font-bold text-slate-800 outline-none cursor-pointer hover:bg-slate-100/50 transition-colors">
                            <option>English (US)</option>
                            <option>Hindi (HI)</option>
                            <option>Spanish (ES)</option>
                        </select>
                    </div>
                    <div className="space-y-2">
                        <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest ml-2">Active Timezone</label>
                        <select value={settings.timezone} onChange={(e) => handleUpdate('timezone', e.target.value)} className="w-full px-6 py-4 bg-slate-50 border border-slate-100 rounded-[1.5rem] font-bold text-slate-800 outline-none cursor-pointer hover:bg-slate-100/50 transition-colors">
                            <option value="(GMT+05:30) Mumbai, New Delhi">(GMT+05:30) India Standard Time</option>
                            <option value="(GMT-08:00) Pacific Time">(GMT-08:00) Pacific Standard Time</option>
                        </select>
                    </div>
                </div>
            </Section>

            {/* Privacy Matrix */}
            <Section icon={Eye} title="Privacy Control" desc="Visibility & Status Broadcast">
                <div className="bg-slate-50/50 px-8 rounded-[2rem] border border-slate-100">
                    <Toggle active={settings.show_online_status} label="Online Presence" subtitle="Allow team members to see when you are active" field="show_online_status" />
                    <Toggle active={settings.share_local_time} label="Share Local Time" subtitle="Displays your current time relative to others" field="share_local_time" />
                    <Toggle active={settings.anonymous_reporting} label="Anonymous Telemetry" subtitle="Help us improve with non-identifiable usage data" field="anonymous_reporting" />
                </div>
            </Section>

            {/* Notification Matrix */}
            <Section icon={Bell} title="Smart Notifications" desc="Email & System Matrix">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
                    <div className="space-y-2">
                        <h4 className="text-[10px] font-black text-indigo-600 uppercase tracking-[0.2em] mb-4">Email Dispatch</h4>
                        <Toggle active={settings.email_tasks} label="Task Assignments" subtitle="Daily email for new tasks" field="email_tasks" />
                        <Toggle active={settings.email_summary} label="Weekly Digest" subtitle="Summary of workspace activity" field="email_summary" />
                    </div>
                    <div className="space-y-2">
                        <h4 className="text-[10px] font-black text-indigo-600 uppercase tracking-[0.2em] mb-4">Push Notifications</h4>
                        <Toggle active={settings.push_meetings} label="Meeting Reminders" subtitle="10 min before start" field="push_meetings" />
                        <Toggle active={settings.push_chat} label="Direct Messages" subtitle="Instant alerts for chat" field="push_chat" />
                    </div>
                </div>
            </Section>

            {/* Security Cryptography */}
            <Section icon={ShieldCheck} title="Shield & Security" desc="Encryption & Multi-factor Auth">
                <div className={`p-8 rounded-[2rem] border transition-all duration-500 ${settings.two_factor_enabled ? 'bg-green-50/50 border-green-100' : 'bg-orange-50/50 border-orange-100'}`}>
                    <div className="flex flex-col md:flex-row items-center justify-between gap-6">
                        <div className="flex items-center gap-6 text-center md:text-left">
                            <div className={`p-4 rounded-2xl ${settings.two_factor_enabled ? 'bg-green-100 text-green-600' : 'bg-orange-100 text-orange-600'}`}>
                                <ShieldCheck size={32} />
                            </div>
                            <div>
                                <h3 className="text-xl font-bold text-slate-800">Two-Factor Authentication</h3>
                                <p className="text-sm font-medium text-slate-500 mt-1 max-w-sm">
                                    {settings.two_factor_enabled
                                        ? "Your account is robustly protected with encrypted multi-factor authentication."
                                        : "Your account is currently vulnerable to unauthorized access. Enable 2FA immediately."}
                                </p>
                            </div>
                        </div>
                        <button
                            onClick={() => handleUpdate('two_factor_enabled', !settings.two_factor_enabled)}
                            className={`px-8 py-3.5 rounded-2xl font-black text-xs uppercase tracking-widest shadow-xl transition-all active:scale-95 whitespace-nowrap ${settings.two_factor_enabled
                                ? 'bg-white text-green-600 hover:bg-green-100'
                                : 'bg-black text-white hover:bg-orange-600'
                                }`}
                        >
                            {settings.two_factor_enabled ? 'Disable Shield' : 'Activate 2FA'}
                        </button>
                    </div>
                </div>
            </Section>

            {/* Storage Architecture */}
            <Section icon={HardDrive} title="Cloud Infrastructure" desc="Data Architecture & Sync">
                <div className="space-y-8">
                    <div className="bg-slate-50/50 p-8 rounded-[2rem] border border-slate-100">
                        <div className="flex justify-between items-end mb-4">
                            <div>
                                <h4 className="text-sm font-black text-slate-800">Workspace Capacity</h4>
                                <p className="text-xs font-bold text-slate-400">Total used across all modules</p>
                            </div>
                            <p className="text-2xl font-black text-indigo-600">4.2 <span className="text-sm text-slate-400 uppercase tracking-tighter">GB / 10 GB</span></p>
                        </div>
                        <div className="w-full h-4 bg-white rounded-full overflow-hidden border border-slate-100 p-1">
                            <motion.div
                                initial={{ width: 0 }}
                                animate={{ width: '42%' }}
                                transition={{ duration: 1.5, ease: "easeOut" }}
                                className="h-full bg-indigo-600 rounded-full shadow-lg shadow-indigo-500/20"
                            />
                        </div>
                    </div>
                    <div className="flex flex-wrap gap-4">
                        <button className="flex items-center gap-2 px-6 py-3 bg-white border border-slate-200 rounded-2xl text-xs font-bold text-slate-600 hover:border-indigo-600 hover:text-indigo-600 transition-all">
                            <Database size={16} /> Export Workspace Dossier
                        </button>
