import React, { useState, useEffect } from "react";
import {
  Plus,
  Search,
  Loader2,
  Trash2,
  Edit3,
  Calendar,
  User as UserIcon,
  Filter,
  AlertCircle,
  X,
  Clock,
  CheckCircle2,
  Zap,
  ArrowRight,
  Shield,
  Layers,
  ChevronRight,
  ArrowUpRight,
  MoreVertical,
  ClipboardList
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import { taskService, Task } from "../../services/taskService";
import { motion, AnimatePresence } from "framer-motion";


const TasksPage: React.FC = () => {
  const navigate = useNavigate();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState<string>("All");
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);

  useEffect(() => {
    fetchTasks();
    const pollInterval = setInterval(() => {
      fetchTasks(false);
    }, 30000);

    return () => {
      clearInterval(pollInterval);
    };
  }, []);

  const fetchTasks = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const data = await taskService.getTasks();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  const normalizeStatus = (status: string) => {
    const s = status ? status.toLowerCase() : "";
    if (s === "todo" || s === "upcoming") return "Upcoming";
    if (s === "in_progress" || s === "in progress" || s === "inprogress") return "In Progress";
    if (s === "completed" || s === "done") return "Completed";
    return "Upcoming";
  };

  const getTimeStatus = (dueDate: string | null | undefined, status: string) => {
    const normStatus = normalizeStatus(status);
    if (normStatus === "Completed") return { label: "Completed", style: "bg-emerald-50 text-emerald-600 border-emerald-100" };
    if (!dueDate) return { label: "Planned", style: "bg-slate-50 text-slate-500 border-slate-100" };

    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const due = new Date(dueDate);
    due.setHours(0, 0, 0, 0);

    if (due < today) return { label: "Overdue", style: "bg-rose-50 text-rose-600 border-rose-100" };
    if (due.getTime() === today.getTime()) return { label: "Due Today", style: "bg-amber-50 text-amber-600 border-amber-100" };

    return { label: "Active", style: "bg-indigo-50 text-indigo-600 border-indigo-100" };
  };

  const handleDelete = async (e: React.MouseEvent, id: number) => {
    e.stopPropagation();
    if (!window.confirm("Are you sure you want to delete this task?")) return;
    try {
      await taskService.deleteTask(id);
      setTasks(prev => prev.filter(t => t.id !== id));
      if (selectedTask?.id === id) setSelectedTask(null);
    } catch (error) {
      console.error("Failed to delete task");
    }
  };

  const filteredTasks = tasks.filter(task => {
    const matchesSearch = task.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (task.description && task.description.toLowerCase().includes(searchQuery.toLowerCase()));
    const matchesStatus = statusFilter === "All" || normalizeStatus(task.status) === statusFilter;
    return matchesSearch && matchesStatus;
  });

  return (
    <div className="min-h-screen bg-[#fcfdfe] text-slate-900 px-8 py-10 lg:px-12 animate-fade-in relative">

      {/* Main Content Area */}
      <div className={`transition-all duration-500 ${selectedTask ? 'lg:mr-[460px]' : ''}`}>

        {/* Header Section */}
        <div className="flex flex-col md:flex-row md:items-center justify-between gap-6 mb-12">
          <div>
            <h1 className="text-4xl font-bold tracking-tight text-slate-900">Tasks</h1>
            <p className="text-slate-500 font-medium mt-1">Manage and track your team's objectives.</p>
          </div>
          <button
            onClick={() => navigate("/tasks/add")}
            className="btn-primary"
          >
            <Plus size={18} />
            New Task
          </button>
        </div>

        {/* Toolbar */}
        <div className="flex flex-col lg:flex-row items-center gap-6 mb-8">
          <div className="relative flex-1 w-full group">
            <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-indigo-600 transition-colors" size={18} />
            <input
              type="text"
              placeholder="Search tasks..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-12 pr-4 py-3 bg-white border border-slate-100 rounded-2xl focus:ring-4 focus:ring-indigo-500/5 focus:border-indigo-500 focus:outline-none transition-all font-medium text-sm text-slate-900 placeholder:text-slate-400 shadow-sm"
            />
          </div>
          <div className="flex items-center gap-2 p-1.5 bg-slate-100/50 rounded-2xl w-full lg:w-auto">
            {["All", "Upcoming", "In Progress", "Completed"].map((status) => (
              <button
                key={status}
                onClick={() => setStatusFilter(status)}
                className={`px-4 py-2 text-xs font-bold rounded-xl transition-all ${statusFilter === status
                    ? "bg-white text-indigo-600 shadow-sm"
                    : "text-slate-500 hover:text-slate-900"
                  }`}
              >
                {status}
              </button>
            ))}
          </div>
        </div>

        {/* Task List Rendering */}
        <div className="space-y-4">
          {loading ? (
            <div className="py-24 text-center bg-white rounded-[32px] border border-slate-100 shadow-sm">
              <div className="w-12 h-12 border-4 border-slate-50 border-t-indigo-600 rounded-full animate-spin mx-auto mb-4" />
              <p className="text-sm font-semibold text-slate-400">Loading your tasks...</p>
            </div>
          ) : filteredTasks.length === 0 ? (
            <div className="py-24 text-center bg-white rounded-[32px] border border-slate-100 shadow-sm">
              <div className="w-16 h-16 bg-slate-50 rounded-2xl flex items-center justify-center mx-auto mb-4">
                <ClipboardList className="text-slate-300" size={32} />
              </div>
              <h4 className="text-lg font-bold text-slate-900">No tasks found</h4>
              <p className="text-sm text-slate-400 font-medium mt-1">Try adjusting your filters or search query.</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 gap-4">
              {filteredTasks.map((task) => {
                const timeStatus = getTimeStatus(task.due_date, task.status);
                const isSelected = selectedTask?.id === task.id;
                return (
                  <motion.div
                    layout
                    key={task.id}
                    onClick={() => setSelectedTask(task)}
                    className={`group relative p-6 bg-white border rounded-[24px] transition-all cursor-pointer flex flex-col md:flex-row md:items-center gap-6 ${isSelected ? 'border-indigo-500 ring-4 ring-indigo-500/5 shadow-lg shadow-indigo-100/50' : 'border-slate-100 hover:border-indigo-100 hover:shadow-md'
                      }`}
                  >
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-3 mb-2">
                        <span className={`px-3 py-1 text-[10px] font-bold rounded-lg border ${timeStatus.style}`}>
                          {timeStatus.label}
                        </span>
                        {task.priority === 'High' && (
                          <span className="px-3 py-1 text-[10px] font-bold bg-rose-50 text-rose-600 border border-rose-100 rounded-lg uppercase tracking-wider">Priority</span>
                        )}
                      </div>
                      <h4 className="text-lg font-bold text-slate-900 mb-1 leading-tight">
                        {task.title}
                      </h4>
                      <p className="text-sm text-slate-400 font-medium line-clamp-1">{task.description || "No description provided."}</p>
                    </div>

                    <div className="flex items-center justify-between md:justify-end gap-10 shrink-0 mt-4 md:mt-0 pt-4 md:pt-0 border-t md:border-t-0 border-slate-50">
                      <div className="flex flex-col md:items-end">
                        <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest mb-1">Due Date</p>
                        <div className="flex items-center gap-2 text-slate-900">
                          <Calendar size={14} className="text-slate-400" />
                          <p className="text-sm font-bold">
                            {task.due_date ? new Date(task.due_date).toLocaleDateString(undefined, { month: 'short', day: 'numeric', year: 'numeric' }) : "No Date"}
                          </p>
                        </div>
                      </div>

                      <div className="flex items-center gap-3">
                        <button
                          onClick={(e) => { e.stopPropagation(); navigate(`/tasks/edit/${task.id}`); }}
                          className="p-2.5 bg-slate-50 text-slate-400 hover:bg-indigo-50 hover:text-indigo-600 rounded-xl transition-all"
                        >
                          <Edit3 size={18} />
                        </button>
                        <button
                          onClick={(e) => handleDelete(e, task.id)}
                          className="p-2.5 bg-slate-50 text-slate-400 hover:bg-rose-50 hover:text-rose-600 rounded-xl transition-all"
                        >
                          <Trash2 size={18} />
                        </button>
                      </div>
                    </div>
                  </motion.div>
                );
              })}
            </div>
          )}
        </div>
      </div>

      {/* Slide-over Panel */}
      <AnimatePresence>
        {selectedTask && (
          <motion.div
            initial={{ x: "100%" }}
            animate={{ x: 0 }}
            exit={{ x: "100%" }}
            transition={{ type: "spring", damping: 25, stiffness: 200 }}
            className="fixed top-0 right-0 w-full lg:w-[450px] h-screen bg-white bg-opacity-80 backdrop-blur-xl border-l border-slate-100 shadow-2xl z-50 flex flex-col overflow-hidden"
          >
            <div className="p-8 pb-4 flex items-center justify-between border-b border-slate-100">
              <p className="text-[11px] font-bold text-slate-400 uppercase tracking-widest">Task Details</p>
              <button
                onClick={() => setSelectedTask(null)}
                className="w-10 h-10 flex items-center justify-center bg-slate-50 text-slate-400 hover:bg-slate-100 hover:text-slate-900 rounded-xl transition-all"
              >
                <X size={20} />
              </button>
            </div>

            <div className="flex-1 overflow-y-auto p-8 space-y-8 custom-scrollbar">
              <div className="space-y-4">
                <div className="flex gap-2">
                  <span className={`px-3 py-1.5 text-[10px] font-bold rounded-lg border ${getTimeStatus(selectedTask.due_date, selectedTask.status).style}`}>
                    {normalizeStatus(selectedTask.status)}
                  </span>
                  {selectedTask.priority === 'High' && (
                    <span className="px-3 py-1.5 text-[10px] font-bold bg-rose-50 text-rose-600 border border-rose-100 rounded-lg uppercase tracking-wider">High Priority</span>
                  )}
                </div>
                <h3 className="text-3xl font-bold text-slate-900 tracking-tight leading-tight">{selectedTask.title}</h3>
                <p className="text-sm text-slate-500 font-medium leading-relaxed">
                  {selectedTask.description || "No description provided for this task."}
                </p>
              </div>

              <div className="space-y-6 pt-8 border-t border-slate-100">
                <div className="flex items-center gap-4 p-4 bg-slate-50 rounded-2xl">
                  <div className="w-10 h-10 bg-white rounded-xl flex items-center justify-center text-slate-400 border border-slate-100 shadow-sm">
                    <Calendar size={20} />
                  </div>
                  <div>
                    <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest mb-0.5">Due Date</p>
                    <p className="text-sm font-bold text-slate-900">
                      {selectedTask.due_date ? new Date(selectedTask.due_date).toLocaleDateString(undefined, { month: 'long', day: 'numeric', year: 'numeric' }) : "No Date Set"}
                    </p>
                  </div>
                </div>

                <div className="flex items-center gap-4 p-4 bg-slate-50 rounded-2xl">
                  <div className="w-10 h-10 bg-white rounded-xl flex items-center justify-center text-slate-400 border border-slate-100 shadow-sm">
                    <UserIcon size={20} />
                  </div>
                  <div>
                    <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest mb-0.5">Assigned To</p>
                    <p className="text-sm font-bold text-slate-900">{selectedTask.assigned_to_user?.name || "Unassigned"}</p>
                  </div>
                </div>
              </div>
            </div>

            <div className="p-8 border-t border-slate-100 bg-slate-50/50 mt-auto grid grid-cols-2 gap-4">
              <button
                onClick={() => navigate(`/tasks/edit/${selectedTask.id}`)}
                className="btn-primary w-full shadow-none"
              >
                <Edit3 size={18} />
                Edit Task
              </button>
              <button
                onClick={(e) => handleDelete(e, selectedTask.id)}
                className="w-full py-3 bg-white text-rose-600 border border-rose-100 rounded-xl font-bold text-sm hover:bg-rose-50 transition-all flex items-center justify-center gap-2"
              >
                <Trash2 size={18} />
                Delete
              </button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default TasksPage;
