import React, { useState, useEffect, useRef, useCallback } from "react";
import {
    Search, Users, Paperclip, Send,
    X, Plus, MessageSquare as MessageIcon,
    ArrowLeft
} from "lucide-react";
import { messageService, Chat, Message } from "../../services/messageService";
import { userService, User } from "../../services/userService";
import { authService } from "../../services/authService";
import { motion, AnimatePresence } from "framer-motion";

const CreateGroupModal = ({ isOpen, onClose, users, onCreate }: any) => {
    const [groupName, setGroupName] = useState("");
    const [selectedIds, setSelectedIds] = useState<number[]>([]);

    const toggleUser = (id: number) => {
        setSelectedIds(prev => prev.includes(id) ? prev.filter(x => x !== id) : [...prev, id]);
    };

    const handleSubmit = () => {
        if (!groupName || selectedIds.length === 0) return;
        onCreate(groupName, selectedIds);
        onClose();
        setGroupName("");
        setSelectedIds([]);
    };

    return (
        <AnimatePresence>
            {isOpen && (
                <div className="fixed inset-0 z-[100] flex items-center justify-center p-4">
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="absolute inset-0 bg-zinc-900/40 backdrop-blur-sm"
                        onClick={onClose}
                    />
                    <motion.div
                        initial={{ opacity: 0, scale: 0.95, y: 10 }}
                        animate={{ opacity: 1, scale: 1, y: 0 }}
                        exit={{ opacity: 0, scale: 0.95, y: 10 }}
                        className="bg-white rounded-xl border border-zinc-200 w-full max-w-md p-6 relative z-10 shadow-xl"
                    >
                        <div className="flex justify-between items-center mb-6">
                            <h3 className="text-lg font-semibold text-zinc-900">New Group</h3>
                            <button onClick={onClose} className="p-1 hover:bg-zinc-100 rounded-md text-zinc-500">
                                <X size={20} />
                            </button>
                        </div>

                        <div className="space-y-4">
                            <div>
                                <label className="text-xs font-medium text-zinc-500 mb-1 block">Group Name</label>
                                <input
                                    className="w-full px-3 py-2 border border-zinc-200 rounded-lg text-sm bg-zinc-50 focus:bg-white focus:ring-2 focus:ring-zinc-900/10 outline-none transition-all"
                                    value={groupName}
                                    onChange={e => setGroupName(e.target.value)}
                                    placeholder="Enter name..."
                                />
                            </div>

                            <div>
                                <label className="text-xs font-medium text-zinc-500 mb-1 block">Add Members</label>
                                <div className="max-h-[200px] overflow-y-auto border border-zinc-200 rounded-lg divide-y divide-zinc-100">
                                    {users.map((u: User) => (
                                        <button
                                            key={u.id}
                                            onClick={() => toggleUser(u.id)}
                                            className="w-full flex items-center gap-3 p-3 hover:bg-zinc-50 transition-colors text-left"
                                        >
                                            <div className={`w-4 h-4 rounded-full border flex items-center justify-center ${selectedIds.includes(u.id) ? 'bg-zinc-900 border-zinc-900' : 'border-zinc-300'}`}>
                                                {selectedIds.includes(u.id) && <div className="w-1.5 h-1.5 bg-white rounded-full" />}
                                            </div>
                                            <span className="text-sm font-medium text-zinc-700">{u.name}</span>
                                        </button>
                                    ))}
                                </div>
                            </div>

                            <button
                                onClick={handleSubmit}
                                disabled={!groupName || selectedIds.length === 0}
                                className="w-full py-2 bg-zinc-900 text-white rounded-lg text-sm font-medium hover:bg-zinc-800 disabled:opacity-50 mt-2"
                            >
                                Create Group
                            </button>
                        </div>
                    </motion.div>
                </div>
            )}
        </AnimatePresence>
    );
};

const MessagesPage: React.FC = () => {
    const [chats, setChats] = useState<Chat[]>([]);
    const [messages, setMessages] = useState<Message[]>([]);
    const [currentUser, setCurrentUser] = useState<any>(null);
    const [activeChatId, setActiveChatId] = useState<number | null>(null);
    const [searchQuery, setSearchQuery] = useState("");
    const [allUsers, setAllUsers] = useState<User[]>([]);
    const [inputText, setInputText] = useState("");
    const [isMobileChatOpen, setIsMobileChatOpen] = useState(false);
    const [isGroupModalOpen, setIsGroupModalOpen] = useState(false);

    const messagesContainerRef = useRef<HTMLDivElement>(null);

    const loadChats = useCallback(async () => {
        try {
            const data = await messageService.getChats();
            setChats(data);
        } catch (e) {
            console.error("Failed to load chats:", e);
        }
    }, []);

    const loadUserAndData = useCallback(async () => {
        try {
            const user = await authService.getCurrentUser();
            setCurrentUser(user);
            await loadChats();
            const users = await userService.getAllUsers();
            setAllUsers(users.filter(u => u.id !== user.id));
        } catch (e) {
            console.error("Failed to load user data:", e);
        }
    }, [loadChats]);

    const loadMessages = useCallback(async (chatId: number) => {
        try {
            const msgs = await messageService.getMessages(chatId);
            setMessages(msgs);
            await messageService.markAsRead(chatId);
            loadChats();
        } catch (e) {
            console.error("Failed to load messages:", e);
        }
    }, [loadChats]);

    const scrollToBottom = useCallback((smooth = true) => {
        if (messagesContainerRef.current) {
            const { scrollHeight, clientHeight } = messagesContainerRef.current;
            messagesContainerRef.current.scrollTo({
                top: scrollHeight - clientHeight,
                behavior: smooth ? 'smooth' : 'auto'
            });
        }
    }, []);

    useEffect(() => {
        loadUserAndData();
    }, [loadUserAndData]);

    useEffect(() => {
        const pollInterval = setInterval(() => {
            loadChats();
            if (activeChatId) {
                loadMessages(activeChatId);
            }
        }, 10000);

        return () => {
            clearInterval(pollInterval);
        };
    }, [activeChatId, loadChats, loadMessages]);

    useEffect(() => {
        if (activeChatId) {
            loadMessages(activeChatId);
            setTimeout(() => scrollToBottom(false), 100);
        }
    }, [activeChatId, loadMessages, scrollToBottom]);

    useEffect(() => {
        scrollToBottom();
    }, [messages, scrollToBottom]);

    const handleSendMessage = async () => {
        if (!inputText.trim() || !activeChatId) return;
        try {
            await messageService.sendMessage(activeChatId, inputText);
            setInputText("");
            loadMessages(activeChatId);
            loadChats();
            setTimeout(() => scrollToBottom(), 100);
        } catch (e) {
            console.error("Failed to send message:", e);
        }
    };

    const handleCreateP2PChat = async (userId: number) => {
        try {
            const chat = await messageService.createChat([userId], undefined, false);
            setChats(prev => prev.find(c => c.id === chat.id) ? prev : [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
            setSearchQuery("");
        } catch (e) {
            console.error("Failed to create P2P chat:", e);
        }
    };

    const handleCreateGroup = async (name: string, userIds: number[]) => {
        try {
            const chat = await messageService.createChat(userIds, name, true);
            setChats(prev => [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
        } catch (e) {
            console.error("Failed to create group chat:", e);
        }
    };

    const filteredChats = chats.filter(c => c.name?.toLowerCase().includes(searchQuery.toLowerCase()));
    const suggestedUsers = searchQuery ? allUsers.filter(u => u.name.toLowerCase().includes(searchQuery.toLowerCase())) : [];
    const activeChat = chats.find(c => c.id === activeChatId);

    return (
        <div className="flex h-[calc(100vh-140px)] bg-white rounded-xl border border-zinc-200 shadow-sm overflow-hidden animate-in">
            <CreateGroupModal
                isOpen={isGroupModalOpen}
                onClose={() => setIsGroupModalOpen(false)}
                users={allUsers}
                onCreate={handleCreateGroup}
            />

            {/* Sidebar List */}
            <div className={`w-full md:w-80 border-r border-zinc-100 flex flex-col ${isMobileChatOpen ? 'hidden md:flex' : 'flex'}`}>
                <div className="p-4 border-b border-zinc-100 flex justify-between items-center">
                    <h2 className="font-semibold text-zinc-900">Messages</h2>
                    <button onClick={() => setIsGroupModalOpen(true)} className="p-1.5 hover:bg-zinc-100 rounded-md text-zinc-500 transition-colors">
                        <Plus size={18} />
                    </button>
                </div>

                <div className="p-3">
                    <div className="relative">
                        <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-zinc-400" size={14} />
                        <input
                            className="w-full pl-9 pr-3 py-2 bg-zinc-50 border border-zinc-200 rounded-lg text-sm focus:bg-white focus:ring-2 focus:ring-zinc-100 outline-none transition-all placeholder:text-zinc-400"
                            placeholder="Search..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                        />
                    </div>
                </div>

                <div className="flex-1 overflow-y-auto custom-scrollbar">
                    {filteredChats.map(chat => (
                        <button
                            key={chat.id}
                            onClick={() => {
                                setActiveChatId(chat.id);
                                setIsMobileChatOpen(true);
                            }}
                            className={`w-full p-3 flex items-center gap-3 hover:bg-zinc-50 transition-colors border-l-2 ${activeChatId === chat.id ? 'border-zinc-900 bg-zinc-50' : 'border-transparent'
                                }`}
                        >
                            <div className="w-10 h-10 rounded-full bg-zinc-100 flex items-center justify-center text-xs font-semibold text-zinc-500 shrink-0">
                                {chat.is_group ? <Users size={16} /> : chat.name?.substring(0, 2).toUpperCase()}
                            </div>
                            <div className="flex-1 min-w-0 text-left">
                                <div className="flex justify-between items-baseline">
                                    <span className={`text-sm font-medium truncate ${activeChatId === chat.id ? 'text-zinc-900' : 'text-zinc-700'}`}>
                                        {chat.name}
                                    </span>
                                    {chat.last_message_time && (
                                        <span className="text-[10px] text-zinc-400">
                                            {new Date(chat.last_message_time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                        </span>
                                    )}
                                </div>
                                <p className="text-xs text-zinc-500 truncate mt-0.5">
                                    {chat.last_message || "No messages"}
                                </p>
                            </div>
                        </button>
                    ))}

                    {suggestedUsers.length > 0 && (
                        <div className="p-3 border-t border-zinc-100 mt-2">
                            <p className="text-xs font-medium text-zinc-400 mb-2 uppercase tracking-wide">Suggested</p>
                            {suggestedUsers.map(user => (
                                <button key={user.id} onClick={() => handleCreateP2PChat(user.id)} className="w-full p-2 hover:bg-zinc-50 rounded-lg flex items-center gap-3 text-left">
                                    <div className="w-8 h-8 rounded-full bg-zinc-100 flex items-center justify-center text-xs text-zinc-500">{user.name.substring(0, 2).toUpperCase()}</div>
                                    <span className="text-sm text-zinc-700">{user.name}</span>
                                </button>
                            ))}
                        </div>
                    )}
                </div>
            </div>

            {/* Chat Area */}
            {activeChat ? (
                <div className={`flex-1 flex flex-col bg-slate-50/30 ${isMobileChatOpen ? 'flex absolute inset-0 z-20 bg-white' : 'hidden md:flex'}`}>
                    <div className="h-14 border-b border-zinc-200 bg-white flex items-center justify-between px-4 shrink-0">
                        <div className="flex items-center gap-3">
                            <button onClick={() => setIsMobileChatOpen(false)} className="md:hidden text-zinc-500">
                                <ArrowLeft size={20} />
                            </button>
                            <span className="font-semibold text-zinc-900">{activeChat.name}</span>
                        </div>
                    </div>

                    <div className="flex-1 overflow-hidden relative bg-white">
                        <div ref={messagesContainerRef} className="absolute inset-0 overflow-y-auto p-4 space-y-3 custom-scrollbar">
                            {messages.map((msg, idx) => {
                                const isMe = msg.sender_id === currentUser?.id;
                                return (
                                    <div key={msg.id || idx} className={`flex ${isMe ? 'justify-end' : 'justify-start'}`}>
                                        <div className={`max-w-[75%] px-4 py-2 rounded-2xl text-sm ${isMe
                                                ? 'bg-zinc-900 text-white rounded-br-none'
                                                : 'bg-zinc-100 text-zinc-800 rounded-bl-none'
                                            }`}>
                                            {!isMe && (
                                                <p className="text-[10px] text-zinc-500 font-bold mb-0.5 uppercase opacity-70">
                                                    {allUsers.find(u => u.id === msg.sender_id)?.name.split(' ')[0]}
                                                </p>
                                            )}
                                            {msg.content}
                                            <p className={`text-[9px] mt-1 text-right ${isMe ? 'text-zinc-400' : 'text-zinc-400'}`}>
                                                {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                            </p>
                                        </div>
                                    </div>
                                );
                            })}
                        </div>
                    </div>

                    <div className="p-4 bg-white border-t border-zinc-200">
                        <form onSubmit={(e) => { e.preventDefault(); handleSendMessage(); }} className="flex gap-2">
                            <div className="flex-1 relative">
                                <input
                                    className="w-full pl-4 pr-10 py-2.5 bg-zinc-50 border border-zinc-200 rounded-lg text-sm focus:ring-2 focus:ring-zinc-900/5 outline-none transition-all placeholder:text-zinc-400"
                                    placeholder="Type a message..."
                                    value={inputText}
                                    onChange={(e) => setInputText(e.target.value)}
                                />
                                <button type="button" className="absolute right-2 top-1/2 -translate-y-1/2 p-1.5 text-zinc-400 hover:text-zinc-600">
                                    <Paperclip size={16} />
                                </button>
                            </div>
                            <button type="submit" disabled={!inputText.trim()} className="p-2.5 bg-zinc-900 text-white rounded-lg hover:bg-zinc-800 disabled:opacity-50 transition-colors">
                                <Send size={18} />
                            </button>
                        </form>
                    </div>
                </div>
            ) : (
                <div className="hidden md:flex flex-1 flex-col items-center justify-center text-zinc-400 bg-zinc-50/30">
                    <MessageIcon size={48} className="mb-4 opacity-20" />
                    <p className="text-sm font-medium">Select a conversation to start chatting</p>
                </div>
            )}
        </div>
    );
};

export default MessagesPage;
