import React, { useState } from "react";
import { Plus, ChevronLeft, ChevronRight } from "lucide-react";

interface Task {
  id: number;
  title: string;
  description: string;
  status: "Todo" | "In Progress" | "Completed";
  assignedTo: string;
  dueDate: string;
}

const initialTasks: Task[] = [
  {
    id: 1,
    title: "Setup Backend APIs",
    description: "Develop FastAPI endpoints for authentication and projects.",
    status: "Todo",
    assignedTo: "Ashok Kumar",
    dueDate: "2025-11-03",
  },
  {
    id: 2,
    title: "UI Wireframes",
    description: "Create mockups for dashboard and chat modules.",
    status: "In Progress",
    assignedTo: "Zara Ali",
    dueDate: "2025-11-06",
  },
  {
    id: 3,
    title: "Integrate AI Assistant",
    description: "Connect AWS Bedrock for AI chat in Collabryta.",
    status: "Completed",
    assignedTo: "John Smith",
    dueDate: "2025-10-29",
  },
];

const statusOrder: ("Todo" | "In Progress" | "Completed")[] = [
  "Todo",
  "In Progress",
  "Completed",
];

const TasksPage: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>(initialTasks);
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);
  const [addingTask, setAddingTask] = useState(false);
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    assignedTo: "",
    dueDate: "",
  });

  const groupedTasks = {
    Todo: tasks.filter((t) => t.status === "Todo"),
    "In Progress": tasks.filter((t) => t.status === "In Progress"),
    Completed: tasks.filter((t) => t.status === "Completed"),
  };

  const moveForward = (task: Task) => {
    const currentIndex = statusOrder.indexOf(task.status);
    if (currentIndex < statusOrder.length - 1) {
      const newStatus = statusOrder[currentIndex + 1];
      setTasks((prev) =>
        prev.map((t) =>
          t.id === task.id ? { ...t, status: newStatus } : t
        )
      );
      setSelectedTask({ ...task, status: newStatus });
    }
  };

  const moveBackward = (task: Task) => {
    const currentIndex = statusOrder.indexOf(task.status);
    if (currentIndex > 0) {
      const newStatus = statusOrder[currentIndex - 1];
      setTasks((prev) =>
        prev.map((t) =>
          t.id === task.id ? { ...t, status: newStatus } : t
        )
      );
      setSelectedTask({ ...task, status: newStatus });
    }
  };

  const handleAddTask = () => {
    if (!newTask.title.trim()) return;
    const task: Task = {
      id: Date.now(),
      title: newTask.title,
      description: newTask.description,
      assignedTo: newTask.assignedTo || "Unassigned",
      dueDate: newTask.dueDate || new Date().toISOString().split("T")[0],
      status: "Todo",
    };
    setTasks([...tasks, task]);
    setNewTask({ title: "", description: "", assignedTo: "", dueDate: "" });
    setAddingTask(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-semibold text-gray-800">Task Dashboard</h1>
        <button
          onClick={() => setAddingTask(true)}
          className="flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition"
        >
          <Plus size={18} /> Add New Task
        </button>
      </div>

      {/* Sections */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {Object.entries(groupedTasks).map(([status, list]) => (
          <div
            key={status}
            className="bg-white shadow rounded-lg p-4 border border-gray-200"
          >
            <h2 className="text-lg font-semibold mb-3 text-gray-700 border-b pb-2">
              {status === "Todo"
                ? "游릭 Upcoming"
                : status === "In Progress"
                ? "游리 In Progress"
                : "游댯 Completed"}
            </h2>
            {list.length === 0 ? (
              <p className="text-gray-400 text-sm">No tasks available</p>
            ) : (
              list.map((task) => (
                <div
                  key={task.id}
                  onClick={() => setSelectedTask(task)}
                  className="cursor-pointer bg-gray-50 hover:bg-gray-100 p-3 rounded-md mb-2 border border-gray-100 transition"
                >
                  <h3 className="font-medium text-gray-800">{task.title}</h3>
                  <p className="text-sm text-gray-500">{task.assignedTo}</p>
                  <p className="text-xs text-gray-400">
                    Due: {new Date(task.dueDate).toLocaleDateString()}
                  </p>
                </div>
              ))
            )}
          </div>
        ))}
      </div>

      {/* Task Modal */}
      {selectedTask && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white rounded-xl w-11/12 max-w-lg p-6 shadow-lg relative">
            <button
              onClick={() => setSelectedTask(null)}
              className="absolute top-3 right-4 text-gray-500 hover:text-gray-700 text-lg"
            >
              九됲r
            </button>

            <h2 className="text-xl font-semibold text-gray-800 mb-2">
              {selectedTask.title}
            </h2>
            <p className="text-gray-600 mb-4">{selectedTask.description}</p>

            <div className="space-y-2 text-sm text-gray-600">
              <p>
                <strong>Assigned to:</strong> {selectedTask.assignedTo}
              </p>
              <p>
                <strong>Due Date:</strong>{" "}
                {new Date(selectedTask.dueDate).toLocaleDateString()}
              </p>
              <p>
                <strong>Status:</strong>{" "}
                <span className="capitalize">{selectedTask.status}</span>
              </p>
            </div>

            <div className="mt-6 flex justify-between">
              <button
                disabled={selectedTask.status === "Todo"}
                onClick={() => moveBackward(selectedTask)}
                className={`flex items-center gap-1 px-3 py-2 rounded-md ${
                  selectedTask.status === "Todo"
                    ? "bg-gray-200 text-gray-400 cursor-not-allowed"
                    : "bg-gray-300 hover:bg-gray-400 text-gray-800"
                }`}
              >
                <ChevronLeft size={16} /> Back
              </button>

              <button
                onClick={() => {
                  if (selectedTask.status === "Completed") {
                    setSelectedTask(null);
                  } else {
                    moveForward(selectedTask);
                  }
                }}
                className="flex items-center gap-1 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition"
              >
                {selectedTask.status === "Completed" ? "Done" : "Next"}
                <ChevronRight size={16} />
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Add New Task Modal */}
      {addingTask && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white rounded-xl w-11/12 max-w-md p-6 shadow-lg relative">
            <button
              onClick={() => setAddingTask(false)}
              className="absolute top-3 right-4 text-gray-500 hover:text-gray-700 text-lg"
            >
              九됲r
            </button>

            <h2 className="text-xl font-semibold mb-4 text-gray-800">
              Add New Task
            </h2>

            <div className="space-y-3">
              <input
                type="text"
                placeholder="Task Title"
                value={newTask.title}
                onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                className="w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
              <textarea
                placeholder="Description"
                value={newTask.description}
                onChange={(e) =>
                  setNewTask({ ...newTask, description: e.target.value })
                }
                className="w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
              <input
                type="text"
                placeholder="Assigned To"
                value={newTask.assignedTo}
                onChange={(e) =>
                  setNewTask({ ...newTask, assignedTo: e.target.value })
                }
                className="w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
              <input
                type="date"
                value={newTask.dueDate}
                onChange={(e) =>
                  setNewTask({ ...newTask, dueDate: e.target.value })
                }
                className="w-full border border-gray-300 rounded-md p-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
            </div>

            <div className="mt-5 flex justify-end">
              <button
                onClick={handleAddTask}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition"
              >
                Add Task
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TasksPage;
