import React, { useState, useEffect, useRef, useCallback } from "react";
import {
    Search, Phone, Video, Users, Paperclip, Smile, Send,
    Check, ArrowLeft, X
} from "lucide-react";
import { messageService, Chat, Message } from "../../services/messageService";
import { userService, User } from "../../services/userService";
import { authService } from "../../services/authService";

/**
 * Modal component for creating a new group chat.
 */
const CreateGroupModal = ({ isOpen, onClose, users, onCreate }: any) => {
    const [groupName, setGroupName] = useState("");
    const [selectedIds, setSelectedIds] = useState<number[]>([]);

    if (!isOpen) return null;

    const toggleUser = (id: number) => {
        setSelectedIds(prev => prev.includes(id) ? prev.filter(x => x !== id) : [...prev, id]);
    };

    const handleSubmit = () => {
        if (!groupName || selectedIds.length === 0) return;
        onCreate(groupName, selectedIds);
        onClose();
        setGroupName("");
        setSelectedIds([]);
    };

    return (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 backdrop-blur-sm">
            <div className="bg-white rounded-2xl w-full max-w-md p-6 shadow-2xl animate-in fade-in zoom-in duration-200">
                <div className="flex justify-between items-center mb-4">
                    <h3 className="text-xl font-bold text-slate-800">Create Group Chat</h3>
                    <button onClick={onClose}><X className="text-slate-400 hover:text-red-500 transition" /></button>
                </div>

                <div className="mb-4">
                    <label className="block text-sm font-medium text-slate-700 mb-1">Group Name</label>
                    <input
                        className="w-full px-4 py-2 bg-slate-50 border border-slate-200 rounded-xl focus:ring-2 focus:ring-blue-500 outline-none transition"
                        value={groupName}
                        onChange={e => setGroupName(e.target.value)}
                        placeholder="e.g. Project Alpha Team"
                    />
                </div>

                <div className="mb-6">
                    <label className="block text-sm font-medium text-slate-700 mb-2">Select Members</label>
                    <div className="max-h-60 overflow-y-auto space-y-2 border border-slate-100 rounded-xl p-2 bg-slate-50/50">
                        {users.map((u: User) => (
                            <div
                                key={u.id}
                                onClick={() => toggleUser(u.id)}
                                className={`flex items-center gap-3 p-2 rounded-lg cursor-pointer transition ${selectedIds.includes(u.id) ? 'bg-blue-50 border border-blue-200 shadow-sm' : 'hover:bg-white border border-transparent'}`}
                            >
                                <div className={`w-8 h-8 rounded-full flex items-center justify-center text-xs font-bold text-white transition ${selectedIds.includes(u.id) ? 'bg-blue-500' : 'bg-slate-300'}`}>
                                    {u.name.substring(0, 2).toUpperCase()}
                                </div>
                                <span className={`text-sm font-medium ${selectedIds.includes(u.id) ? 'text-blue-900' : 'text-slate-600'}`}>{u.name}</span>
                                {selectedIds.includes(u.id) && <Check size={16} className="ml-auto text-blue-600" />}
                            </div>
                        ))}
                    </div>
                </div>

                <div className="flex justify-end gap-3">
                    <button onClick={onClose} className="px-4 py-2 text-slate-500 hover:bg-slate-100 rounded-xl font-medium transition">Cancel</button>
                    <button
                        onClick={handleSubmit}
                        disabled={!groupName || selectedIds.length === 0}
                        className="px-6 py-2 bg-blue-600 text-white rounded-xl font-medium hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition shadow-sm hover:shadow-md"
                    >
                        Create Group
                    </button>
                </div>
            </div>
        </div>
    );
};

const MessagesPage: React.FC = () => {
    const [chats, setChats] = useState<Chat[]>([]);
    const [messages, setMessages] = useState<Message[]>([]);
    const [currentUser, setCurrentUser] = useState<any>(null);
    const [activeChatId, setActiveChatId] = useState<number | null>(null);
    const [searchQuery, setSearchQuery] = useState("");
    const [allUsers, setAllUsers] = useState<User[]>([]);
    const [inputText, setInputText] = useState("");
    const [isMobileChatOpen, setIsMobileChatOpen] = useState(false);
    const [isGroupModalOpen, setIsGroupModalOpen] = useState(false);

    const messagesEndRef = useRef<HTMLDivElement>(null);
    const messagesContainerRef = useRef<HTMLDivElement>(null);

    const loadChats = useCallback(async () => {
        try {
            const data = await messageService.getChats();
            setChats(data);
        } catch (e) {
            console.error("Failed to load chats:", e);
        }
    }, []);

    const loadUserAndData = useCallback(async () => {
        try {
            const user = await authService.getCurrentUser();
            setCurrentUser(user);
            await loadChats();
            const users = await userService.getAllUsers();
            setAllUsers(users.filter(u => u.id !== user.id));
        } catch (e) {
            console.error("Failed to load user data:", e);
        }
    }, [loadChats]);

    const loadMessages = useCallback(async (chatId: number) => {
        try {
            const msgs = await messageService.getMessages(chatId);
            setMessages(msgs);
            await messageService.markAsRead(chatId);
            loadChats();
        } catch (e) {
            console.error("Failed to load messages:", e);
        }
    }, [loadChats]);

    const scrollToBottom = useCallback((smooth = true) => {
        if (messagesContainerRef.current) {
            const { scrollHeight, clientHeight } = messagesContainerRef.current;
            messagesContainerRef.current.scrollTo({
                top: scrollHeight - clientHeight,
                behavior: smooth ? 'smooth' : 'auto'
            });
        }
    }, []);

    // Initial Load
    useEffect(() => {
        loadUserAndData();
    }, [loadUserAndData]);

    // Polling for updates (WebSocket Replacement)
    useEffect(() => {
        if (!activeChatId) return;
        const interval = setInterval(() => {
            loadMessages(activeChatId);
            loadChats();
        }, 5000);
        return () => clearInterval(interval);
    }, [activeChatId, loadMessages, loadChats]);

    // Load messages when active chat changes
    useEffect(() => {
        if (activeChatId) {
            loadMessages(activeChatId);
            setTimeout(() => scrollToBottom(false), 100);
        }
    }, [activeChatId, loadMessages, scrollToBottom]);

    // Auto-scroll on new messages
    useEffect(() => {
        scrollToBottom();
    }, [messages, scrollToBottom]);

    const handleSendMessage = async () => {
        if (!inputText.trim() || !activeChatId) return;
        try {
            await messageService.sendMessage(activeChatId, inputText);
            setInputText("");
            loadMessages(activeChatId);
            loadChats();
            setTimeout(() => scrollToBottom(), 100);
        } catch (e) {
            console.error("Failed to send message:", e);
        }
    };

    const handleCreateP2PChat = async (userId: number) => {
        try {
            const chat = await messageService.createChat([userId], undefined, false);
            setChats(prev => prev.find(c => c.id === chat.id) ? prev : [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
            setSearchQuery("");
        } catch (e) {
            console.error("Failed to create P2P chat:", e);
        }
    };

    const handleCreateGroup = async (name: string, userIds: number[]) => {
        try {
            const chat = await messageService.createChat(userIds, name, true);
            setChats(prev => [chat, ...prev]);
            setActiveChatId(chat.id);
            setIsMobileChatOpen(true);
        } catch (e) {
            console.error("Failed to create group chat:", e);
        }
    };

    const filteredChats = chats.filter(c => c.name?.toLowerCase().includes(searchQuery.toLowerCase()));
    const suggestedUsers = searchQuery ? allUsers.filter(u => u.name.toLowerCase().includes(searchQuery.toLowerCase())) : [];
    const activeChat = chats.find(c => c.id === activeChatId);

    return (
        <div className="flex h-[calc(100vh-64px)] bg-slate-50 overflow-hidden font-sans">
            <CreateGroupModal
                isOpen={isGroupModalOpen}
                onClose={() => setIsGroupModalOpen(false)}
                users={allUsers}
                onCreate={handleCreateGroup}
            />

            {/* Chats Sidebar */}
            <div className={`w-full md:w-80 lg:w-96 bg-white border-r border-slate-200 flex flex-col ${isMobileChatOpen ? 'hidden md:flex' : 'flex'}`}>
                <div className="p-4 border-b border-slate-100">
                    <div className="flex justify-between items-center mb-4">
                        <h1 className="text-xl font-bold text-slate-800 tracking-tight">Messages</h1>
                        <button
                            onClick={() => setIsGroupModalOpen(true)}
                            className="p-2 text-slate-500 hover:text-blue-600 hover:bg-blue-50 rounded-full transition"
                            title="Create Group"
                        >
                            <Users size={20} />
                        </button>
                    </div>
                    <div className="relative group">
                        <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-blue-500 transition" size={16} />
                        <input
                            className="w-full bg-slate-50 border-none rounded-xl py-2.5 pl-10 pr-4 text-sm focus:ring-2 focus:ring-blue-100 transition placeholder-slate-400 outline-none"
                            placeholder="Search chats or people..."
                            value={searchQuery}
                            onChange={e => setSearchQuery(e.target.value)}
                        />
                    </div>
                </div>

                <div className="flex-1 overflow-y-auto">
                    <div className="p-2 space-y-1">
                        {filteredChats.map(chat => (
                            <div
                                key={chat.id}
                                onClick={() => { setActiveChatId(chat.id); setIsMobileChatOpen(true); }}
                                className={`p-3 rounded-xl flex items-center gap-3 cursor-pointer transition-all border ${activeChatId === chat.id ? 'bg-blue-50 border-blue-100 shadow-sm' : 'hover:bg-slate-50 border-transparent'
                                    }`}
                            >
                                <div className={`w-12 h-12 rounded-full flex items-center justify-center text-white font-bold shadow-sm ${chat.is_group ? 'bg-indigo-500' : 'bg-gradient-to-br from-blue-400 to-blue-600'}`}>
                                    {chat.is_group ? <Users size={16} /> : (chat.name?.substring(0, 2).toUpperCase() || "?")}
                                </div>
                                <div className="flex-1 min-w-0">
                                    <div className="flex justify-between items-baseline mb-0.5">
                                        <h3 className={`text-sm font-semibold truncate ${activeChatId === chat.id ? 'text-blue-900' : 'text-slate-800'}`}>{chat.name}</h3>
                                        <span className="text-[10px] text-slate-400">
                                            {chat.last_message_time ? new Date(chat.last_message_time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : ''}
                                        </span>
                                    </div>
                                    <div className="flex justify-between items-center">
                                        <p className="text-xs text-slate-500 truncate max-w-[85%]">{chat.last_message || <span className="italic opacity-50">No messages yet</span>}</p>
                                        {chat.unread_count && chat.unread_count > 0 && (
                                            <span className="bg-blue-600 text-white text-[10px] font-bold px-1.5 py-0.5 rounded-full min-w-[1.25rem] text-center shadow-sm">
                                                {chat.unread_count}
                                            </span>
                                        )}
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>

                    {suggestedUsers.length > 0 && (
                        <div className="p-2 border-t border-slate-100 mt-2">
                            <p className="px-3 py-2 text-xs font-bold text-slate-400 uppercase tracking-wider">Start New Chat</p>
                            {suggestedUsers.map(user => (
                                <div
                                    key={user.id}
                                    onClick={() => handleCreateP2PChat(user.id)}
                                    className="p-3 rounded-xl flex items-center gap-3 cursor-pointer hover:bg-slate-50 transition"
                                >
                                    <div className="w-10 h-10 rounded-full bg-slate-200 flex items-center justify-center text-slate-600 font-bold text-xs">
                                        {user.name.substring(0, 2).toUpperCase()}
                                    </div>
                                    <div>
                                        <p className="text-sm font-semibold text-slate-700">{user.name}</p>
                                        <p className="text-xs text-slate-400">Click to message</p>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>

            {/* Active Conversation Area */}
            {activeChat ? (
                <div className={`flex-1 flex flex-col bg-[#f8fafc] ${isMobileChatOpen ? 'flex' : 'hidden md:flex'}`}>
                    <div className="h-16 bg-white border-b border-slate-200 flex items-center justify-between px-6 shadow-sm z-10">
                        <div className="flex items-center gap-3">
                            <button onClick={() => setIsMobileChatOpen(false)} className="md:hidden p-2 -ml-2 text-slate-500 hover:bg-gray-100 rounded-full">
                                <ArrowLeft size={20} />
                            </button>
                            <div className={`w-10 h-10 rounded-full flex items-center justify-center text-white font-bold text-sm shadow-sm ${activeChat.is_group ? 'bg-indigo-500' : 'bg-gradient-to-br from-blue-400 to-blue-600'}`}>
                                {activeChat.is_group ? <Users size={18} /> : activeChat.name?.substring(0, 2).toUpperCase()}
                            </div>
                            <div>
                                <h2 className="font-bold text-slate-800 text-[15px]">{activeChat.name}</h2>
                                {activeChat.is_group && <span className="text-xs text-slate-500 font-medium bg-slate-100 px-2 py-0.5 rounded-full">Group Chat</span>}
                            </div>
                        </div>
                    </div>

                    <div ref={messagesContainerRef} className="flex-1 overflow-y-auto p-6 space-y-3 bg-[radial-gradient(#e2e8f0_1px,transparent_1px)] [background-size:20px_20px]">
                        {messages.map((msg, idx) => {
                            const isMe = msg.sender_id === currentUser?.id;
                            const showSender = !isMe && activeChat.is_group && (idx === 0 || messages[idx - 1].sender_id !== msg.sender_id);

                            return (
                                <div key={msg.id} className={`flex w-full ${isMe ? 'justify-end' : 'justify-start'}`}>
                                    <div className={`flex flex-col max-w-[75%] md:max-w-[60%] ${isMe ? 'items-end' : 'items-start'}`}>
                                        {showSender && <span className="text-[10px] text-slate-500 mb-1 ml-2 font-medium">{msg.sender?.name || 'Unknown'}</span>}
                                        <div className={`px-4 py-2.5 rounded-2xl shadow-sm text-[14px] leading-relaxed break-words ${isMe ? 'bg-blue-600 text-white rounded-br-none' : 'bg-white text-slate-800 border border-slate-100 rounded-bl-none'
                                            }`}>
                                            <p>{msg.content}</p>
                                        </div>
                                        <span className="text-[10px] text-slate-400 mt-1 px-1 font-medium opacity-80">{new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
                                    </div>
                                </div>
                            );
                        })}
                        <div ref={messagesEndRef} />
                    </div>

                    <div className="p-4 bg-white border-t border-slate-200">
                        <div className="flex items-center gap-3 bg-slate-50 border border-slate-200 rounded-2xl p-2 pr-2 shadow-sm focus-within:ring-2 focus-within:ring-blue-100 transition-all">
                            <button className="p-2 text-slate-400 hover:text-blue-600 transition"><Paperclip size={20} /></button>
                            <input
                                className="flex-1 bg-transparent border-none focus:ring-0 text-slate-700 placeholder-slate-400 text-sm h-10"
                                placeholder="Type a message..."
                                value={inputText}
                                onChange={e => setInputText(e.target.value)}
                                onKeyDown={e => e.key === 'Enter' && handleSendMessage()}
                            />
                            <button
                                onClick={handleSendMessage}
                                disabled={!inputText.trim()}
                                className="p-2.5 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition shadow-sm disabled:opacity-50"
                            >
                                <Send size={18} fill="currentColor" />
                            </button>
                        </div>
                    </div>
                </div>
            ) : (
                <div className="hidden md:flex flex-1 flex-col items-center justify-center text-slate-400 bg-slate-50/50">
                    <div className="w-24 h-24 bg-white rounded-full flex items-center justify-center mb-6 shadow-sm border border-slate-100">
                        <Smile size={40} className="text-blue-200" />
                    </div>
                    <h3 className="text-lg font-bold text-slate-700 mb-2">My Messages</h3>
                    <p className="text-sm max-w-xs text-center text-slate-500">Pick a person or group from the left to start chatting.</p>
                </div>
            )}
        </div>
    );
};

export default MessagesPage;
