import React from "react";
import {
  LayoutDashboard,
  MessageSquare,
  CalendarDays,
  FileUp,
  Workflow,
  ClipboardList,
  LogOut,
  UsersRound,
} from "lucide-react";
import { useNavigate, useLocation } from "react-router-dom";

const Sidebar: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const navItems = [
    { label: "Dashboard", icon: LayoutDashboard, path: "/dashboard" },
    { label: "Chat", icon: MessageSquare, path: "/chat" },
    { label: "Tasks", icon: ClipboardList, path: "/tasks" },
    { label: "Meetings", icon: UsersRound, path: "/meetings" },
    { label: "Calendar", icon: CalendarDays, path: "/calendar" },
    { label: "Files", icon: FileUp, path: "/files" },
    { label: "Workflows", icon: Workflow, path: "/workflows" },
  ];

  return (
    <aside className="h-screen w-64 flex flex-col border-r bg-white border-gray-200 text-gray-900">
      {/* ðŸ”¹ Logo Section */}
      <div
        className="flex flex-col items-center justify-center py-8 border-b cursor-pointer group"
        onClick={() => navigate("/dashboard")}
      >
        <img
          src="/logo1.png"
          alt="Collabryta Logo"
          className="w-28 h-28 object-contain drop-shadow-lg transition-transform duration-300 group-hover:scale-105"
        />
        <h1 className="mt-3 text-lg font-semibold tracking-wide text-gray-800">
          Collabryta
        </h1>
        <p className="text-xs font-light text-gray-500">
          Empower â€¢ Collaborate â€¢ Innovate
        </p>
      </div>

      {/* ðŸ§­ Navigation */}
      <nav className="flex-1 overflow-y-auto px-5 py-6 space-y-1">
        {navItems.map(({ label, icon: Icon, path }) => {
          const active = location.pathname === path;
          return (
            <button
              key={path}
              onClick={() => navigate(path)}
              className={`w-full flex items-center gap-3 px-3 py-2.5 rounded-xl cursor-pointer font-medium text-sm transition-all duration-300 ${active
                  ? "bg-blue-100 text-blue-700 shadow-sm"
                  : "hover:bg-gray-100 text-gray-700"
                }`}
            >
              <Icon size={18} />
              <span>{label}</span>
            </button>
          );
        })}
      </nav>

      {/* ðŸšª Logout */}
      <div className="px-5 py-6 border-t border-gray-200">
        <button
          onClick={() => navigate("/login")}
          className="w-full flex items-center justify-center gap-2 py-2.5 rounded-lg font-medium text-sm bg-gray-100 hover:bg-gray-200 text-red-600 transition-all"
        >
          <LogOut size={18} />
          <span>Logout</span>
        </button>
      </div>
    </aside>
  );
};

export default Sidebar;
