import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Calendar as CalendarIcon,
  MessageSquare,
  CheckCircle,
  Clock,
  ArrowRight,
  Activity,
  Zap,
  Star,
  Shield,
  Layers,
  ChevronRight,
  Plus,
  ArrowUpRight,
  ClipboardList,
  Video,
  Users,
  Sparkles
} from "lucide-react";
import { motion } from "framer-motion";
import { taskService, Task } from "../../services/taskService";
import { meetingService } from "../../services/meetingService";
import { messageService, Chat } from "../../services/messageService";
import { userService } from "../../services/userService";
import { authService } from "../../services/authService";


interface DashboardEvent {
  id: string;
  title: string;
  time: string;
  type: "meeting" | "task";
  status?: string;
  details?: string;
  timestamp: Date;
}

const DashboardPage: React.FC = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState<any>(null);
  const [stats, setStats] = useState({
    tasksPending: 0,
    meetingsToday: 0,
    unreadMessages: 0,
    teamMembers: 0,
    tasksCompleted: 0
  });
  const [todaysEvents, setTodaysEvents] = useState<DashboardEvent[]>([]);
  const [recentTasks, setRecentTasks] = useState<Task[]>([]);

  const fetchData = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const [user, tasks, meetings, chats, users] = await Promise.all([
        authService.getCurrentUser(),
        taskService.getTasks(),
        meetingService.getAllMeetings(),
        messageService.getChats(),
        userService.getAllUsers()
      ]);

      setCurrentUser(user);

      const pendingTasks = tasks.filter(t => t.status !== "Completed");
      const completedTasks = tasks.filter(t => t.status === "Completed");
      const unreadCount = chats.reduce((acc: number, curr: Chat) => acc + (curr.unread_count || 0), 0);
      const todayStr = new Date().toLocaleDateString("en-CA");
      const todayMeetings = meetings.filter(m => new Date(m.start_time).toLocaleDateString("en-CA") === todayStr);

      setStats({
        tasksPending: pendingTasks.length,
        meetingsToday: todayMeetings.length,
        unreadMessages: unreadCount,
        teamMembers: users.length,
        tasksCompleted: completedTasks.length
      });

      const taskEvents: DashboardEvent[] = tasks
        .filter(t => t.due_date && new Date(t.due_date).toLocaleDateString("en-CA") === todayStr && t.status !== "Completed")
        .map(t => ({
          id: `task-${t.id}`,
          title: t.title,
          time: "Due Today",
          type: "task",
          status: t.status,
          details: t.priority,
          timestamp: new Date(t.created_at || new Date())
        }));

      const meetingEvents: DashboardEvent[] = todayMeetings.map(m => {
        const startDate = new Date(m.start_time);
        return {
          id: `meeting-${m.id}`,
          title: m.title,
          time: startDate.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', hour12: true }),
          type: "meeting",
          details: m.location || "Virtual HQ",
          timestamp: startDate
        };
      });

      const allEvents = [...taskEvents, ...meetingEvents].sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());
      setTodaysEvents(allEvents);
      setRecentTasks(tasks.slice(0, 5));

    } catch (error) {
      console.error("Failed to load dashboard data", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    const pollInterval = setInterval(() => {
      fetchData(false);
    }, 30000);

    return () => {
      clearInterval(pollInterval);
    };
  }, []);

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return "Good morning";
    if (hour < 18) return "Good afternoon";
    return "Good evening";
  };

  if (loading) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center bg-[#fcfdfe]">
        <div className="w-12 h-12 border-4 border-slate-100 border-t-indigo-600 rounded-full animate-spin mb-6" />
        <p className="text-sm font-semibold text-slate-400">Loading your workspace...</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-[#fcfdfe] text-slate-900 px-8 py-10 lg:px-12 animate-fade-in">
      {/* Header Area */}
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-6 mb-12">
        <div>
          <p className="text-sm font-bold text-indigo-600 mb-1">{getGreeting()},</p>
          <h1 className="text-4xl font-bold tracking-tight text-slate-900">
            {currentUser?.name || 'User'}
          </h1>
          <p className="text-slate-500 font-medium mt-1">Here's what's happening in your workspace today.</p>
        </div>

        <div className="flex items-center gap-4">
          <button
            onClick={() => navigate('/tasks/add')}
            className="btn-primary"
          >
            <Plus size={18} />
            New Task
          </button>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-12">
        <StatCard
          label="Pending Tasks"
          value={stats.tasksPending}
          icon={ClipboardList}
          color="indigo"
          onClick={() => navigate('/tasks')}
        />
        <StatCard
          label="Meetings Today"
          value={stats.meetingsToday}
          icon={Video}
          color="indigo"
          onClick={() => navigate('/meetings')}
        />
        <StatCard
          label="Unread Messages"
          value={stats.unreadMessages}
          icon={MessageSquare}
          color="indigo"
          onClick={() => navigate('/messages')}
        />
        <StatCard
          label="Team Members"
          value={stats.teamMembers}
          icon={Users}
          color="indigo"
          onClick={() => navigate('/team')}
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-10">
        {/* Schedule Section */}
        <div className="lg:col-span-2 space-y-6">
          <div className="flex items-center justify-between px-2">
            <div>
              <h3 className="text-xl font-bold text-slate-900">Today's Schedule</h3>
              <p className="text-sm text-slate-500 font-medium">Your agenda for the next 24 hours</p>
            </div>
            <button onClick={() => navigate('/calendar')} className="text-xs font-bold text-indigo-600 hover:text-indigo-700 underline underline-offset-4">View Calendar</button>
          </div>

          <div className="bg-white rounded-[32px] border border-slate-100 shadow-sm overflow-hidden">
            {todaysEvents.length > 0 ? (
              <div className="divide-y divide-slate-50">
                {todaysEvents.map((event, idx) => (
                  <div key={idx} className="p-6 flex items-center gap-6 hover:bg-slate-50 transition-all group cursor-pointer" onClick={() => navigate(event.type === 'meeting' ? '/meetings' : '/tasks')}>
                    <div className={`w-14 h-14 rounded-2xl flex flex-col items-center justify-center shrink-0 ${event.type === 'meeting' ? 'bg-indigo-50 text-indigo-600' : 'bg-slate-50 text-slate-600'}`}>
                      <p className="text-[10px] font-bold uppercase tracking-wider leading-none mb-1">{event.type.substring(0, 3)}</p>
                      <Clock size={16} />
                    </div>
                    <div className="flex-1">
                      <h4 className="text-base font-bold text-slate-900 mb-0.5">{event.title}</h4>
                      <p className="text-xs font-medium text-slate-400 capitalize">{event.time} â€¢ {event.details}</p>
                    </div>
                    <div className="p-2.5 rounded-xl bg-slate-50 text-slate-400 group-hover:bg-indigo-600 group-hover:text-white transition-all">
                      <ArrowRight size={16} />
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="p-20 text-center">
                <div className="w-16 h-16 bg-slate-50 rounded-2xl flex items-center justify-center mx-auto mb-4">
                  <CalendarIcon className="text-slate-300" size={32} />
                </div>
                <h4 className="text-lg font-bold text-slate-900">No events today</h4>
                <p className="text-sm text-slate-400 font-medium mt-1">Enjoy your free time!</p>
              </div>
            )}
          </div>
        </div>

        {/* Recent Tasks */}
        <div className="space-y-6">
          <div className="flex items-center justify-between px-2">
            <div>
              <h3 className="text-xl font-bold text-slate-900">Recent Tasks</h3>
              <p className="text-sm text-slate-500 font-medium">Quick access to latest work</p>
            </div>
            <button onClick={() => navigate('/tasks')} className="text-xs font-bold text-indigo-600 hover:text-indigo-700 underline underline-offset-4">View All</button>
          </div>

          <div className="space-y-4">
            <div className="bg-white rounded-[32px] border border-slate-100 shadow-sm overflow-hidden p-2">
              {recentTasks.length > 0 ? (
                <div className="divide-y divide-slate-50">
                  {recentTasks.map(task => (
                    <div key={task.id} className="p-5 flex items-center justify-between group cursor-pointer hover:bg-slate-50 rounded-[24px] transition-all" onClick={() => navigate('/tasks')}>
                      <div className="flex items-center gap-4">
                        <div className={`w-2 h-2 rounded-full ${task.status === 'Completed' ? 'bg-green-500' : 'bg-indigo-500'}`} />
                        <div>
                          <p className={`text-sm font-bold ${task.status === 'Completed' ? 'text-slate-300 line-through' : 'text-slate-900'}`}>
                            {task.title}
                          </p>
                          <p className="text-[10px] font-semibold text-slate-400 uppercase tracking-wider mt-0.5">{task.priority} Priority</p>
                        </div>
                      </div>
                      <ChevronRight size={16} className="text-slate-300 group-hover:text-indigo-600 transition-all" />
                    </div>
                  ))}
                </div>
              ) : (
                <div className="py-12 text-center">
                  <p className="text-sm font-medium text-slate-400">No recent tasks</p>
                </div>
              )}
            </div>

            {/* Pro Card */}
            <div className="bg-indigo-600 rounded-[32px] p-8 text-white relative overflow-hidden shadow-xl shadow-indigo-100 mt-6">
              <div className="absolute -top-10 -right-10 w-40 h-40 bg-white/10 rounded-full blur-3xl" />
              <div className="absolute -bottom-10 -left-10 w-40 h-40 bg-indigo-400/20 rounded-full blur-3xl" />

              <div className="relative z-10">
                <div className="w-10 h-10 bg-white/20 rounded-xl flex items-center justify-center mb-6">
                  <Sparkles size={20} className="text-white" />
                </div>
                <h4 className="text-lg font-bold mb-2">Upgrade to Pro</h4>
                <p className="text-sm text-indigo-100 font-medium leading-relaxed mb-6">
                  Unlock advanced team collaboration features and unlimited cloud storage.
                </p>
                <button className="w-full py-3 bg-white text-indigo-600 rounded-xl font-bold text-sm hover:bg-slate-50 transition-colors shadow-lg">
                  Learn More
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Sub-components
const StatCard = ({ label, value, icon: Icon, color, onClick }: any) => (
  <button
    onClick={onClick}
    className="bg-white p-7 rounded-[32px] border border-slate-100 shadow-sm hover:shadow-md hover:border-indigo-100 transition-all group text-left relative overflow-hidden"
  >
    <div className="absolute top-0 right-0 p-4 opacity-[0.03] group-hover:opacity-[0.07] transition-opacity">
      <Icon size={80} />
    </div>

    <div className="relative z-10">
      <div className={`w-12 h-12 rounded-2xl bg-${color}-50 flex items-center justify-center mb-6 text-${color}-600 group-hover:scale-110 transition-transform`}>
        <Icon size={24} />
      </div>
      <div>
        <h3 className="text-3xl font-bold text-slate-900 mb-1">{value}</h3>
        <p className="text-xs font-bold text-slate-400 uppercase tracking-widest">{label}</p>
      </div>
    </div>
  </button>
);

export default DashboardPage;
