import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Calendar as CalendarIcon,
  MessageSquare,
  CheckCircle,
  Clock,
  ArrowRight,
  Activity,
  Zap,
  Star,
  Shield,
  Layers,
  ChevronRight,
  Plus
} from "lucide-react";
import { motion } from "framer-motion";
import { taskService, Task } from "../../services/taskService";
import { meetingService } from "../../services/meetingService";
import { messageService, Chat } from "../../services/messageService";
import { userService } from "../../services/userService";
import { authService } from "../../services/authService";
import { webSocketService, WebSocketMessage } from "../../services/websocketService";

interface DashboardEvent {
  id: string;
  title: string;
  time: string;
  type: "meeting" | "task";
  status?: string;
  details?: string;
  timestamp: Date;
}

const DashboardPage: React.FC = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState<any>(null);
  const [stats, setStats] = useState({
    tasksPending: 0,
    meetingsToday: 0,
    unreadMessages: 0,
    teamMembers: 0,
    tasksCompleted: 0
  });
  const [todaysEvents, setTodaysEvents] = useState<DashboardEvent[]>([]);
  const [recentTasks, setRecentTasks] = useState<Task[]>([]);

  const fetchData = async (showLoading = true) => {
    if (showLoading) setLoading(true);
    try {
      const [user, tasks, meetings, chats, users] = await Promise.all([
        authService.getCurrentUser(),
        taskService.getTasks(),
        meetingService.getAllMeetings(),
        messageService.getChats(),
        userService.getAllUsers()
      ]);

      setCurrentUser(user);

      const pendingTasks = tasks.filter(t => t.status !== "Completed");
      const completedTasks = tasks.filter(t => t.status === "Completed");
      const unreadCount = chats.reduce((acc: number, curr: Chat) => acc + (curr.unread_count || 0), 0);
      const todayStr = new Date().toLocaleDateString("en-CA");
      const todayMeetings = meetings.filter(m => new Date(m.start_time).toLocaleDateString("en-CA") === todayStr);

      setStats({
        tasksPending: pendingTasks.length,
        meetingsToday: todayMeetings.length,
        unreadMessages: unreadCount,
        teamMembers: users.length,
        tasksCompleted: completedTasks.length
      });

      const taskEvents: DashboardEvent[] = tasks
        .filter(t => t.due_date && new Date(t.due_date).toLocaleDateString("en-CA") === todayStr && t.status !== "Completed")
        .map(t => ({
          id: `task-${t.id}`,
          title: t.title,
          time: "Due Today",
          type: "task",
          status: t.status,
          details: t.priority,
          timestamp: new Date(t.created_at || new Date())
        }));

      const meetingEvents: DashboardEvent[] = todayMeetings.map(m => {
        const startDate = new Date(m.start_time);
        return {
          id: `meeting-${m.id}`,
          title: m.title,
          time: startDate.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', hour12: true }),
          type: "meeting",
          details: m.location || "Virtual HQ",
          timestamp: startDate
        };
      });

      const allEvents = [...taskEvents, ...meetingEvents].sort((a, b) => a.timestamp.getTime() - b.timestamp.getTime());
      setTodaysEvents(allEvents);
      setRecentTasks(tasks.slice(0, 5));

    } catch (error) {
      console.error("Failed to load dashboard data", error);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    const unsubscribe = webSocketService.subscribe((message: WebSocketMessage) => {
      if (['task', 'meeting', 'message', 'notification'].includes(message.type)) {
        fetchData(false);
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);

  const getGreeting = () => {
    const hour = new Date().getHours();
    if (hour < 12) return "Good Morning";
    if (hour < 18) return "Good Afternoon";
    return "Good Evening";
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1, transition: { staggerChildren: 0.05 } }
  };

  const itemVariants = {
    hidden: { y: 15, opacity: 0 },
    visible: { y: 0, opacity: 1 }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center bg-[#fcfdfe]">
        <div className="w-12 h-12 border-4 border-indigo-100 border-t-indigo-600 rounded-full animate-spin mb-4" />
        <p className="text-sm font-medium text-slate-400">Loading your workspace...</p>
      </div>
    );
  }

  return (
    <motion.div
      initial="hidden"
      animate="visible"
      variants={containerVariants}
      className="min-h-screen bg-[#fcfdfe] text-slate-900 p-6 lg:p-10 space-y-10"
    >
      {/* Header Area */}
      <motion.div variants={itemVariants} className="flex flex-col md:flex-row md:items-center justify-between gap-6">
        <div>
          <h1 className="text-3xl font-bold tracking-tight text-slate-900 mb-1">
            {getGreeting()}, <span className="text-indigo-600">{currentUser?.name?.split(' ')[0] || 'User'}</span>
          </h1>
          <p className="text-slate-500 font-medium">Here's an overview of your team's progress today.</p>
        </div>

        <div className="flex items-center gap-3">
          <button
            onClick={() => navigate('/tasks/add')}
            className="flex items-center gap-2 px-5 py-2.5 bg-indigo-600 hover:bg-indigo-700 text-white rounded-xl font-semibold transition-all shadow-lg shadow-indigo-100 active:scale-95"
          >
            <Plus size={18} />
            <span>New Task</span>
          </button>
          <div className="h-11 w-[1px] bg-slate-200 mx-2 hidden md:block" />
          <div className="flex items-center gap-3 px-4 py-2.5 bg-white rounded-xl border border-slate-100 shadow-sm">
            <div className="w-2 h-2 rounded-full bg-emerald-500 animate-pulse" />
            <span className="text-sm font-semibold text-slate-700">System Online</span>
          </div>
        </div>
      </motion.div>

      {/* Highlights Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          icon={Shield}
          label="Pending Tasks"
          value={stats.tasksPending}
          delta="+2 this hour"
          color="text-indigo-600"
          bg="bg-indigo-50"
          onClick={() => navigate('/tasks')}
        />
        <StatCard
          icon={Activity}
          label="Today's Meetings"
          value={stats.meetingsToday}
          delta="Next: 2:00 PM"
          color="text-emerald-600"
          bg="bg-emerald-50"
          onClick={() => navigate('/meetings')}
        />
        <StatCard
          icon={MessageSquare}
          label="Unread Messages"
          value={stats.unreadMessages}
          delta="3 priority"
          color="text-blue-600"
          bg="bg-blue-50"
          onClick={() => navigate('/messages')}
        />
        <StatCard
          icon={Star}
          label="Tasks Completed"
          value={stats.tasksCompleted}
          delta="Weekly goal: 80%"
          color="text-amber-600"
          bg="bg-amber-50"
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Schedule Section */}
        <motion.div variants={itemVariants} className="lg:col-span-2 space-y-6">
          <div className="flex items-center justify-between px-2">
            <h3 className="text-lg font-bold text-slate-900">Your Schedule</h3>
            <button onClick={() => navigate('/calendar')} className="text-sm font-semibold text-indigo-600 hover:underline">View Calendar</button>
          </div>

          <div className="bg-white rounded-2xl border border-slate-100 shadow-sm overflow-hidden">
            {todaysEvents.length > 0 ? (
              <div className="divide-y divide-slate-50">
                {todaysEvents.map((event, idx) => (
                  <div key={idx} className="p-5 flex items-center gap-5 hover:bg-slate-50 transition-colors group">
                    <div className="w-16 text-center">
                      <p className="text-[11px] font-bold text-slate-400 uppercase tracking-wider">{event.time === "Due Today" ? "ALL DAY" : event.time}</p>
                    </div>
                    <div className={`w-1 h-10 rounded-full ${event.type === 'meeting' ? 'bg-indigo-500' : 'bg-emerald-500'}`} />
                    <div className="flex-1">
                      <h4 className="font-bold text-slate-900 mb-0.5">{event.title}</h4>
                      <p className="text-xs font-medium text-slate-400">{event.details}</p>
                    </div>
                    <button className="opacity-0 group-hover:opacity-100 p-2 text-slate-400 hover:text-indigo-600 hover:bg-indigo-50 rounded-lg transition-all">
                      <ChevronRight size={18} />
                    </button>
                  </div>
                ))}
              </div>
            ) : (
              <div className="p-16 text-center">
                <div className="w-16 h-16 bg-slate-50 rounded-full flex items-center justify-center mx-auto mb-4">
                  <CalendarIcon size={24} className="text-slate-300" />
                </div>
                <h4 className="font-bold text-slate-900 mb-1">Clear Schedule</h4>
                <p className="text-sm text-slate-400">You have no tasks or meetings scheduled for today.</p>
              </div>
            )}
          </div>
        </motion.div>

        {/* Recent Activity/Tasks */}
        <motion.div variants={itemVariants} className="space-y-6">
          <div className="flex items-center justify-between px-2">
            <h3 className="text-lg font-bold text-slate-900">Task Overview</h3>
            <button onClick={() => navigate('/tasks')} className="text-sm font-semibold text-indigo-600 hover:underline">View All</button>
          </div>

          <div className="bg-white rounded-2xl border border-slate-100 shadow-sm p-6 space-y-5">
            {recentTasks.length > 0 ? (
              recentTasks.map(task => (
                <div key={task.id} className="flex items-center justify-between group cursor-pointer" onClick={() => navigate('/tasks')}>
                  <div className="flex items-center gap-4">
                    <div className={`w-2 h-2 rounded-full ${task.status === 'Completed' ? 'bg-emerald-500' : 'bg-slate-200'}`} />
                    <div>
                      <p className={`text-sm font-semibold text-slate-700 ${task.status === 'Completed' ? 'text-slate-400 line-through' : ''}`}>
                        {task.title}
                      </p>
                      <p className="text-[10px] font-bold text-slate-400 uppercase tracking-tighter mt-0.5">{task.priority} Priority</p>
                    </div>
                  </div>
                  <ChevronRight size={14} className="text-slate-300 group-hover:text-indigo-600 transition-colors" />
                </div>
              ))
            ) : (
              <p className="text-sm text-center text-slate-400 py-4">No recent tasks</p>
            )}

            <div className="pt-4 border-t border-slate-50">
              <div className="bg-indigo-600 rounded-xl p-5 text-white shadow-xl shadow-indigo-100">
                <p className="text-[11px] font-bold uppercase tracking-widest text-indigo-100 mb-2">Team Insight</p>
                <p className="text-sm font-medium leading-relaxed">
                  Your team is currently focused on <strong>Sprint Alpha</strong>. 85% of core objectives are on track.
                </p>
              </div>
            </div>
          </div>
        </motion.div>
      </div>
    </motion.div>
  );
};

// Sub-components
const StatCard = ({ icon: Icon, label, value, delta, color, bg, onClick }: any) => (
  <motion.div
    whileHover={{ y: -4 }}
    onClick={onClick}
    className="bg-white p-6 rounded-2xl border border-slate-100 shadow-sm hover:shadow-md transition-all cursor-pointer"
  >
    <div className="flex items-center justify-between mb-4">
      <div className={`p-2.5 ${bg} ${color} rounded-xl`}>
        <Icon size={20} />
      </div>
      <span className="text-[11px] font-bold text-slate-400 uppercase tracking-wider">{delta}</span>
    </div>
    <h3 className="text-2xl font-bold text-slate-900 mb-0.5">{value}</h3>
    <p className="text-sm font-medium text-slate-500">{label}</p>
  </motion.div>
);

export default DashboardPage;
